scwin.msk_sInqrKeyCd_onkeydown_Async = function(e) {
	if (e.keyCode == 13) {
		util.eventAsyncCall(this, scwin.msk_sInqrKeyCd_onkeydown, arguments);
	} else {
		scwin.msk_sInqrKeyCd_onkeydown(e);
	}
}

scwin.msk_sInqrKeyCd_onkeydown = function(e) {
	if (e.ctrlKey == true && e.keyCode == 86) {
	}
	scwin.lfn_msk_sInqrKeyCd_OnChar(e);
};

//---------------------------------------------------------------------------
// function 명 : lfn_msk_sInqrKeyCd_OnChar
// 내용        : 엔터입력시 조회
//---------------------------------------------------------------------------
scwin.lfn_msk_sInqrKeyCd_OnChar = function(e) {
    //입력후엔터 입력시
    if (e.keyCode == 13) {
        scwin.lfn_Search();
    } else {
        //기존 데이터 삭제
        edt_sInqrKeyValue.setValue("");
        wconv.clear(Ds_search);
        wconv.clear(Ds_LTIFA01CNT);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchCstName
// 내용        : 고객명 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchCstName = function(sRegNoType, sRegNo, tagetEdtObj, sLabel) { //완료
    if (sRegNoType == "1") {
        if (wconv.length(sRegNo) != 13) {
            tagetEdtObj.setValue("");
            return;
        }
    } else if (sRegNoType == "2") {
        if (wconv.length(sRegNo) != 10) {
            tagetEdtObj.setValue("");
            return;
        }
    }
    if (util.getGdsValue("gUserID") == "") {
        wprom.alert("고객명 찾기 실패(사유 : 로그인 사용자 ID 오류)");
        return;
    }

    var transObj = new callService("com.inswave.longterm.cst.cin.task.CBA125Task", "selectList", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("selectList");
	transObj.setParam("sUserID", util.getGdsValue("gUserID"));
	transObj.setParam("sPsnCorpFlag", sRegNoType);
	transObj.setParam("sCdNameFlag", "Cd");
	transObj.setParam("sSerchKey", sRegNo);
	transObj.setXPath("//*/vector/data/LTIFA01");
	transObj.addXPath("//*/hashtable/data/LTIFA01CNT");
	transObj.addXPath("//*/LTICA01");
	transObj.addXPath("//*/WARNING");
	transObj.MsgHide();
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");
	//util.setRowPosition(warning, 0);

	if (warning.getRowCount() != 0) {
        wprom.alert(warning.getCellData(0, "msg"));
        return;
    }

    util.jSonDsCreate(_tranSync.getDsJson("LTICA01"), "DsWs_searchName");
	util.setRowPosition(DsWs_searchName, 0);

	if (DsWs_searchName.getRowCount() == 0) {
        wprom.alert(sLabel + "명을 찾을수없습니다.");
        return;
    } else {
        tagetEdtObj.setValue(DsWs_searchName.getCellData(0, "sCstName"));
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchEmpName
// 내용        : 사원명 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchEmpName = function(sEmpNo, targetEdtObj) {
    if (wconv.length(sEmpNo) != 7) {
        targetEdtObj.setValue("");
        return;
    }

    wconv.clear(warning);
    wconv.clear(DsWs_searchName);

    var transObj = new callService("com.inswave.longterm.dpt.com.task.FAF000Task", "getEmpName", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("getEmpName");
	transObj.setParam("sEmpNo ", sEmpNo);
	transObj.setXPath("//*/vector/data/LTIFA01");
	transObj.addXPath("//*/hashtable/data/LTIFA01CNT");
	transObj.addXPath("//*/ADMAB01");
	transObj.addXPath("//*/WARNING");
	transObj.MsgHide();
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	util.interfaceKey(_tranSync.result.serviceDs);
    //DsWs_search  //*/vector/data/LTIFA01
    //DsWs_LTIFA01CNT //*/hashtable/data/LTIFA01CNT
    //DsWs_searchName ////*/ADMAB01

    util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");
    // 오류처리
    if (warning.getRowCount() != 0) {
        wprom.alert(warning.getCellData(0, "msg"));
        return;
    }

    util.jSonDsCreate(_tranSync.getDsJson("ADMAB01"), "DsWs_searchName");

    if (DsWs_searchName.getRowCount() == 0) {
        edt_sInqrKeyValue.setValue("");
        wprom.alert("상담원명을 찾을수 없습니다. 상담원코드를 확인해 주십시요");
        return false;
    } else {
        targetEdtObj.setValue(DsWs_searchName.getCellData(0, "sEmpName"));
    }
    return true;    
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchPrd
// 내용        : 상품리스트를 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchPrd = function() { //완료
	var prdCd = wconv.mid(msk_sInqrKeyCd.getValue(), 1, 4);

	var transObj = new callService("com.inswave.longterm.prd.att.task.BAA610Task", "selectProdList", "", "");
	transObj.setUrl("/contract/CBSC025MController.do");
	transObj.setId("selectProdList");
	transObj.setParam("sAttrFlagCd", "01");
	transObj.setParam("sAttrName", "GdHnglName");
	// gfn_SetParam(MethodObj, "sGdCd", "1101");
	transObj.setParam("sGdCd", prdCd);
	transObj.setParam("sGdHnglName", "");
	transObj.setParam("sGijunDate", "");
	transObj.setXPath("//*/vector/data/LTIBA01");
	transObj.addXPath("//*/WARNING");
	transObj.MsgHide();
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(warning);
    wconv.clear(Ds_ProdList);
    wconv.clear(DsWs_ProdList);

	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "warning");
	util.setCompBindRefresh("warning");
	// 오류처리
    if (warning.getRowCount() != 0) {
        wprom.alert(warning.getCellData(0, "msg"));
        return;
    }

	DsWs_ProdList.setJSON(_tranSync.getDsJson("LTIBA01"));
	util.setRowPosition(DsWs_ProdList, 0);

	if (DsWs_ProdList.getRowCount() == 0) {
        edt_sInqrKeyValue.setValue("");
        wprom.alert("상품명을 찾을수 없습니다. 계약번호를 확인해 주십시요");
        return false;
    } else {
    	Ds_ProdList.setJSON(DsWs_ProdList.getAllJSON());
		util.setRowPosition(Ds_ProdList, 0);
        edt_sInqrKeyValue.setValue(Ds_ProdList.getCellData(0, "sCdAttrValue"));
    }
    return true;    
};

//---------------------------------------------------------------------------
// function 명 : btn_CtnPremRecvDlng_OnClick
// 내용        : 버튼클릭 - 계속보험료 입금 처리 
//---------------------------------------------------------------------------
scwin.btn_CtnPremRecvDlng_OnClick = function(obj) {
	wprom.alert("추후개발예정");
	return;
    //var sArg  = 'sFormId="F010"';
    //var result = Open("dpt_rec::F020.xml", sArg, 820, 612); 
    wconv.copy(Ds_argsTemp, Ds_search);
    wconv.filter(Ds_argsTemp, "sSelYn=='1'");
    wconv.clear(gds.gDs_LinkArg);
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    /*수작업 변환 개발자 가이드(테스트 중)
    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
    wconv.copyF(gds.gDs_LinkArg, Ds_argsTemp);
    gfn_OpenLinkPage("F020");
    wconv.clear(Ds_argsTemp);
};

