scwin.init = function() { //완료

var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();

pro = pro.then(function(rv) {
	return util.winTopShowModalPromise();
});


pro = pro.then(function(rv) {


	//switch_div.setSelectedID("sw_div2");
	switch_div.hide();

	//Mask Edit 정규식 입력
	//"######-#ZZZZZZ" , "A-####-#######"
	var maskRegExp = [/^[A-Z\s]{1}[0-9\s]{11}$/];
	var maxLength  = [12];
	var chkIndex   = 0;
	Edt_sCrNo_Event = new wMaskEditObj(Edt_sCrNo, maskRegExp, maxLength, chkIndex);
	Edt_sCrNo.setDisplayFormat("#-####-#######");

    /** 링크 콤보 데이터를 가저온다. 
     반드시 링크 데이터셋 ID = Ds_LinkList, 링크콤보 ID = cmb_linkMenu !!!!!
     링크 버튼 ID = btn_linkMenu !!!!!*/
    //gfn_GetLinkList("D020");
    gfn_getCommonCodes("DsWs_CrStatCd DsWs_BntpCd", "CrStatCd S70", "", "");

	//화면에서 기본적으로 display none 인 comp 숨기기
	Btn_Renwl.hide();
	btn_AnntPymAmt.hide();
	Div1_btn_SwHsHldGds.hide();

    //trace(DsWs_BntpCd.savexml());
    //버튼 활성,비활성화
    gfn_btnEnable(false, false, false, false, false, false, false, true);

    scwin.lfn_btnCntlMain(false);
    scwin.lfn_btnCntlPibo(true);

    //개인정보 안전조치 -초기화
    Static31.hide();
    //Shape4.hide();

    //Shape2.Bottom = 542;
    //grd_Pibo.Bottom = 538;	
    //Div1.Shape2.Bottom = 183;
    //Div1.grd_Pibo.Bottom = 175;	
    //Div1.Top = 377;
    //Div1.Left = 4;
    //Div1.Height = 183;
    //대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
    if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35") {
        Btn_Buhal.setDisabled(true);
        Btn_Pymt.setDisabled(true);
        Btn_Loan.setDisabled(true);
        //대리점 관리자
        if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "21013")) {
            Btn_popD550.setDisabled(false);
        }
    }




    //2014.03.06 N1403-00033(장기보험 화면 개인정보 처리 요청)
    //2014.04.10 N1404-00035(장기보험 화면 개인정보 처리 요청)
    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "5000014") || //장기업무
        nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "32014")) {
        //CSR 상담원
        sAuthByflag = "1";
    }
    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "5000014") || //장기업무
        nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "32014") || //장기보험상담원
        nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "34024") || //대면총무권한
        nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "4000103")) {
        //장기보험부기본
        sBtnLinkAuthFlag = "1";
    }
    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "34024")) {
        //대면총무권한
        sBtnLinkAuthFlag34024 = "1";
    }

    //
    //var sToday = wconv.substr(gfn_GetSysTime(), 0, 8);
    gfn_getServerTime();
    var sToday = wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);

    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "22030")) {
        Static61.show();
        Msk_sStndDate.show();
        Msk_sStndDate.setValue(sToday);
        Btn_Renwl.show(); //2015.09.09 (N1509-00072)
    } else {
        Static61.hide();
        Msk_sStndDate.hide();
        Msk_sStndDate.setValue(sToday);
        Btn_Renwl.hide(); //2015.09.09 (N1509-00072)
    }

    //trace(gDs_LinkArg.SaveXML());
    if (gds.gDs_LinkArg.getRowCount() == 0) {
    	Edt_sCrNo.focus();
    	return;
    }
    if (gds.gDs_LinkArg.getRowCount() != 0) {
        Edt_sCrNo.setValue(gds.gDs_LinkArg.getCellData(0, "sCrNo"));
        //str = gDs_LinkArg.GetColumn(0, "sScrnId");
        if (Edt_sCrNo.getValue().length == 0) {
            //alert("계약번호를 입력해주세요");
            Edt_sCrNo.focus();
            return;
        }
        if (Edt_sCrNo.getValue().length != 12) {
            //alert("계약번호가 정확하지 않습니다");
            Edt_sCrNo.focus();
            return;
        }

        //alert(gDs_LinkArg.GetColumn(0, "sPlanNo"));
        gds.gDs_LinkArg.removeAll();
        scwin.lfn_Search(); // 계약기본사항을 조회한다.
    } else {
    	Edt_sCrNo.focus(); //Edt_sCrNo.Value ="L23011324567";
    }  
    //Edt_sCrNo.Value ="N23010000001";


});

pro = pro.then(function(rv) {
	util.winTopHideModal();
});

return pro;

};

