scwin.lfn_PsnAnnt = function() { //완료

    var pCnt = Ds_PiboList.getRowCount();
    var pCnt2 = Ds_Prprt.getRowCount();
    var sCheck = "";
    if (util.getGdsValue("gUserID") == DsWs_CntrDtl.getCellData(0, "sDoerUser") || util.getGdsValue("gUserID") == DsWs_CntrDtl.getCellData(0, "sDoer")) {
        sCheck = 1;
    } else {
        sCheck = 0;
    }

    //alert("Div1.Top : "+Div1.Top);
    //alert("Div1.Height: "+Div1.Height);
    //업무보고서분류코드 7:연금
    var clsfCd = Ds_CntrDtl.getCellData(0, "sBunsMetdDocuClsfCd");
    //alert("업무보고서분류코드 :  " + clsfCd);
    //alert("pCnt :  " + pCnt);
    //alert("Div1.Bottom = "+Div1.Bottom); 		
    if (clsfCd != 7) {
        //원본 사이즈 
        //Shape1.Bottom = 362;
        //Shape1.Height = 191;
        Shape1.Bottom = 382;
        Shape1.Height = 211;
        if (pCnt2 <= 0) {
            //정기중도인출신청여부
            Static56.show();
            Static56.setValue("추가납입보험료");
            Edit25.hide();
            Msk_AddPrem.show();
            Static70.show();
            Static62.show();
            Static62.setValue("정기중도인출여부");
            Edit21.show();
            var sPeriMwayWdraYn = "";
            if (Ds_CntrDtl.getCellData(0, "sPeriMwayWdraYn") == "Y")
                sPeriMwayWdraYn = "정기";
            else if (Ds_CntrDtl.getCellData(0, "sPeriMwayWdraYn") == "N")
                sPeriMwayWdraYn = "수시";
            Edit21.setValue(sPeriMwayWdraYn);

            //정기중도인출신청일자
            Static66.show();
            Static66.setValue("신청일자");
            Edit32.show();
            Edit32.setValue("");
            var sPeriMwayRqstDate = Ds_CntrDtl.getCellData(0, "sPeriMwayRqstDate");
            if (sPeriMwayRqstDate != "" && sPeriMwayRqstDate.length == 8) {
                Edit32.setValue(sPeriMwayRqstDate.substr(0, 4) + "-" + sPeriMwayRqstDate.substr(4, 2) + "-" + sPeriMwayRqstDate.substr(6, 2));
            }
        } else {
            Static56.show();
            Static56.setValue("추가납입보험료");
            Edit25.hide();
            Msk_AddPrem.show();
            Static70.show();
            Static62.show();
            Static62.setValue("정기중도인출여부");
            Edit21.show();
            var sPeriMwayWdraYn = "";
            if (Ds_CntrDtl.getCellData(0, "sPeriMwayWdraYn") == "Y")
                sPeriMwayWdraYn = "정기";
            else if (Ds_CntrDtl.getCellData(0, "sPeriMwayWdraYn") == "N")
                sPeriMwayWdraYn = "수시";
            Edit21.setValue(sPeriMwayWdraYn);

            //정기중도인출신청일자
            Static66.show();
            Static66.setValue("신청일자");
            Edit32.show();
            Edit32.setValue("");
            var sPeriMwayRqstDate = Ds_CntrDtl.getCellData(0, "sPeriMwayRqstDate");
            if (sPeriMwayRqstDate != "" && sPeriMwayRqstDate.length == 8) {
                Edit32.setValue(sPeriMwayRqstDate.substr(0, 4) + "-" + sPeriMwayRqstDate.substr(4, 2) + "-" + sPeriMwayRqstDate.substr(6, 2));
            }
        }

        //연금지급형태
        Static69.hide();
        Edit26.hide();
        //연금지급주기
        //Static69.Visible 	= false;
        //Edit26.Visible 		= false;
		/*
        if (Div1.Height != 150 && Div1.Height != 190) {
            //150: 물보험일경우, 190: 본인건이고 인보험에서 물보험건을 조회했을경우,as_is: 경고div 표시안됨
            Div1.Top = Div1.Top; //
            Div1_Shape2.Bottom = Div1_Shape2.Bottom;
            Div1_grd_Pibo.Bottom = Div1_grd_Pibo.Bottom;
            Div1.Height = Div1.Height; //Div1.Bottom = 2500;
        }
        */
        chkPsn = 1;
        if (clsfCd == "4")
            Div1_btn_SwHsHldGds.show();
        else
            Div1_btn_SwHsHldGds.hide();
    } else if (clsfCd == 7) {
        // 연금시 변경 사이즈 
        Shape1.Bottom = 382;
        Shape1.Height = 211;
        //연금개시나이
        Static56.setValue("연금개시나이");
        Static56.show();
        Edit25.show();
        //연금지급기간
        Static62.setValue("연금지급기간");
        Static62.show();
        Edit21.show();
        //연금지급형태
        Static66.show();
        Edit32.show();
        //연금지급주기
        Static69.show();
        Edit26.show();
        Msk_AddPrem.hide();
        Static70.hide();

        //alert("sCheck = "+sCheck);
        /*
        if (Div1.Height != 125 && sCheck == 0) {
            //125: 연금에 본인건이 아닐경우 피보험자 div 높이
            Div1.Top = Div1.Top + 21;
            Div1_Shape2.Bottom = Div1_Shape2.Bottom - 25;
            Div1_grd_Pibo.Bottom = Div1_grd_Pibo.Bottom - 25;
            Div1.Bottom = Div1.Bottom - 25;
        }
        */
        chkPsn = 0;
        if (pCnt > 0) {
            //피보험자건이 있을경우 물건div 창 겹치는걸 제거하기 위해 
            //Div1.show();
            //Div2.hide();
            switch_div.show();
            switch_div.setSelectedID("sw_div1");
        } else {
        	switch_div.show();
        	switch_div.setSelectedID("sw_div2");
            //Div1.hide();
            //Div2.show();
        }
    }
    scwin.lfn_modyBtn();
};

/**
	의미없는 함수, div로 인해 버튼 가려짐 현상을 없애기 위해
**/
scwin.lfn_modyBtn = function() { //완료
    btn_SuspcTrnstRprt.hide();
    Btn_InoutTrans.hide();
    Btn_Prem.hide();
    Btn_AutoTran.hide();
    Btn_Card.hide();
    Button_bu.hide();
    //goo Btn_scanYn.hide();
    //goo Btn_scanYn.hide();
    Btn_Renwl.hide();
    btn_SuspcTrnstRprt.show();
    Btn_InoutTrans.show();
    Btn_Prem.show();
    Btn_AutoTran.show();
    Btn_Card.show();
    Button_bu.show();
    //goo Btn_scanYn.show();
    if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "22040")) {
        Btn_Renwl.show();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_Ed_KeyWord_OnKeyDown
// 내용        : KeyDown Event처리
//---------------------------------------------------------------------------
scwin.Edt_SchVal_OnKeyDown_Async = function(e) { //완료
	if (e.keyCode == 13) {
		util.eventAsyncCall(this, scwin.Edt_SchVal_OnKeyDown, arguments);
	}
}

scwin.Edt_SchVal_OnKeyDown = function(e) { //완료
    if (e.keyCode == 13) {
        scwin.lfn_Search();
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼  이벤트 처리
//---------------------------------------------------------------------------
//비동기 호출
scwin.lfn_search_Async = function() { //완료
	util.eventAsyncCall(this, scwin.lfn_Search, arguments);
}

scwin.lfn_Search = function(obj) { //완료
	var pro = Promise.resolve();

	pro = pro.then(function(rv) {
    	return scwin.lfn_selectCntrDtl();
    });

    pro = pro.then(function(rv) {
    	if (Ds_CntrDtl.getCellData(0, "sGdTypeFlgCd") == "3") {
        	btn_Moni.setDisabled(false);
    	} else {
        	btn_Moni.setDisabled(true);
    	}
    });

	return pro;
};

//---------------------------------------------------------------------------
// function 명 : lfn_selectCntrDtl
// 내용        : 계약상세내역 조회
//---------------------------------------------------------------------------
scwin.lfn_selectCntrDtl = function() { //완료

	var pro = Promise.resolve();

	pro = pro.then(function(rv) {
	    if (Edt_sCrNo.getValue().length == 0) {
	    	var pro = Promise.resolve();
	    	pro = pro.then(function(rv) {
	        	return wprom.alertF("계약번호를 입력해주세요", {}, Edt_sCrNo);
	        });
	        pro = pro.then(function(rv) {
	        	Edt_sCrNo.focus();
	        	return Promise.reject("");
	        });
	        return pro;
	    }
	    if (Edt_sCrNo.getValue().length != 12) {
	    	var pro = Promise.resolve();
	    	pro = pro.then(function(rv) {
	        	return wprom.alertF("계약번호가 정확하지 않습니다", {}, Edt_sCrNo);
	        });
	        pro = pro.then(function(rv) {
	        	Edt_sCrNo.focus();
	        	return Promise.reject("");
	        });
	        return pro;
	    }

	    //2015.01.23 (N1501-00121)
	    // medt_PlanNo.Value 화면에 없는 Object 오류 발생 주석처리 
	    /*
	    if (gfn_chekAcesCntrAuth("DsWs_chekCrInqrAuth", "1", medt_PlanNo.Value) == false) {
	        wprom.alert("접근 권한이 없습니다.");
	        return;
	    }
	    */

	    //END (N1501-00121)
	    if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", Edt_sCrNo.getValue()) == false) {
	    	var pro = Promise.resolve();
	    	pro = pro.then(function(rv) {
	        	return wprom.alertF("해당 계약의 조회 권한이 없습니다.", {}, Edt_sCrNo);
	        });
	        pro = pro.then(function(rv) {
	        	Edt_sCrNo.focus();
	        	return Promise.reject("");
	        });
	        return pro;
	    }
	});

	pro = pro.then(function(rv) {
	    //휴면계약 존재 여부 확인
	    return scwin.lfn_selectCntrRetrList(wconv.trim(Edt_sCrNo.getValue()));
	});

	pro = pro.then(function(rv) {

		var transObj = new callService("com.inswave.longterm.psv.ens.task.EAD020Task", "selectCntrDtl", "", "");
		transObj.setUrl("/contract/CBSC028MController.do");
		transObj.setId("selectCntrDtl");
		sCrNo=wconv.trim(Edt_sCrNo.getValue());
