			MaskEdit4.setStyle("color", gds.user40);
	    } else {
			//MaskEdit4.DisableColor = "user27";
			MaskEdit4.setStyle("color", gds.user27);
	    }


    	//alert("Ds_Prprt_cnt ["+Ds_Prprt.GetCount()+"]");
	    /*20131120
	    if(Ds_Prprt.GetCount() > 0){
	    	Div2.Shape2.Bottom = 185 + 11;
