	    for (var m = 0; m < Ds_Prprt.getRowCount(); m++) {
			Ds_Prprt.setCellData(m, "sel1", "0");
	    }

    	for (var k = 0; k < Ds_PiboList.getRowCount(); k++) {
        	Ds_PiboList.setCellData(k, "sel", "0");
    	}

    	if (DsWs_CntrDtl.getRowCount() > 0) {
    		var sPsnGroupFlgcd = DsWs_CntrDtl.getCellData(0, "sPsnGroupFlgcd");
