	            msk_EndPftbpCd.setValue(sEndPftbpCd);
	            //msk_EndPftbpCd.setDisplayFormat("######-9999999");
	              msk_EndPftbpCd.setDisplayFormat("######-#######");
	        }
	        if (sASmtAgntCd != "") {
	            if (wconv.length(sASmtAgntCd) == 10) {
	                msk_ASmtAgntCd.setValue(sASmtAgntCd);
	                msk_ASmtAgntCd.setDisplayFormat("###-##-#####");
	            } else {
	                msk_ASmtAgntCd.setValue(sASmtAgntCd);
	                //msk_ASmtAgntCd.setDisplayFormat("######-9999999");
	                  msk_ASmtAgntCd.setDisplayFormat("######-#######");
	            }
	        }
	        // 마스킹 처리
	        // 계약자 명 sCrtorName
	        if (sAuthByflag != "1") {
	            Ds_CntrDtl.setCellData(0, "sCrtorName", gfn_changeMask(Ds_CntrDtl.getCellData(0, "sCrtorName"), "4"));
	        }

	        // 계약자 주민번호 sCrtorCd
	        //Ds_CntrDtl.SetColumn(0,"sCrtorCd", gfn_changeMask(Ds_CntrDtl.GetColumn(0,"sCrtorCd"),"1"));
	        // 예금자 sDpsrName
	        if (sAuthByflag != "1") {
	            Ds_CntrDtl.setCellData(0, "sDpsrName", gfn_changeMask(Ds_CntrDtl.getCellData(0, "sDpsrName"), "4"));
	        }

	        // 카드번호 sAcctNo
	        Ds_CntrDtl.setCellData(0, "sAcctNo", Ds_CntrDtl.getCellData(0, "sAcctNo"));
	        // 예금자 주민번호 sDpsrCd
	        //Ds_CntrDtl.SetColumn(0,"sDpsrCd", gfn_changeMask(Ds_CntrDtl.GetColumn(0,"sDpsrCd"),"1"));
	        // 만기수익자 sEndPftbpName
	        if (sAuthByflag != "1") {
	            Ds_CntrDtl.setCellData(0, "sEndPftbpName", gfn_changeMask(Ds_CntrDtl.getCellData(0, "sEndPftbpName"), "4"));
	        }

	        // 지정대리인 2014.04.03
	        if (sAuthByflag != "1") {
	            Ds_CntrDtl.setCellData(0, "sAsmtAgntName", gfn_changeMask(Ds_CntrDtl.getCellData(0, "sAsmtAgntName"), "4"));
	        }

	        //대면 청약서 이미지 연계
	        if (sScanObjtYn == "Y") {
	            //goo Btn_scanYn.show();
	            edt_sSaleChnl.setValue("대면");
	        } else {
	            //goo Btn_scanYn.hide();
	            edt_sSaleChnl.setValue("TM");
	        }

	        //trace(DsWs_CntrDtl.savexml());
	        //개인정보 안전조치.(로그인 사람과 계약조회 취급자가 같은 경우 문구 안보이기 다른 경우 보이기)
	        //if(gUserDuty == "33" || gUserDuty == "35"){
	        if (util.getGdsValue("gUserID") == DsWs_CntrDtl.getCellData(0, "sDoerUser") || util.getGdsValue("gUserID") == DsWs_CntrDtl.getCellData(0, "sDoer")) {
	            Static31.hide();
	            //Shape4.hide();
	            //Shape2.Bottom = 542;
	            //grd_Pibo.Bottom = 538;
	            if (Ds_Prprt.getRowCount() > 0) {	
	            	switch_div.show();
	            	switch_div.setSelectedID("sw_div2");
	            	/*
	                Div2_Shape2.Bottom = 185 - 60;
	                Div2_Shape3.Bottom = 185 - 60;
	                Div2_grd_Prprt.Bottom = 180 - 60;
	                Div2_grd_Pibo.Bottom = 180 - 60;
	                Div2.Top = 385;
	                Div2.Left = 4;
	                Div2.Height = 190 - 65; //Ds_Prprt.SetColumn(0, "sel1","1");
	                */
	            } else {
	            	switch_div.show();
	            	switch_div.setSelectedID("sw_div1");
	            	/*
	                Div1.show();
	                Div2.hide();
	                Div1_Shape2.Bottom = 185 - 25;
	                Div1_grd_Pibo.Bottom = 180 - 25;
	                Div1.Top = 364 + 21;
	                Div1.Left = 4;
	                Div1.Height = 190 - 25;
	                */
	            }
	        } else {
	            Static31.show();
	            //Shape4.show();
	            //Shape2.Bottom = 502;
	            //grd_Pibo.Bottom = 498;
	            if (Ds_Prprt.getRowCount() > 0) {
	            	switch_div.show();
	            	switch_div.setSelectedID("sw_div2");
	            	/*
	                Div1.hide();
	                Div2.show();
	                Div2_Shape2.Bottom = 125;
	                Div2_Shape3.Bottom = 125;
	                Div2_grd_Pibo.Bottom = 120;
	                Div2_grd_Prprt.Bottom = 120;
	                Div2.Top = 385; //364;
	                Div2.Left = 4;
	                Div2.Height = 125; //Ds_Prprt.SetColumn(0, "sel1","1");
	                */
	            } else {
	            	switch_div.show();
	            	switch_div.setSelectedID("sw_div1");
	            	/*
	                Div1.show();
	                Div2.hide();
	                Div1_Shape2.Bottom = 147 - 25;
	                Div1_grd_Pibo.Bottom = 144 - 25;
	                Div1.Top = 364 + 21;
	                Div1.Left = 4;
	                Div1.Height = 150 - 25;
	                */
	            }
	        }

	        scwin.lfn_PsnAnnt();

	        //msk_DpsrCd2.Text = Ds_CntrDtl.GetColumn(0,"sDpsrCd");
	        msk_DpsrCd.setValue(sDpsrCd);
	        //msk_DpsrCd.setDisplayFormat("######-9999999");
	          msk_DpsrCd.setDisplayFormat("######-#######");
