	    if (Ds_Prprt.getRowCount() > 0) {
	        Ds_Prprt.setCellData(0, "sel1", "1");
	    } else {
	        if (Ds_PiboList.getRowCount() > 0) {
	            Ds_PiboList.setCellData(0, "sel", "1");
	            scwin.lfn_btnCntlPibo(true);
	        }
	    }
	    if (Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "21") {
	        Div1_Btn_PiboPrnt.setDisabled(false);
	        Div2_Btn_PiboPrnt.setDisabled(false);
	    } else {
	        Div1_Btn_PiboPrnt.setDisabled(true);
	        Div2_Btn_PiboPrnt.setDisabled(true);
	    }

    	/*
	    //대리점인지 여부(30:대리점,33:사용인,35:TM사용인) 2014.2.13
	    if(gUserDuty == "30" || gUserDuty == "33" || gUserDuty == "35"){

	    	if(Ds_CntrDtl.GetColumn(0,"sCmMetd") == "03"){
