//---------------------------------------------------------------------------
// function 명 : lfn_PiboListClick
// 내용        : 피보험자리스트 클릭시 이벤트처리
//---------------------------------------------------------------------------
scwin.lfn_PiboListClick = function(nRow, nCol) { //완료
    if (nCol == 0) { //sel
        //물건은 모두 체크박스 값을 0으로 바꾼다.
        for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
            Ds_Prprt.setCellData(i, "sel1", 0);
        }
        for (var i = 0; i < Ds_PiboList.getRowCount(); i++) {
            if (Ds_PiboList.getRowCount() > 0) {
                if (nRow != i && Ds_PiboList.getCellData(i, "sel") == "1") {
                    Ds_PiboList.setCellData(i, "sel", 0);
                } else {
                    Ds_PiboList.setCellData(nRow, "sel", "1"); //continue;
                }
            }
        }

        //for
        if (Ds_PiboList.getCellData(nRow, "sel") == "0") {
            scwin.lfn_btnCntlPibo(true);
        } else {
            scwin.lfn_btnCntlPibo(true);
        }
    } else if (nCol == 1 || nCol == 7 || nCol == 8) {
		var idx = scwin.lfn_GetChkIdx();
	    var nGroupSeqno = "";
	    var nInrpsSeqno = "";
	    var sInrpsCd = "";
	    /*
	    if (idx != -1) {
	        nGroupSeqno = Ds_PiboList.getCellData(idx, "nGroupSeqno");
	        nInrpsSeqno = Ds_PiboList.getCellData(idx, "nInrpsSeqno");
	        sInrpsCd    = Ds_PiboList.getCellData(idx, "sInrpsCd");
	    }
	    */
	    nGroupSeqno = Ds_PiboList.getCellData(nRow, "nGroupSeqno");
	    nInrpsSeqno = Ds_PiboList.getCellData(nRow, "nInrpsSeqno");
	    sInrpsCd    = Ds_PiboList.getCellData(nRow, "sInrpsCd");
	    var sCrNo         = Ds_CntrDtl.getCellData(0, "sCrNo");
	    var sCrtorCd      = Ds_CntrDtl.getCellData(0, "sCrtorCd");
	    var sEndPftbpCd   = Ds_CntrDtl.getCellData(0, "sEndPftbpCd");
	    var sAsmtAgntCd   = Ds_CntrDtl.getCellData(0, "sAsmtAgntCd");

		//팝업창으로 보낼 자료
		var indata  = {"sCrNo" : sCrNo, "nGroupSeqno" : nGroupSeqno, "nInrpsSeqno" : nInrpsSeqno, "sInrpsCd" : sInrpsCd, "sAsmtAgntCd" : sAsmtAgntCd, "sEndPftbpCd" : sEndPftbpCd, "sCrtorCd" : sCrtorCd};
		//팜업창 위치
		var popInfo = {
			"width"     : 913,                  //폭    : 생략시 기본값
			"height"    : 693,                  //높이   : 생략시 기본값
			"top"       : 100,                   //top   : 생략시 중심
			//"left"      : 20,                   //left  : 생략시 중심
			"indata"    : indata,               //보낼 자료
			"title"     : "인적사항"            //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
			//,"callback"  : scwin.popupCallback,
		};
		//wprom.windowOpen("/ui/cbsc/CBSC086P.xml", popInfo); //D021_Pop.xml
		wprom.popupOpen("/ui/cbsc/CBSC086P.xml", popInfo); //D021_Pop.xml
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_PrprtClick
// 내용        : 물건리스트 클릭시 이벤트처리
//---------------------------------------------------------------------------
scwin.lfn_PrprtClick = function(nRow, nCol) { //완료
    if (nCol == 0) { //sel1
        //피보험자리스트는 모두 체크박스 값을 0으로 바꾼다.
        for (var i = 0; i < Ds_PiboList.getRowCount(); i++) {
            Ds_PiboList.setCellData(i, "sel", 0);
        }
        for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
            if (Ds_Prprt.getRowCount() > 0) {
                if (nRow != i && Ds_Prprt.getCellData(i, "sel1") == "1") {
                    Ds_Prprt.setCellData(i, "sel1", 0);
                } else {
                    Ds_Prprt.setCellData(nRow, "sel1", "1");
                }
            }
        }
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_CntorInfo
// 내용        : 계약자링크 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_CntorInfo = function(e) { //완료
	//gubn 1:계약자, 2:만기수익자, 3:지정대리인,4:피보험자
	var idx = scwin.lfn_GetChkIdx();
    var nGroupSeqno = "";
    var nInrpsSeqno = "";
    var sInrpsCd = "";
    if (idx != -1) {
        nGroupSeqno = Ds_PiboList.getCellData(idx, "nGroupSeqno");
        nInrpsSeqno = Ds_PiboList.getCellData(idx, "nInrpsSeqno");
        sInrpsCd    = Ds_PiboList.getCellData(idx, "sInrpsCd");
    }
    var sCrNo         = Ds_CntrDtl.getCellData(0, "sCrNo");
    var sCrtorCd      = Ds_CntrDtl.getCellData(0, "sCrtorCd");
    var sEndPftbpCd   = Ds_CntrDtl.getCellData(0, "sEndPftbpCd");
    var sAsmtAgntCd   = Ds_CntrDtl.getCellData(0, "sAsmtAgntCd");
    var sInsurPftbpCd = Ds_CntrDtl.getCellData(0, "sInsurPftbpCd");

	//팝업창으로 보낼 자료
	var indata  = {"sCrNo" : sCrNo, "nGroupSeqno" : nGroupSeqno, "nInrpsSeqno" : nInrpsSeqno, "sInrpsCd" : sInrpsCd, "sAsmtAgntCd" : sAsmtAgntCd, "sEndPftbpCd" : sEndPftbpCd
					, "sInsurPftbpCd" : sInsurPftbpCd, "sCrtorCd" : sCrtorCd};
	//팜업창 위치
	var popInfo = {
			"width"     : 913,                  //폭    : 생략시 기본값
			"height"    : 693,                  //높이   : 생략시 기본값
		"top"       : 100,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
		"indata"    : indata,               //보낼 자료
		"title"     : "인적사항"            //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.popupCallback,
	};
	wprom.popupOpen("/ui/cbsc/CBSC086P.xml", popInfo);  //D021_Pop.xml
};

