scwin.lfn_btnCntlPibo = function(bEnable1) { //완료
	var enable = !bEnable1;
    Div1_Btn_TrtyDtl.setDisabled(enable);
    Div2_Btn_TrtyDtl.setDisabled(enable);
    Div1_Btn_BosangLmt.setDisabled(enable);
    Div2_Btn_BosangLmt.setDisabled(enable);
    if (!enable) {
        Div1_Btn_Qst.setDisabled(enable);
        Div2_Btn_Qst.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "10") {
        Div1_Btn_Qst.setDisabled(enable);
        Div2_Btn_Qst.setDisabled(enable);
    }
    if (!enable) {
        Div1_Btn_OthPrm.setDisabled(enable);
        Div2_Btn_OthPrm.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "10") {
        Div1_Btn_OthPrm.setDisabled(enable);
        Div2_Btn_OthPrm.setDisabled(enable);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_btnCntl
// 내용        : 버튼을 인에이블 디세이블
//---------------------------------------------------------------------------
scwin.lfn_btnCntlMain = function(strEnable) { //완료
	var enable = !strEnable;
    Btn_InoutTrans.setDisabled(enable);
    Btn_Prem.setDisabled(enable);
    Button_bu.setDisabled(enable);
    //goo Btn_scanYn.setDisabled(enable);
    if (!enable) {
        Btn_AutoTran.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "10") {
        Btn_AutoTran.setDisabled(enable);
    }
    if (!enable) {
        Btn_Card.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "10") {
        Btn_Card.setDisabled(enable);
    }
    Btn_CntrMod.setDisabled(enable);
    Btn_MdfDsgn.setDisabled(enable);
    Btn_CertIssu.setDisabled(enable);

    //Btn_sugumMdf.Enable=enable;
    //Btn_Cncl.Enable=enable;
    Btn_Loan.setDisabled(enable);
    if (!enable) {
        Btn_Buhal.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sCrStatCd") == "10") {
        Btn_Buhal.setDisabled(enable);
    }
    if (!enable) {
        Btn_sugumMdf.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sRecvProgStat") != "4" && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "10") {
        Btn_sugumMdf.setDisabled(enable);
    }
    if (!enable) {
        Btn_Ipgum.setDisabled(enable);
    } else if (enable && Ds_CntrDtl.getCellData(0, "sCrStatCd") != "10" && Ds_CntrDtl.getCellData(0, "sPaymCyclCd") != "99" && Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") != "20") {
        Btn_Ipgum.setDisabled(enable);
    }

    /*if(!enable){
    	Btn_Pymt.Enable=enable;
    }else if(enable && (Ds_CntrDtl.getColumn(0, "sCrStatCd")=='00' || Ds_CntrDtl.getColumn(0, "sCrStatCd")=='10' || Ds_CntrDtl.getColumn(0, "sCrStatCd")=='20' || Ds_CntrDtl.getColumn(0, "sCrStatCd")=='97')){
    	Btn_Pymt.Enable=enable;
    }*/
    if ((Ds_CntrDtl.getCellData(0, "sCrStatCd") == "00" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "10" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "20" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "90" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "97" || (Ds_CntrDtl.getCellData(0, "sBunsMetdDocuClsfCd") == "7" && Ds_CntrDtl.getCellData(0, "sCrStatCd") == "30"))) {
        Btn_Pymt.setDisabled(false);
    } else {
        Btn_Pymt.setDisabled(true);
    }
    if (!enable) {
        Btn_Hae.setDisabled(enable);
    } else if (enable && ((Ds_CntrDtl.getCellData(0, "sCrStatCd") == "00" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "10" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "20" || Ds_CntrDtl.getCellData(0, "sCrStatCd") == "97"))) {
        Btn_Hae.setDisabled(enable);
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_GetChkIdx
// 내용        : 피보험자 리스트의 체크된 인덱스 취득
//---------------------------------------------------------------------------
scwin.lfn_GetChkIdx = function() { //완료
	var rtn = -1;
	for (var i = 0; i < Ds_PiboList.getRowCount(); i++) {
        if (Ds_PiboList.getRowCount() > 0) {
            if (Ds_PiboList.getCellData(i, "sel") == "1") {
                return i;
            }
        }
    }
    return rtn;
};

//---------------------------------------------------------------------------
// function 명 : lfn_GetChkPrprtIdx
// 내용        : 물건 리스트의 체크된 인덱스 취득
//---------------------------------------------------------------------------
scwin.lfn_GetChkPrprtIdx = function() { //완료
    var rtn = -1;
    for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
        if (Ds_Prprt.getRowCount() > 0) {
            if (Ds_Prprt.getCellData(i, "sel1") == "1") {
                return i;
            }
        }
    }
    return rtn;
};

//---------------------------------------------------------------------------
// function 명 : lfn_TrtyDtl
// 내용        : 특약버튼 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_TrtyDtl = function(obj) { //완료
	var sCrNo = wconv.trim(Edt_sCrNo.getValue());
    var sPsnPrprtGroupFlgcd = "";
    var nPsnPrprtGroupSeqno = "";
    var idx = "";
    var nGroupSeqno = "";
    var nInrpsSeqno = "";
    if (idx == -1) {
        wprom.alert("피보험자를 선택해 주십시요");
        return;
    }
    var prprtCheckFlag = false;
    for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
        if (Ds_Prprt.getCellData(i, "sel1") == "1") {
            index = i;
            prprtCheckFlag = true;
            break;
        }
    }
    if (prprtCheckFlag) {
        //alert("idx2["+lfn_GetChkPrprtIdx()+"]");
        idx = scwin.lfn_GetChkPrprtIdx();
        nGroupSeqno = "";
        nInrpsSeqno = Ds_Prprt.getCellData(idx, "nInsrdObjctSeqno");
    } else {
        //alert("idx1["+lfn_GetChkIdx()+"]");
        idx = scwin.lfn_GetChkIdx();
        nGroupSeqno = Ds_PiboList.getCellData(idx, "nGroupSeqno");
        nInrpsSeqno = Ds_PiboList.getCellData(idx, "nInrpsSeqno");
    }


    //trace(Ds_PiboList.saveXml());
    if (nGroupSeqno.length > 0) {
        //단체인경우
        nPsnPrprtGroupSeqno = nInrpsSeqno;
        sPsnPrprtGroupFlgcd = "3";
    } else {
        nPsnPrprtGroupSeqno = nInrpsSeqno;
        sPsnPrprtGroupFlgcd = "1";
        if (prprtCheckFlag) {
            //물건인경우
            sPsnPrprtGroupFlgcd = "2";
        }
    }


    //alert("nPsnPrprtGroupSeqno["+nPsnPrprtGroupSeqno+"]sPsnPrprtGroupFlgcd["+sPsnPrprtGroupFlgcd+"]");
    //alert(nPsnPrprtGroupSeqno);
    //sPsnPrprtGroupFlgcd=Ds_CntrDtl.getColumn(0,"sPsnGroupFlgcd");
    //alert(sPsnPrprtGroupFlgcd);
    if (sCrNo.length != 12) {
        wprom.alert("계약번호를 확인해 주십시오.");
        return;
    }
    //var arg = "sCrNo=" + wconv.quote(sCrNo) + " " + "sPsnPrprtGroupFlgcd=" + wconv.quote(sPsnPrprtGroupFlgcd) + " " + "nPsnPrprtGroupSeqno=" + wconv.quote(nPsnPrprtGroupSeqno);
console.log("★★★★★★sCrNo : " + sCrNo 
	                  + " sPsnPrprtGroupFlgcd : " + sPsnPrprtGroupFlgcd
	                  + " nPsnPrprtGroupSeqno : " + nPsnPrprtGroupSeqno);	                  

	//팝업창으로 보낼 자료
	var indata  = {"sCrNo" : sCrNo
	                  , "sPsnPrprtGroupFlgcd" : sPsnPrprtGroupFlgcd
	                  , "nPsnPrprtGroupSeqno" : nPsnPrprtGroupSeqno};

	//팜업창 위치
	var popInfo = {
		"width"     : 1124,                  //폭    : 생략시 기본값
		"height"    : 407,                  //높이   : 생략시 기본값  
		"top"       : 150,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
		"indata"    : indata,               //보낼 자료
		"title"     : "특약세부내역"            //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.ddd
	};
	//wprom.windowOpen("/ui/cbsc/CBSC125P.xml", popInfo);
	wprom.popupOpen("/ui/cbsc/CBSC125P.xml", popInfo);

    //trace("argu ==>  " + arg);
    //var result = Open("psv_ens::D060_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
};

