        wprom.alert("계약번호를 확인해 주십시오.");
        return;
    }
    //var arg = "sCrNo=" + wconv.quote(sCrNo);
    //trace("argu ==>  " + arg);
    //팝업창으로 보낼 자료
	var indata  = {"sCrNo" : sCrNo};
	//팜업창 위치
	var popInfo = {
		"width"     : 677,                  //폭    : 생략시 기본값
		"height"    : 331,                  //높이   : 생략시 기본값  
		"top"       : 150,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
		"indata"    : indata,               //보낼 자료
		"title"     : "계약상태 이력조회"                    //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.ddd
	};
	wprom.popupOpen("/ui/cbsc/CBSC030P.xml", popInfo);
    //var result = Open("psv_ens::D030_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
};

