	var popInfo = {
		"width"     : 740,                  //폭    : 생략시 기본값
		"height"    : 510,                  //높이   : 생략시 기본값  
		"top"       : 150,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
