		"title"     : "계약상태 이력조회"                    //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.ddd
	};
	//wprom.windowOpen("/ui/cbsc/CBSC087P.xml", popInfo);    
	wprom.popupOpen("/ui/cbsc/CBSC087P.xml", popInfo);
    //trace("argu ==>  " + arg);
    //var result = Open("psv_ens::D040_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
};

//---------------------------------------------------------------------------
// function 명 : lfn_PremList
// 내용        : 보험료내역버튼 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_PremList = function() { //완료
	var sCrNo = Ds_CntrDtl.getCellData(0, "sCrNo");
    // 계약자
    var sCrtor = Ds_CntrDtl.getCellData(0, "sCrtorName");
    // 계약상태
    var sCrStat = Ds_CntrDtl.getCellData(0, "statNm");
    // 보험기간
    var sInsurStrtdate = Ds_CntrDtl.getCellData(0, "sInsurStrtdate");
    var sInsurEndDate = Ds_CntrDtl.getCellData(0, "sInsurEndDate");
    if (wconv.length(sInsurStrtdate) == 8) {
        sInsurStrtdate = wconv.substr(sInsurStrtdate, 0, 4) + "/" + wconv.substr(sInsurStrtdate, 4, 2) + "/" + wconv.substr(sInsurStrtdate, 6, 2);
    }
    if (wconv.length(sInsurEndDate) == 8) {
        sInsurEndDate = wconv.substr(sInsurEndDate, 0, 4) + "/" + wconv.substr(sInsurEndDate, 4, 2) + "/" + wconv.substr(sInsurEndDate, 6, 2);
    }
    var sInsurTerm = sInsurStrtdate + "~" + sInsurEndDate;
    if (sCrNo.length != 12) {
        wprom.alert("계약번호를 확인해 주십시오.");
        return;
    }

    //팝업창으로 보낼 자료
	var indata  = {"sCrNo" : sCrNo, "sCrtor" : sCrtor, "sCrStat" : sCrStat, "sInsurTerm" : sInsurTerm};
	//팜업창 위치
	var popInfo = {
		"width"     : 855,                  //폭    : 생략시 기본값
		"height"    : 579,                  //높이   : 생략시 기본값  
		//"top"       : 150,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
		"indata"    : indata,               //보낼 자료
		"title"     : "보험료입출금내역"                    //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.ddd
	};
	//goo wprom.windowOpen("/ui/cbsc/CBSC059P.xml", popInfo);    
	wprom.popupOpen("/ui/cbsc/CBSC059P.xml", popInfo);

	//return;
    //var arg = "sCrNo=" + wconv.quote(sCrNo) + " sCrtor=" + wconv.quote(sCrtor) + " sCrStat=" + wconv.quote(sCrStat) + " sInsurTerm=" + wconv.quote(sInsurTerm);
    //trace("argu ==>  " + arg);
    //var result = Open("psv_ens::D050_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
};

