//---------------------------------------------------------------------------
// function 명 : lfn_cntr_mod_hist
// 내용        : 계약상태이력링크 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_cntr_mod_hist = function(obj, stroldText, strText) { //완료
	//wprom.alert("추후개발예정"); 

    //trace("Ds_EtcInfo : " + Ds_EtcInfo.SaveXML());
    //trace("crChngCount : " + Ds_EtcInfo.getColumn(0,"crChngCount"));
    if (Ds_EtcInfo.getCellData(0, "crChngCount") == 0) {
        return;
    }

    gds.gDs_LinkArg.removeAll();
//    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
//    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.
//    util.insertColumn(gds.gDs_LinkArg, [        [            "sCrNo",            "sCrNo",            "text"        ]    ]);
//    util.insertColumn(gds.gDs_LinkArg, [        [            "sScrnId",            "sScrnId",            "text"     ]    ]);
//    gds.gDs_LinkArg.setCellData(0, "sCrNo", sCrNo);
//    gds.gDs_LinkArg.setCellData(0, "sScrnId", "D110");

	var oDsJson=[{
		 "sCrNo"    : sCrNo    //계약자코드(주민번호)
		,"sScrnId"   : "D110"           //화면번호
	}];
	gds.gDs_LinkArg.setJSON(oDsJson);
    //trace("parent.Id : " + parent.Id);
    //var result = Open("psv_ens::D110.xml", "", 800, 520, "TaskBar=false Resize=false");
    var popInfo = {
    	width : 800,
    	height : 520,
    	title : "계약변경내역조회"
    };
    wprom.popupOpen("/ui/cbsc/CBSC174P.xml", popInfo);

};

