//---------------------------------------------------------------------------
scwin.lfn_cntr_mod_hist_Cusor = function(e) { //완료
    if (Ds_EtcInfo.getCellData(0, "crChngCount") == 0) {
        //Edit34.Cursor = "ARROW";
        Edit34.setStyle("cursor", "");
    } else {
        //Edit34.Cursor = "HAND";
        Edit34.setStyle("cursor", "pointer");
    }
};

