// function 명 : lfn_insu_simsa
// 내용        : 인수심사 링크 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_insu_simsa = function(obj) { //완료

    if (Ds_EtcInfo.getCellData(0, "insuSimsaYn") == "N") {
        return;
    }
    var sPlanNo = Ds_CntrDtl.getCellData(0, "sPlanNo");
    var rqstFlag = "20";
    var sOnlySrarch = "1";
    var arg = {};
    arg.sPlanNo = sPlanNo 
    arg.rqstFlag =rqstFlag
    arg.sOnlySrarch=sOnlySrarch;

    var popInfo = {
			"width"     : 732,
			"height"    : 674,  
		    indata : arg,
			"title"     : "인수심의결재"
    };
    //trace("argu ==>  " + arg);
    //var result = Open("ssr_sst::B170_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
    wprom.popupOpen("/ui/cbsc/CBSC165P.xml",popInfo);    
};

