//---------------------------------------------------------------------------
// function 명 : btn_CntrMod
// 내용        : 스캔심사버튼 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.Lfn_ScanSimsa = function(obj) { //완료

    if (Ds_EtcInfo.getCellData(0, "scanSimsaYn") == "N") {
        return;
    }

    //edt_deptCd 팀코드
    //edt_deptName 팀명을 주면 더 좋겠는데 없으면 우선 빼세요
    //cal_standStrtdate 청약일
    //cal_standEndDate  청약일+7일
    //sPlanNo 설계번호
    var cal_standStrtdate = wconv.addDateSimple(Ds_CntrDtl.getCellData(0, "sSsrtDate"), -7);
    gds.gDs_LinkArg.removeAll();
    /*
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    util.insertColumn(gds.gDs_LinkArg, [        [            "edt_deptCd",            "edt_deptCd",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "edt_deptName",            "edt_deptName",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "cal_standStrtdate",            "cal_standStrtdate",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "cal_standEndDate",            "cal_standEndDate",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [       [            "sPlanNo",            "sPlanNo",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "sOnlySrarch",            "sOnlySrarch",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "sScrnId",            "sScrnId",            "text"        ]    ]);
    gds.gDs_LinkArg.setCellData(0, "edt_deptCd", Ds_CntrDtl.getCellData(0, "sFrstDoerTeamCd"));
    gds.gDs_LinkArg.setCellData(0, "edt_deptName", "");
    gds.gDs_LinkArg.setCellData(0, "cal_standStrtdate", cal_standStrtdate);
    gds.gDs_LinkArg.setCellData(0, "cal_standEndDate", Ds_CntrDtl.getCellData(0, "sSsrtDate"));
    gds.gDs_LinkArg.setCellData(0, "sPlanNo", Ds_CntrDtl.getCellData(0, "sPlanNo"));
    gds.gDs_LinkArg.setCellData(0, "sOnlySrarch", "1");
    gds.gDs_LinkArg.setCellData(0, "sScrnId", "D020");
    */
    var oDsJson=[{
		 "edt_deptCd" : Ds_CntrDtl.getCellData(0, "sFrstDoerTeamCd")
	    ,"edt_deptName"  : ""
	    ,"cal_standStrtdate"  : cal_standStrtdate
	    ,"cal_standEndDate"  : Ds_CntrDtl.getCellData(0, "sSsrtDate")
	    ,"sPlanNo"  : Ds_CntrDtl.getCellData(0, "sPlanNo")
	    ,"sOnlySrarch"  : "1"
	    ,"sScrnId"  : "D020"
	}];
	gds.gDs_LinkArg.setJSON(oDsJson);
    if (Ds_CntrDtl.getCellData(0, "sSsrtDate") > "20140331") {
        gfn_OpenLinkPage("CBSC110M");
    } else {
    	//alert("추후개발예정- B520.xml");
        //gfn_OpenLinkPage("B520");
    }

};

