//---------------------------------------------------------------------------
// function 명 : btn_CntrMod
// 내용        : 부담보링크 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.Lfn_Budambo = function(obj) { //완료

    if (Ds_EtcInfo.getCellData(0, "nCount") == 0) {
        return;
    }
    var sPlanNo = Ds_CntrDtl.getCellData(0, "sPlanNo");
    var sCrNo = Ds_CntrDtl.getCellData(0, "sCrNo");
    var sInsurStrtdate = Ds_CntrDtl.getCellData(0, "sInsurStrtdate");
    var sInsurEndDate = Ds_CntrDtl.getCellData(0, "sInsurEndDate");
    var nInrpsSeqno = Ds_PiboList.getCellData(0, "nInrpsSeqno");
    var nCrChngNo = Ds_CntrDtl.getCellData(0, "nCrChngNo");
    var arg = {};
    arg.sPlanNo=sPlanNo; 
    arg.sCrNo=sCrNo; 
    arg.sInsurStrtdate=sInsurStrtdate; 
    arg.sInsurEndDate=sInsurEndDate; 
    arg.nInrpsSeqno=nInrpsSeqno; 
    arg.nCrChngNo=nCrChngNo; 
    arg.sOnlySrarch="1";
    //trace("argu ==>  " + arg);
    //var result = Open("ssr_sst::B210_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
    var pObj = {"Ds_LTIDA00" : Ds_CntrDtl} 
    var popInfo = {
			"width"     : 820,
			"height"    : 646,  
			"indata"    : arg,
			"parentObject":pObj,
			"title"     : "특정부위.질병 부담보"
    };
    wprom.popupOpen("/ui/cbsc/CBSC126P.xml", popInfo);
};

