//---------------------------------------------------------------------------
// function 명 : lfn_qst
// 내용        : 질의서버튼 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_qst = function(obj) { //완료
	var idx = scwin.lfn_GetChkIdx();
    if (idx == -1) {
        wprom.alert("피보험자를 선택해 주십시요");
        return;
    }


    // 마스킹 처리 2014.02.13
    // 피보험자 명 sCrtorName
    //Ds_PiboList.SetColumn(idx,"sInrpsName", gfn_changeMask( Ds_PiboList.GetColumn(idx,"sInrpsName"),"4"));
    // 피보험자 주민번호 sCrtorCd
    //Ds_PiboList.SetColumn(idx,"sInrpsCd", gfn_changeMask(Ds_PiboList.GetColumn(idx,"sInrpsCd"),"1"));
    var nGroupSeqno = Ds_PiboList.getCellData(idx, "nGroupSeqno");
    var nInrpsSeqno = Ds_PiboList.getCellData(idx, "nInrpsSeqno");
    var nPsnPrprtGroupSeqno = "";
    var sPsnPrprtGroupFlgcd = "";
    var sInrpsCd = Ds_PiboList.getCellData(idx, "sInrpsCd");
    var sInrpsName = Ds_PiboList.getCellData(idx, "sInrpsName");
    var sCrtorRelnName = Ds_PiboList.getCellData(idx, "sCrtorRelnName");
    var sDriveYn = Ds_CntrDtl.getCellData(0, "sGdTypeFlgCd");
    if (Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "21") {
        //단체인경우
        nPsnPrprtGroupSeqno = nInrpsSeqno; //sPsnPrprtGroupFlgcd='3';
    } else {
        nPsnPrprtGroupSeqno = nInrpsSeqno; //sPsnPrprtGroupFlgcd='1';
    }

    //팝업창으로 보낼 자료
	var indata  = {"sCrNo" : Ds_CntrDtl.getCellData(0, "sCrNo"),
		"sPsnGroupFlgcd" : Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd"),
		"nPsnGroupSeqno" : nPsnPrprtGroupSeqno,
		"sSsrtDate" : Ds_CntrDtl.getCellData(0, "sSsrtDate"),
		"sInrpsCd" : sInrpsCd,
		"sInrpsName" : sInrpsName
		};
	if (Ds_CntrDtl.getCellData(0, "sGdCd") == "2801") {
        indata.sDriveYn = "1"; //운전구분
    } else {
        if (Ds_CntrDtl.getCellData(0, "sNotcGdFlag") == "01" || Ds_CntrDtl.getCellData(0, "sNotcGdFlag") == "02") {
            indata.sDriveYn="1"; //운전구분
        } else {
            indata.sDriveYn="0"; //운전구분
        }
    }
    indata.sCrtorRelnName=sCrtorRelnName;
    indata.sOnlySrarch='1'; //편집기능 디세이블 
    indata.nCrChngNo=Ds_CntrDtl.getCellData(0, "nCrChngNo"); //편집기능 디세이블 
    indata.sNotcGdFlag=Ds_CntrDtl.getCellData(0, "sNotcGdFlag"); //고지상품구분
    indata.sGdCd=Ds_CntrDtl.getCellData(0, "sGdCd"); //상품코드 2014.11.25 (N1408-00032) 간병보험 적용을 위해 추가

	  var pObj = {   
	   		"Ds_Pibo" : Ds_PiboList      // 피보험자 정보 Ds 
	   		,"Ds_LTIDA00" : Ds_CntrDtl   // 보험 기본사항 정보 Ds  
	  }  
	//팜업창 위치
	var popInfo = {
		"width"     : 890,   // 수정(width, height): 2017.11.04, by 진태만   --> 880, 879 
		"height"    : 879,                  //높이   : 생략시 기본값  
		//"top"       : 150,                   //top   : 생략시 중심
		//"left"      : 20,                   //left  : 생략시 중심
		"indata"    : indata,               //보낼 자료
		"parentObject":pObj,
		"title"     : "피보험자 고지사항"            //팝업창에 표시할 TITLE 명칭 : 생략시 기본값 출력
		//,"callback"  : scwin.ddd
	};
	//wprom.windowOpen("/ui/cbsc/CBSC129P.xml", popInfo);	// 피보험자고지사항 팝업 -> 개발 완료 후 windowOpen으로 변경할 것
	wprom.popupOpen("/ui/cbsc/CBSC129P.xml", popInfo);	// 피보험자고지사항 팝업

	return;
    /*
    var arg = "";
    arg += " sCrNo=" + wconv.quote(Ds_CntrDtl.getCellData(0, "sCrNo")); //설계번호
    arg += " sPsnGroupFlgcd=" + wconv.quote(Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd")); //개인단체구분
    arg += " nPsnGroupSeqno=" + wconv.quote(nPsnPrprtGroupSeqno); //seq
    arg += " sSsrtDate=" + wconv.quote(Ds_CntrDtl.getCellData(0, "sSsrtDate")); //청약일자
    arg += " sInrpsCd=" + wconv.quote(sInrpsCd);
    arg += " sInrpsName=" + wconv.quote(sInrpsName);
    //arg += " sDrveFlagCd="+quote(Ds_Pibo.GetColumn(0, "sDrveFlagCd"));
    if (Ds_CntrDtl.getCellData(0, "sGdCd") == "2801") {
        arg += " sDriveYn=" + wconv.quote("1"); //운전구분
    } else {
        if (Ds_CntrDtl.getCellData(0, "sNotcGdFlag") == "01" || Ds_CntrDtl.getCellData(0, "sNotcGdFlag") == "02") {
            arg += " sDriveYn=" + wconv.quote("1"); //운전구분
        } else {
            arg += " sDriveYn=" + wconv.quote("0"); //운전구분
        }
    }
    arg += " sCrtorRelnName=" + wconv.quote(sCrtorRelnName);
    arg += " sOnlySrarch='1'"; //편집기능 디세이블 
    arg += " nCrChngNo=" + wconv.quote(Ds_CntrDtl.getCellData(0, "nCrChngNo")); //편집기능 디세이블 
    arg += " sNotcGdFlag=" + wconv.quote(Ds_CntrDtl.getCellData(0, "sNotcGdFlag")); //고지상품구분
    arg += " sGdCd=" + wconv.quote(Ds_CntrDtl.getCellData(0, "sGdCd")); //상품코드 2014.11.25 (N1408-00032) 간병보험 적용을 위해 추가
    var result = Open("ssr_sst::B230_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true"); //Dialog("ssr_sst::B231_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
    */ 
};

//---------------------------------------------------------------------------
// function 명 : lfn_oth_prm
// 내용        : 타보험버튼 클릭시 이벤트 처리
//---------------------------------------------------------------------------
scwin.lfn_oth_prm = function(obj) { //완료
	var idx = scwin.lfn_GetChkIdx();
    if (idx == -1) {
        wprom.alert("피보험자를 선택해 주십시요");
        return;
    }
    var nGroupSeqno = Ds_PiboList.getCellData(idx, "nGroupSeqno");
    var nInrpsSeqno = Ds_PiboList.getCellData(idx, "nInrpsSeqno");
    var nPsnPrprtGroupSeqno = "";
    var sPsnPrprtGroupFlgcd = "";
    if (Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd") == "21") {
        //단체인경우
        nPsnPrprtGroupSeqno = nInrpsSeqno; //sPsnPrprtGroupFlgcd='3';
    } else {
        nPsnPrprtGroupSeqno = nInrpsSeqno; //sPsnPrprtGroupFlgcd='1';
    }

    //팝업창으로 보낼 자료
	var indata  = {"sCrNo" : Ds_CntrDtl.getCellData(0, "sCrNo"), 
		"sPsnGroupFlgcd" : Ds_CntrDtl.getCellData(0, "sPsnGroupFlgcd"), 
		"sPsnPrprtGroupSeqno" : nPsnPrprtGroupSeqno,
		"sCrChngNo" : Ds_CntrDtl.getCellData(0, "nCrChngNo"),
		"nCount" : 1,
		"sOnlySrarch" : "1"};
	var pObj = {  // 수정(추가): 2017.11.20, by 진태만  
			 "Ds_LTIDA00" : Ds_CntrDtl   // 보험 기본사항 정보 Ds  
		}
	//팜업창 위치
	var popInfo = {
