        var setParam = ["sPlanNo|" + Ds_CntrDtl.getCellData(0,"sPlanNo")
                             , "sDmCode|L057"];
		console.log("setParam : "+setParam);
        //alert(Ds_CntrDtl.getCellData(0,"sCrNo"));
 		var reportInfo = {};
		//단체명세서(장기보험)
		reportInfo.rptName        = "longterm/B640_Rep";		//레포트 파일명       (필수) ex) "sc/A0001"//
		reportInfo.dataType       = "xml";				//인터페이스 Task Value      ex) com.inswave.admin.task.JsonConvertorTask

		reportInfo.sInterfaceType = "Y";					//인터페이스 Action Value    ex) sample_PrintEndObjectList 
		reportInfo.taskValue       = "com.inswave.longterm.psv.ens.task.EBE230Task";					// 인터페이스: B640_Rep.ozr(단체명세서	장기보험)
		reportInfo.actionValue    = "printB640_RepV01";					//인터페이스 사용유무 (필수) ex) "Y" or "N" 
		reportInfo.setParam        = setParam;		//인테페이스에서 사용하는 파라미터 정보 배열로 파라미터 개수만큼 입력한다.

		reportInfo.gUserID        = util.getGdsValue("gUserEmpName");			 //레포트 하단에 출력될 발급자명 ex) "홍길동"
		reportInfo.sDirectPrint   = "0";					//바로출력 1: 바로출력 바로출력할경우 EXE설치파일 설치 필요 ex) "1" or "0"
		reportInfo.title              = "피보험자명세";	//팝업창 타이틀

		wprom.reportOpen(reportInfo);
/*
