scwin.lfn_ScanYn = function(obj) { //완료
	wprom.alert("추후적용예정");
	return;
    //var arg = "";
    //arg += "sPlanNo="+quote(Ds_CntrDtl.getColumn(0,"sPlanNo"));//청약번호
    //arg += " sImagClsfCd="+"JCA10001";//분류코드
    //var result = Open("ssr_sst::B340_Pop01.xml", arg , 800, 550, "TaskBar=true Resize=true");
    if (Ds_CntrDtl.getCellData(0, "sScanObjtYn") == "Y") {
        //N1406-00225
        var nRow;
        var sPlanNo;
        sPlanNo = Ds_CntrDtl.getCellData(0, "sPlanNo");
        var url;
        //운영환경일 때 Y  아니면 N 
        if (gds.gProdYN == "Y") {
            url = "http://10.1.1.58:9000/imgsys/jsp/index.jsp?";
        } else {
            url = "http://10.1.1.131:8090/imgsys/jsp/index.jsp?";
        }
        var arg = "UsrId=" + util.getGdsValue("gUserID") + "&";
        arg += "hidJob_gubun=4" + "&";
        arg += "ContractNo=" + sPlanNo; //설계번호
        ExecBrowser(url + arg);
    } else {
        wprom.alert("비대면 계약입니다");
    }
};

//---------------------------------------------------------------------------
// function 명 : btn_SwHsHldGds_OnClick
// 내용        : 명기가재물건등록화면 연결.
//---------------------------------------------------------------------------
scwin.btn_SwHsHldGds_OnClick = function(obj) { //완료
	var nFindRow;
    var nSeq1;

    //2014.08.29 수정 By SDJ (N1408-00279)
    var nPrprtFindRow = wconv.findRow(Ds_Prprt, "sel1", "1");
    var nPiboFindRow = wconv.findRow(Ds_PiboList, "sel", "1");
    if (nPiboFindRow != -1) {
        nFindRow = nPiboFindRow;
        nSeq1 = Ds_PiboList.getCellData(nFindRow, "nInrpsSeqno"); //피보험순번
    }
    if (nPrprtFindRow != -1) {
        nFindRow = nPrprtFindRow;
        nSeq1 = Ds_Prprt.getCellData(nFindRow, "nInsrdObjctSeqno"); //피보험순번
    }

	var arg;

    if (nFindRow != -1) {
    	//팝업창으로 보낼 자료
		var indata  = {"sNo"  : Ds_CntrDtl.getCellData(0, "sCrNo")
		                  , "sGb"  : "2"
		                  , "nSeq" : nSeq1};
		 var pObj = {"Ds_Prprt" : Ds_Prprt,
	   			          "Ds_PiboList" : Ds_PiboList
	    			      //"Ds_Trty" : Ds_Trty
	    			};
		//팜업창 위치
		var popInfo = {
			"width"     : 800,                  //폭    : 생략시 기본값
			"height"    : 405,                  //높이   : 생략시 기본값  
