scwin.btn_AnntPymAmt_OnClick = function(obj) { //완료	
    //[장기고도화-연금 ]
    if (Ds_CntrDtl.getCellData(0, "sCrStatCd") != "00" && Ds_CntrDtl.getCellData(0, "sCrStatCd") != "10") {
        wprom.alert("계약상태가 정상 또는 실효상태에서만 연금지급금액을 조회할 수 있습니다.");
        return false;
        //break;
    }
    if (wconv.toNumber(Ds_CntrDtl.getCellData(0, "nBussPrem")) <= 0) {
        wprom.alert("영업보험료가 0원 입니다.\n연금지급금액을 조회를 할수 없습니다.");
        return false;
        //break;
    }
    if (wconv.toNumber(Ds_CntrDtl.getCellData(0, "nApplPrem")) <= 0) {
        wprom.alert("적용보험료가 0원 입니다.\n연금지급금액을 조회를 할수 없습니다.");
        return false;
        //break;
    }
    var sCrNo = Ds_CntrDtl.getCellData(0,"sCrNo");
    //팝업창으로 보낼 자료
	var indata  = {"sCrNo" : sCrNo, "sPlanNo" : Ds_CntrDtl.getCellData(0, "sPlanNo")};
	//팜업창 위치
	var popInfo = {
		"width"     : 760,                  //폭    : 생략시 기본값
		"height"    : 574,                  //높이   : 생략시 기본값  
		//"top"       : 20,                 //top   : 생략시 중심
