//---------------------------------------------------------------------------
// function 명 : lfn_selectCntrRetrList
// 내용        : 휴면계약조회를 위한 계약관련자 조회
//---------------------------------------------------------------------------
scwin.lfn_selectCntrRetrList = function(sCrNo) { //완료

	var pro = Promise.resolve();

	wconv.clear(DsWs_LTIEA03);
	wconv.clear(DsWs_Warning);

	var transObj = new callService("com.inswave.longterm.psv.ens.task.EAD311Task", "selectCntrRetrList", "", "");
	transObj.setUrl("/contract/CBSC028MController.do");
	transObj.setId("selectCntrRetrList");
	transObj.setParam("sCrNo", sCrNo);
	transObj.setXPath("//*/LTIEA03");
	transObj.addXPath("//*/WARNING");  
	//transObj.callBack("scwin.subCallBackfunction");
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);
	DsWs_LTIEA03.setJSON(_tranSync.getDsJson("LTIEA03"));
	if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
		pro = pro.then(function(rv) {
        	return wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        });
    } else {
    	var sCrtorCds;
        for (var i = 0; i < DsWs_LTIEA03.getRowCount(); i++) {
            if (i == 0) {
                sCrtorCds = wconv.trim(DsWs_LTIEA03.getCellData(i, "sCrtorCd"));
            } else {
                sCrtorCds = sCrtorCds + "," + wconv.trim(DsWs_LTIEA03.getCellData(i, "sCrtorCd"));
            }
        }
        if (sCrtorCds != "") {
            var rtnValue = gfn_checkDmcyInsurByCustNo("DsWs_Dmcy", wconv.trim(sCrtorCds));
            if (rtnValue != "") {
            	pro = pro.then(function(rv) {
					return wprom.alert(rtnValue);
				});
            }
        }
    }

    return pro;
};

//---------------------------------------------------------------------------
// function 명 : lfn_SearchCount
// 내용        : 휴면계약조회를 위한 계약관련자 조회
//---------------------------------------------------------------------------
scwin.lfn_SearchCount = function() { //완료

	sysDate = gfn_GetSysTime();
    sDate = wconv.substr(sysDate, 0, 8); // YYYY MM DD 받아옴

	var transObj = new callService("com.inswave.longterm.cla.adt.task.CAC004335sTask", "selectAcdProcessList", "", "");
	transObj.setUrl("/contract/CBSC028MController.do");
	transObj.setId("selectAcdProcessList");
	transObj.setParam("searchCode", "05");
	transObj.setParam("searchValue", wconv.replace(Edt_sCrNo.getValue(), "-", "")); //trim(Edt_sCrNo.Text));
	transObj.setParam("sFmdt", "20100101");
	transObj.setParam("sTodt", sDate);
	transObj.setXPath("//hashtable/data[@hashkey='genda01DocCnt']");
	transObj.addXPath("//*/WARNING");
	//transObj.callBack("scwin.subCallBackfunction");
	//transObj.setXPathDs();
	transObj.sync();
	transObj.send();
	//util.interfaceKey(_tranSync.result.serviceDs);

	wconv.clear(Ds_Count);
	Ds_Count.setJSON(_tranSync.getDsJson("data[@hashkey='genda01DocCnt']"));
	Edit41.setValue(wconv.toNumber(Ds_Count.getCellData(0, "value")));
};

