scwin.ExprDis = function(gridId, colId, rowIndex) { //완료
	//console.log("EXPR : " + gridId + "_" + colId + "_" + rowIndex);
	var rtnValue = "";
	try {
		//console.log("EXPR : " + gridId + "_" + colId + "_" + rowIndex);
		var grdOb = eval(gridId);
		var dsObj = eval(eval(gridId).getDataList());
		switch (gridId) {
			case "Div2_grd_Prprt" :
				if (colId == "nInsArea") {
					rtnValue = dsObj.getCellData(rowIndex, colId) + " (㎡)";
				} else if (colId == "sObjctGrodUndgFlagCd") {
					//Iif(sObjctGrodUndgFlagCd=1, "지상", "지하")+"  "+nObjctRelvFlct+"층"
					rtnValue = ((dsObj.getCellData(rowIndex, colId) == 1) ? "지상" : "지하");
					rtnValue += "  ";
					rtnValue += dsObj.getCellData(rowIndex, "nObjctRelvFlct") + "층";
				}
				break;
			case "Div2_grd_Pibo" :
				if (colId == "sPftbpName") {
					//iif(sAuthByflag == "1" ,sPftbpName,gfn_changeMask(sPftbpName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sPftbpName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sPftbpName"),4);
				} else if (colId == "sXcptPftbpName") {
					//iif(sAuthByflag == "1" ,sXcptPftbpName,gfn_changeMask(sXcptPftbpName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sXcptPftbpName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sXcptPftbpName"),4);
				} else if (colId == "sInrpsName") {
					//iif(sAuthByflag == "1" ,sInrpsName,gfn_changeMask(sInrpsName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sInrpsName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sInrpsName"),4);
				}
				break;
			case "Div1_grd_Pibo" :
				if (colId == "sInrpsName") {
					//iif(sAuthByflag == "1" ,sInrpsName,gfn_changeMask(sInrpsName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sInrpsName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sInrpsName"),4);
				} else if (colId == "sPftbpName") {
					//iif(sAuthByflag == "1" ,sPftbpName,gfn_changeMask(sPftbpName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sPftbpName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sPftbpName"),4);
				} else if (colId == "sXcptPftbpName") {
					//iif(sAuthByflag == "1" ,sXcptPftbpName,gfn_changeMask(sXcptPftbpName, 4))
					rtnValue = (sAuthByflag == "1") ? dsObj.getCellData(rowIndex, "sXcptPftbpName") : gfn_changeMask(dsObj.getCellData(rowIndex, "sXcptPftbpName"),4);
				}
				break;
		}
	} catch(e){
		rtnValue = "error";
		console.log("EXPR ERROR : lfn_exprDisplay " + gridId + "_" + colId + "_" + rowIndex);
	}
	return rtnValue;
};





