scwin.init = function() {

wprom.setPopupInit(718, 700, "계약찾기", null, 20);

var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();

pro = pro.then(function(rv) {
	return util.winTopShowModalPromise();
});

pro = pro.then(function(rv) {

	//Mask Edit 정규식 입력
	//"A-####-#######", "######-#ZZZZZZ", "######-#ZZZZZZ" //1:계약번호, 3:계약자코드, 5:취급자코드
	var maskRegExp = [/^[A-Z\s]{1}[0-9\s]{11}$/, /^[0-9\s]{7}[A-Z0-9\s]{6}$/, /^[0-9\s]{7}[A-Z0-9\s]{6}$/];
	var maxLength  = [12, 13, 13]; 
	var chkIndex   = 0;
	Msk_SchVal_Event = new wMaskEditObj(Msk_SchVal, maskRegExp, maxLength, chkIndex);

    /** 링크 콤보 데이터를 가저온다. 
     반드시 링크 데이터셋 ID = Ds_LinkList, 링크콤보 ID = cmb_linkMenu !!!!!
     링크 버튼 ID = btn_linkMenu !!!!!*/
    //gfn_GetLinkList(obj.ID);
    //버튼 활성,비활성화
    //gfn_ButtonEnable(true, true);
    //wconv.clear(DsWs_ComCd);
    scwin.lfn_selectAllGdList();

    //콤보에 값 세팅
    scwin.lfn_mkCommonCmb();
    //lfn_btnCntl(false);

    /*
    추후 사용시 적용 예정
    if (isPopup == "1") {
        btn_Confirmation.Visible = true;
        Msk_SchVal.setValue(sCrNo);
    }
    */

    scwin.lfn_SchChange();

});

pro = pro.then(function(rv) {
	util.winTopHideModal();
});

return pro;

};

