scwin.Edt_SchVal_OnKeyDown_Async = function(e) {
	if (e.keyCode == 13) {
		util.eventAsyncCall(this, scwin.Edt_SchVal_OnKeyDown, arguments);
	}
}

scwin.Edt_SchVal_OnKeyDown = function(e) {
    if (e.keyCode == 13) {
        scwin.lfn_Btn_Search_OnClick();
    }
};

