// function 명 : lfn_selectAllGdList
// 내용        : 전체상품리스트를 조회
//---------------------------------------------------------------------------
scwin.lfn_selectAllGdList = function() {

    DsWs_AllGd.removeAll();
    DsWs_CntrList.removeAll();
    DsWs_Cnt.removeAll();
    DsWs_DataWarning.removeAll();

    var transCall = new callService("com.inswave.longterm.psv.ens.task.EAD010Task", "selectAllGd", "", "");
	transCall.setUrl("/contract/CBSC031PController.do");
	transCall.setId("interfaceSample");
	transCall.setXPath("//*/hashtable/data/vector/data/LTIEA00");
	transCall.addXPath("//*/hashtable/data/LTIEA00Count");
	transCall.addXPath("//*/vector/data/LTIEA00GD");
	transCall.addXPath("//*/WARNING");  
	//transCall.callBack("scwin.subCallBackfunction");
	transCall.sync();
	transCall.send();	
	//util.interfaceKey(_tranSync.result.serviceDs);

	DsWs_CntrList.setJSON(_tranSync.getDsJson("LTIEA00"));
	DsWs_Cnt.setJSON(_tranSync.getDsJson("LTIEA00Count"));
	DsWs_AllGd.setJSON(_tranSync.getDsJson("LTIEA00GD"));
	DsWs_DataWarning.setJSON(_tranSync.getDsJson("WARNING"));

    // 오류처리
    if (DsWs_DataWarning.getRowCount() != 0) {
        wprom.alert(DsWs_DataWarning.getCellData(0, "msg"));
        return;
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_mkCommonCmb
// 내용        : 조회항목의 콤보값들을 공통코드에서 조회하여 콤보를 만듦
//---------------------------------------------------------------------------
scwin.lfn_mkCommonCmb = function() {

    Cmb_SearchCd.setValue("3");

    Cmb_GdCd.setNodeSet("", "", "");
    util.appendRow(ds_Cmb_GdCd);
	ds_Cmb_GdCd.setCellData(ds_Cmb_GdCd.getRowCount() - 1, "Code", "00");
	ds_Cmb_GdCd.setCellData(ds_Cmb_GdCd.getRowCount() - 1, "Data", "                      -전체-");
    for (var i = 0; i < DsWs_AllGd.getRowCount(); i++) {
    	util.appendRow(ds_Cmb_GdCd);
    	ds_Cmb_GdCd.setCellData(ds_Cmb_GdCd.getRowCount() - 1, "Code", DsWs_AllGd.getCellData(i, "code"));
    	ds_Cmb_GdCd.setCellData(ds_Cmb_GdCd.getRowCount() - 1, "Data", DsWs_AllGd.getCellData(i, "name"));
    }
    Cmb_GdCd.setNodeSet("data:ds_Cmb_GdCd", "Data", "Code");
    Cmb_GdCd.setValue("00");

	gfn_getCommonCodes("DsWs_CrStatCd DsWs_SexCd", "CrStatCd SexFlag", "", "");
	util.appendRow(ds_Cmb_CrStatCd);
	ds_Cmb_CrStatCd.setCellData(ds_Cmb_CrStatCd.getRowCount() - 1, "Code", "");
	ds_Cmb_CrStatCd.setCellData(ds_Cmb_CrStatCd.getRowCount() - 1, "Data", " -전체- ");
    for (var i = 0; i < DsWs_CrStatCd.getRowCount(); i++) {
    	util.appendRow(ds_Cmb_CrStatCd);
    	ds_Cmb_CrStatCd.setCellData(ds_Cmb_CrStatCd.getRowCount() - 1, "Code", DsWs_CrStatCd.getCellData(i, "sCd"));
    	ds_Cmb_CrStatCd.setCellData(ds_Cmb_CrStatCd.getRowCount() - 1, "Data", DsWs_CrStatCd.getCellData(i, "sHnglCdName"));
    }
	Cmb_CrStatCd.setSelectedIndex(0);


    for (var i = 0; i < DsWs_SexCd.getRowCount(); i++) {
    	util.appendRow(ds_Cmb_SexCd);
    	ds_Cmb_SexCd.setCellData(ds_Cmb_SexCd.getRowCount() - 1, "Code", DsWs_SexCd.getCellData(i, "sCd"));
    	ds_Cmb_SexCd.setCellData(ds_Cmb_SexCd.getRowCount() - 1, "Data", DsWs_SexCd.getCellData(i, "sHnglCdName"));
    }
	Cmb_SexCd.setSelectedIndex(0);

    Edt_StrtAge.setValue("0");
    Edt_EndAge.setValue("99");
    Msk_SchVal.show();
    Edt_SchVal.hide();
    //Msk_SchVal.setDisplayFormat("######-#ZZZZZZ");
    Msk_SchVal.setDisplayFormat("######-#######");
    Msk_SchVal.focus();
    Edt_StrtAge.setDisabled(true);
    Edt_EndAge.setDisabled(true);
    Cmb_SexCd.setDisabled(true);
    Msk_SchVal.focus();

};

