};

//---------------------------------------------------------------------------
// function 명 : lfn_selectCntrList
// 내용        : 계약리스트를 조회
//---------------------------------------------------------------------------
scwin.lfn_selectCntrList = function(pageNo) {
    var sSearchCd = wconv.trim(Cmb_SearchCd.getValue());
    //trace("sSearchCd="+sSearchCd);
    var sSearchVal = "";
    var nInrpsAgeSt = "";
    var nInrpsSsexCd = "";
    var sGdCd = "";
    if (sSearchCd == "1" || sSearchCd == "3" || sSearchCd == "5") {
        sSearchVal = wconv.trim(Msk_SchVal.getValue());
    } else {
        sSearchVal = wconv.trim(Edt_SchVal.getValue());
    }

    //trace("sSearchVal="+sSearchVal);
    if (sSearchVal.length == 0) {
        wprom.alert("조회구분값을 입력해주세요");
        if (sSearchCd == "1" || sSearchCd == "3" || sSearchCd == "5") {
            Msk_SchVal.focus();
        } else {
            Edt_SchVal.focus();
        }
        return;
    }
    if (sSearchCd == "1" || sSearchCd == "3" || sSearchCd == "5") {
        if (
             (sSearchCd == "1" && sSearchVal.length != 12) ||
             (sSearchCd == "3" && sSearchVal.length < 10)  ||
             (sSearchCd == "5" && sSearchVal.length != 13)
            ) {
            wprom.alert("조회구분값이 정확하지 않습니다");
            Msk_SchVal.focus();
            return;
        }
    }
    nInrpsAgeSt = wconv.trim(Edt_StrtAge.getValue());
    if (nInrpsAgeSt.length == 0) {
        nInrpsAgeSt = "0";
    }
    nInrpsAgeEnd = wconv.trim(Edt_EndAge.getValue());
    if (nInrpsAgeEnd.length == 0) {
        nInrpsAgeSt = "99";
    }
    sInrpsSexCd = wconv.trim(Cmb_SexCd.getValue());
    sCrStatCd = wconv.trim(Cmb_CrStatCd.getValue());
    sGdCd = wconv.trim(Cmb_GdCd.getValue());
    //trace("sGdCd"+sGdCd);
    if (sGdCd.length == 0) {
        sGdCd = "00";
    }

    //계약번호 권한체크
    if (sSearchCd == "1") {
        if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", Msk_SchVal.getValue()) == false) {
            wprom.alert("해당 계약의 조회 권한이 없습니다.");
            Msk_SchVal.focus();
            return;
        }
    }

    //취급자 권한체크
    if (sSearchCd == "6" || sSearchCd == "7") {
        if (gfn_chekDoerInqrAuth("DsWs_chekCrInqrAuth", Edt_SchVal.getValue()) == false) {
            wprom.alert("조회 권한이 없습니다.");
            Edt_SchVal.focus();
            return;
        }
    }

    var transCall = new callService("com.inswave.longterm.psv.ens.task.EAD010Task", "selectCntrList",  pageSize, pageNo);
	transCall.setUrl("/contract/CBSC031PController.do");
	transCall.setId("interfaceSample");
	transCall.setParam("sSearchCd"        , sSearchCd);      //
	transCall.setParam("sSearchVal"       , sSearchVal);      //
	transCall.setParam("nInrpsAgeSt"      , nInrpsAgeSt);      //
	transCall.setParam("nInrpsAgeEnd"     , nInrpsAgeEnd);      //
	transCall.setParam("sInrpsSexCd"      , sInrpsSexCd);      //
	transCall.setParam("sCrStatCd"        , sCrStatCd);      //
	transCall.setParam("sGdCd"            , sGdCd);      //
	transCall.setParam("sUserID"          , util.getGdsValue("gUserID"));      //
	transCall.setXPath("//*/hashtable/data/vector/data/LTIEA00");
	transCall.addXPath("//*/hashtable/data/LTIEA00Count");
	transCall.addXPath("//*/vector/data/LTIEA00GD");
	transCall.addXPath("//*/WARNING");  
	//transCall.callBack("scwin.subCallBackfunction");
	transCall.sync();
	transCall.send();	
	//util.interfaceKey(_tranSync.result.serviceDs);

	DsWs_CntrList.setJSON(_tranSync.getDsJson("LTIEA00"));
	DsWs_Cnt.setJSON(_tranSync.getDsJson("LTIEA00Count"));
	DsWs_AllGd.setJSON(_tranSync.getDsJson("LTIEA00GD"));
	DsWs_DataWarning.setJSON(_tranSync.getDsJson("WARNING"));

    // 오류처리
    if (DsWs_DataWarning.getRowCount() != 0) {
        wprom.alert(DsWs_DataWarning.getCellData(0, "msg"));
        return;
    }

    Ds_CntrList.setJSON(DsWs_CntrList.getAllJSON());
};

