//**************************************************************************************************
// 파라미터(계약번호) 리턴
//**************************************************************************************************
scwin.Grd_List_OnCellClick = function(nRow, nCol) {
    var ind = nRow;
    var sPsnPrprtGroupFlgcd = "";

    if (ind < 0) return;

    if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", Ds_CntrList.getCellData(nRow, "sCrNo")) == false) {
        wprom.alert("해당 계약의 처리 권한이 없습니다.");
        return;
    }

    // 계약정보 넘겨줌(그리드의 컬럼순).
    var returnMsg = Ds_CntrList.getCellData(nRow, "sCrNo") + "^" + //계약번호
        Ds_CntrList.getCellData(nRow, "sGdNm") + "^" + //상품명
        Ds_CntrList.getCellData(nRow, "sCrtorName") + "^" + //계약자명
        Ds_CntrList.getCellData(nRow, "sInrpsName") + "^" + //피보험자명
        Ds_CntrList.getCellData(nRow, "sCrStatNm") + "^" + //계약상태
        Ds_CntrList.getCellData(nRow, "sBunsMetdClsfCd") + "^" + //인건/물건 구분 
        Ds_CntrList.getCellData(nRow, "sBunsMetdDocuClsfCd") + "^" + //보종구분
        Ds_CntrList.getCellData(nRow, "sPsnGroupFlgcd"); //개인,단체구분
    //wconv.trace("D011_Pop.Grd_List_OnCellClick.returnMsg=" + returnMsg);
    wprom.close(returnMsg);
};

