scwin.init = function() {
	var pro = Promise.resolve();
		window.top.util.winTopShowModalChk = "T";
		util.winTopShowModal();
	pro = pro.then(function(rv) {
    	return util.winTopShowModalPromise();
	}); 
	pro = pro.then(function(rv) {
     //init 부분 함수 입력하는곳

	//Mask Edit 정규식 입력
	//"######-#ZZZZZZ" , "A-####-#######", @@@@@@@
	var maskRegExp = [ /^[A-Z\s]{1}[0-9\s]{11}$/, /^[0-9\s]{7}$/, /^[^ㄱ-ㅎ^ㅏ-ㅣ^가-힣]{7}$/ ];
	var maxLength  = [12, 7, 7];
	var chkIndex   = 0;
	medt_Str_Event = new wMaskEditObj(medt_Str, maskRegExp, maxLength, chkIndex);

    sToday = wconv.substr(gfn_GetSysTime(), 0, 8);
   	//gfn_GetLinkList("CBSC032M");
    gfn_btnEnable(false, false, false, false, false, false, false, true);

    cal_From.setValue(wconv.addDateSimple(sToday, -7));
    cal_To.setValue(sToday); //Mid(sToday,0,8);
    Ds_UserInfo.removeAll();
    util.appendRow(Ds_UserInfo);

    Ds_UserInfo.setCellData(0, "sUserId", util.getGdsValue("gUserID")); //로긴ID
    Ds_UserInfo.setCellData(0, "sUserIP", util.getGdsValue("gUserIP")); //로긴IP
    //공통코드호출(CrStatCd)
    gfn_getCommonCodes("DsWs_VerfyMetdCd DsWs_grdVerfyMetdCd DsWs_RlnmCnfmRgcrCd", "VerfyMetdCd VerfyMetdCd RlnmCnfmRgcr", "cmb_VerfyMetdCd ", "A N N");
    gfn_getCommonCodes("DsWs_VerfyMetdCd2 DsWs_grdVerfyMetdCd DsWs_RlnmCnfmRgcrCd", "VerfyMetdCd VerfyMetdCd RlnmCnfmRgcr", "cmb_VerfyMetdCd2 ", "A N N");

    DsWs_VerfyMetdCd.setCellData(0, "sCd", "%");
    DsWs_VerfyMetdCd2.setCellData(0, "sCd", "%");
//   	cmb_VerfyMetdCd2.setSelectedIndex(0);
    return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); 
	});
	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});	
	pro = pro.catch(function(rj) {
		util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	});	 
	return pro; 

};
