scwin.lfn_Search = function(pageNo) {
	scwin.lfn_SearchData(pageNo);	
    if (DsWs_ListCnt.getRowCount() > 0) {
        gfn_MakePagingData(Ds_Page, DsWs_ListCnt.getCellData(0, "nCount"), pageSize, pageCount);
        Cmb_Page.setValue("1");
    }
};

