scwin.lfn_SearchDoer_OnClick = function(obj) {
    var promise = Promise.resolve();
    //데이터셋 초기화
    wconv.clear(DsWs_List);
    wconv.clear(DsWs_ListCnt);
    wconv.clear(DsWs_ListExl);
    //조직코드
    if (cmb_Gubun.getValue() == "1") {
        promise = promise.then(function(rtnVal) {			
			var popInfo = {
				"width"     : 754,
				"height"    : 501,  		
				"title"     : "조직찾기",
				"callback"  : ""
			};			
			return wprom.popupOpen("/ui/cbso/CBSO020P.xml", popInfo);
	        //조직팝업
           //return wprom.promise.popupOpen("PROJECT_PATH_TEMP/com_pop/CBSO020P.xml", "", 594, 321);
        });
        promise = promise.then(function(rst) {
            /*wprom.popupOpen(가이드참조)*/
            if (wconv.length(rst) != 0) {
                var arrRst = wconv.split(rst, "^");
                medt_Str.setValue(arrRst[0]);
                edt_StrNm.setValue(arrRst[1]);
            }
        });
    } else {
        promise = promise.then(function(rtnVal) {
        	var popInfo = {
				"width"     : 674,
				"height"    : 451,  		
				"title"     : "사원찾기",
				"callback"  : ""
			};			
			return wprom.popupOpen("/ui/cbso/CBSO015P.xml", popInfo);
            //사용자팝업
            //조직코드 이외(취급자, 사용인)
            //return wprom.promise.popupOpen("PROJECT_PATH_TEMP/popup/CBSO015P.xml", "", 640, 336);
        });
        promise = promise.then(function(rst) {
            /*wprom.popupOpen(가이드참조)*/
            if (wconv.length(rst) != 0) {
                var arrRst = wconv.split(rst, "^");
                medt_Str.setValue(arrRst[0]);
                edt_StrNm.setValue(arrRst[1]);
            }
        });
    }
    return promise;
};

//---------------------------------------------------------------------------
// function 명 : lfn_Input
// 내용        : 입력버튼 Event처리
//---------------------------------------------------------------------------
scwin.cm_btnInput = function() {
//    var row = "0";
  	Ds_ListIns.removeAll();
    Ds_ListUp.removeAll();
    var sVerfyMetdCd = "";
    var sVerfyResltCpltCd = "";
    var sPlanNo = "";
    for (var i = 0; i < DsWs_List.getRowCount(); i++) {
        if (DsWs_List.getRowStatus(i) == "U" ) {
            sVerfyMetdCd = DsWs_List.getCellData(i, "sVerfyMetdCd");
            sVerfyResltCpltCd = DsWs_List.getCellData(i, "sVerfyResltCpltCd");
            sPlanNo = DsWs_List.getCellData(i, "sPlanNo");
            //검증방법코드
            if (sVerfyMetdCd == "" && sVerfyResltCpltCd == "1") {
                wprom.alert("검증방법값을 입력하세요.(" + sPlanNo + ")");
                return false;
            }
            //검증결과확인여부
            if (sVerfyResltCpltCd == "") {
                wprom.alert("검증결과값을 입력하세요.(" + sPlanNo + ")");
                return false;
            }            
            if (DsWs_List.getOriginalCellData(i, "sVerfyDler") == "") {
           		//신규           		               		
                util.appendRow(Ds_ListIns);
                wconv.mi_copyRow(DsWs_List.getID(), i, Ds_ListIns, Ds_ListIns.getRowCount() -1 );                
            } else {
                //수정                                 
                util.appendRow(Ds_ListUp);
                wconv.mi_copyRow(DsWs_List.getID(), i, Ds_ListUp, Ds_ListUp.getRowCount() -1 );                
            }
        }
    }
    if (Ds_ListIns.getRowCount() == 0 && Ds_ListUp.getRowCount() == 0) {
        wprom.alert("변경된 데이타가 없습니다.");
        return false;
    }
	var j = 0;   
//    SOBJ_List.WSDL = gds.gWsdlBasis;
//    var MethodObj = SOBJ_List.CreateMethodObj("perform"); 
//    setHeader("hashtable", "com.inswave.longterm.psv.ens.task.EAD710Task", "insertRlnmInfoList");   //insert XML문 Header 문구 작성
//    addVectorData("LTIEA52I", "LTIEA52I", Ds_ListIns); //벡터 형식 데이타 구조 생성
//    addVectorData("LTIEA52U", "LTIEA52U", Ds_ListUp);
//    addSingleData("dUserInfo", "UserInfo", Ds_UserInfo, "UserInfo"); // 사용자정보
//    setTail("hashtable");
//    SOBJ_List.CreateObj("in0");
//    MethodObj.in0 = insertStr; //XML 구조를 담은 변수
//    gfn_CallService(SOBJ_List, DOBJ_List, MethodObj);

	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD710Task", "insertRlnmInfoList");
 	MethodObj.setId("insertRlnmInfoList"); 
 	MethodObj.addVectorData("LTIEA52I", "LTIEA52I", Ds_ListIns.getID());
 	MethodObj.addVectorData("LTIEA52U", "LTIEA52U", Ds_ListUp.getID());
 	MethodObj.addSingleData("dUserInfo", "UserInfo", Ds_UserInfo.getID()); // 사용자정보
 	MethodObj.setUrl("/contract/CBSC032MController.do");    
 	MethodObj.setXPath("//*/WARNING");
 	MethodObj.setHashTable();
 	MethodObj.sync();
 	MethodObj.send();
 	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
    if (DsWs_Warning.getRowCount() != 0) {
        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        return;
    }
    wprom.alert("저장 되었습니다.");
    scwin.lfn_SearchData(Cmb_Page.getValue());
};
