scwin.grd_List_OnCellClick = function(row, col) {
//debugger;
    switch (col) {
        case 9:        

            //검증방법1
            //검증결과 완료일때만 선택가능
            if (DsWs_List.getCellData(row, "sVerfyResltCpltCd") == "1") {
//                grd_List.SetCellProp("body", col, "Edit", "combo");
//                	grd_List.setReadOnly("cell", row , "sVerfyMetdCd", false);				
            } else {
//                grd_List.SetCellProp("body", col, "Edit", "none");
//                grd_List.setColumnVisible("sVerfyMetdCd" , true );
					grd_List.setReadOnly("cell", row , "sVerfyMetdCd", true);
            }
            break;
        case 10:

            //검증방법2
            //검증방법 선택했을경우만 검증방법2선택가능
            if (DsWs_List.getCellData(row, "sVerfyMetdCd") != null && DsWs_List.getCellData(row, "sVerfyMetdCd") != "") {
//                grd_List.SetCellProp("body", col, "Edit", "combo");
//				grd_List.setReadOnly("cell", row , "sVerfyMetdCd2", false);
            } else {
//                grd_List.SetCellProp("body", col, "Edit", "none");
				grd_List.setReadOnly("cell", row , "sVerfyMetdCd2", true);
            }
            break;            
        case 4:
            var sCrNo = DsWs_List.getCellData(row, "sCrNo");
            if (sCrNo == "") {
                return false;
            }
            //대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
            //설계사(48:DP)
            if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || util.getGdsValue("gUserDuty") == "48" || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65") {
                if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "2", sCrNo) == false) {
                    wprom.alert("조회권한이 없습니다.");
                    return false;
                }
            }
            if (parent.Id == "global") {
                // html에 쌓인 상태
                wconv.UserNotify(1, sCrNo); //D020.html
            } else {
                var arg = "sCrNo=" + wconv.quote(sCrNo);   
//                gds.gDs_LinkArg.removeAll();
//                var insRow = util.InsertRow(0);
                //수작업 변환 개발자 가이드(테스트 중) dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.
//		          util.insertColumn(gds.gDs_LinkArg, [[ "sCrNo", "sCrNo", "text" ]]);
//	              util.insertColumn(gds.gDs_LinkArg, [[ "sScrnId", "sScrnId", "text" ]]);
//                gds.gDs_LinkArg.setCellData(0, "sCrNo", sCrNo);
//                gds.gDs_LinkArg.setCellData(0, "sScrnId", "D010");
   				var oDsJson=[{"PrigId":"CBSC032M", 
   							  "sCrNo": sCrNo ,
   							  "sScrnId":  "CBSC029M"	}];
				gds.gDs_LinkArg.setJSON(oDsJson);
                gfn_OpenLinkPage("CBSC028M");
                }            
            break;
        case 3:
            var sVvalue = DsWs_List.getCellData(row, "sPlanNo");
            //청약번호
            var sBunsMetdDocuClsfCd = DsWs_List.getCellData(row, "sBunsMetdDocuClsfCd");
            //사업방법서 구분
            var sBunsMetdClsfCd = DsWs_List.getCellData(row, "sBunsMetdClsfCd");
            if (wconv.substr(sVvalue, 0, 1) != "M") {
                return false;
            }
            //대리점일때 권한 체크
            //DP, DPM일 때 권한체크 추가(N1301-00008:장기보험 신계약 대면프로세스 개발 요청)
            if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35" || util.getGdsValue("gUserDuty") == "48" || util.getGdsValue("gUserDuty") == "49" || util.getGdsValue("gUserDuty") == "50" || util.getGdsValue("gUserDuty") == "62" || util.getGdsValue("gUserDuty") == "65" || util.getGdsValue("gUserDuty") == "64" || util.getGdsValue("gUserDuty") == "66") {
                //설계번호 권한 체크
                if (gfn_chekCrInqrAuth("DsWs_chekCrInqrAuth", "1", sVvalue) == false) {
                    wprom.alert("조회 권한이 없습니다.");
                    return;
                }
            }
            //대면/배대면 채널별 처리 구분 (N1301-00008:장기보험 신계약 대면프로세스 개발 요청)
            //심사자 권한은 제외
            if (nfn_checkAuthByID("ds_code", util.getGdsValue("gUserID"), "22040") == false) {
                if (gfn_checkFaceSales(gFaceSalesYn, DsWs_List.getCellData(row, "sScanObjtYn")) == false) {
                    if (gFaceSalesYn == "Y") {
                        wprom.alert("비대면 채널로 설계한 청약입니다. 상담원시스템으로 로그인하세요.");
                    } else {
                        wprom.alert("대면 채널로 설계한 청약입니다. 장기보험시스템으로 로그인하세요.");
                    }
                    return false;
                }
            }
            if (parent.Id == "global") {
                if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {
                    UserNotify(2, sVvalue); //B030.html
                } else if (sBunsMetdDocuClsfCd == "7") {
                    //장기보험고도화-연금청약화면 이동
                    UserNotify(4, sVvalue); //C010.html 청약번호로 연금청약 link
                } else {
                    UserNotify(0, sVvalue); //B010.html
                }
            } else {
                var arg = "sBizCode=" + wconv.quote(sVvalue);

//                gds.gDs_LinkArg.removeAll();
//                var insRow = util.insertRow(gds.gDs_LinkArg, 0);
                /*수작업 변환 개발자 가이드(테스트 중)
                    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
                //gDs_LinkArg.setColumn(0, "sKey", sVvalue);
                //gDs_LinkArg.setColumn(0, "sInsuredCode", InsuCode.Value);
//                gds.gDs_LinkArg.setCellData(0, "sInsuredNo", DsWs_List.getCellData(row, "sPlanNo"));  

      	var popInfo = {};
      		var oDsJson=[{"PrigId":"CBSC032M"
			                		, "sInsuredNo": DsWs_List.getCellData(row, "sPlanNo")		// 청약번호
			                		, "sKey": DsWs_List.getCellData(row, "sPlanNo") 				// 청약번호
			         			 }];  		// 필드 Json에 담기(gDs_LinkArg 함수 사용)		 
	       	gds.gDs_LinkArg.setJSON(oDsJson);

                if (sBunsMetdDocuClsfCd == "3" || sBunsMetdClsfCd == "1") {
                    gfn_OpenLinkPage("CBSC094M");
                } else if (sBunsMetdDocuClsfCd == "7") {
                    //장기보험고도화-연금청약화면 이동
                    gfn_OpenLinkPage("CBSC075M");
                } else {
                    gfn_OpenLinkPage("CBSC015M"); //8500
                }
            }
            break;
        default:
            break;
    }
};

//---------------------------------------------------------------------------
// function 명 : DsWs_List_OnColumnChanged()
// 내용        : DsWs_List 데이타변경시
//---------------------------------------------------------------------------
//scwin.DsWs_List_OnColumnChanged = function(obj, nRow, strColumnID, varOldValue, varNewValue, nPivotIndex) {
scwin.DsWs_List_OnColumnChanged = function(info, colID, rowIndex, newValue, oldValue) {
    var colID = info.colID;
    var rowIndex = info.rowIndex;
    var newValue = info.newValue;
    var oldValue = info.oldValue;
    cal_From.focus();
    grd_List.focus();
    if (colID == "sVerfyMetdCd" || colID == "sVerfyResltCpltCd") {
        DsWs_List.setCellData(rowIndex, "sVerfyDler",  util.getGdsValue("gUserID"));
        DsWs_List.setCellData(rowIndex, "sVerfyDlerNm", util.getGdsValue("gUserEmpName"));
        DsWs_List.setCellData(rowIndex, "sVerfyDate", sToday);
    }
    if (colID == "sVerfyMetdCd") {
        DsWs_List.setCellData(rowIndex, "sVerfyMetdCd2", "");
    }
    //검증방법,검증방법2 같은값 선택불가
    if (colID == "sVerfyMetdCd2") {
        if (newValue != "" && DsWs_List.getCellData(rowIndex, "sVerfyMetdCd") == newValue) {
            wprom.alert("동일한 검증방법을 선택할 수 없습니다.");
            DsWs_List.setCellData(rowIndex, "sVerfyMetdCd2", oldValue);
            return false;
        }
    }
    //검증결과
    if (colID == "sVerfyResltCpltCd") {
        if (newValue == "0" || newValue == "2") {
            //미완료,검증불가
            DsWs_List.setCellData(rowIndex, "sVerfyMetdCd", "");
            DsWs_List.setCellData(rowIndex, "sVerfyMetdCd2", "");

        }
    }
};
