// function 명 : medt_Str_OnChanged
// 내용        : 사원,조직조회
//---------------------------------------------------------------------------
scwin.medt_Str_OnChanged = function(obj, strText) {
    // 조직명/사원명 조회
    // 조직명 조회
    if (cmb_Gubun.getValue() == "1") {
        //조직
        // 조직명 조회			
        gfn_getDepartName(medt_Str.getValue());
        if (gds.gDsWs_getTeam.getRowCount() == 0) {
            medt_Str.setValue("");
            medt_Str.focus();
            return false;
        } else {
            edt_StrNm.setValue(gds.gDsWs_getTeam.getCellData(0, "sDepartName"));
        }
    } else if (cmb_Gubun.getValue() == "2" || cmb_Gubun.getValue() == "3" || cmb_Gubun.getValue() == "6") {
        //취급자,사용인
        //사원명 조회
        gfn_getEmpName(medt_Str.getValue());
        if (gds.gDsWs_SelectEmpName.getRowCount() == 0 || gds.gDsWs_SelectEmpName.getCellData(0, "sEmpName") == null) {
            medt_Str.setValue("");
            medt_Str.focus();
            return false;
        } else {
            edt_StrNm.setValue(gds.gDsWs_SelectEmpName.getCellData(0, "sEmpName"));
        }
    }
};
