//---------------------------------------------------------------------------
// function 명 : lfn_ValidationCheck
// 내용        : 필수 입렵 체크
//---------------------------------------------------------------------------
scwin.lfn_ValidationCheck = function() {
    //조회기준
    switch (cmb_Gubun.getValue()) {
        case "1":
        case "2":
        case "3":
        case "6":
            //검증처리자
            if (wconv.length(medt_Str.getValue()) != 7) {
                wprom.alert("조회기준값을 입력하세요.");
                medt_Str.focus();
                return false;
            }
            break;
        case "4":
        case "5":
            //계약번호
            if (wconv.length(medt_Str.getValue()) != 12) {
                wprom.alert("조회기준값을 입력하세요.");
                medt_Str.focus();
                return false;
            }
            break;
    }
    if (wconv.length(txt_sVerfyDler.getValue()) > 0 && wconv.length(txt_sVerfyDler.getValue()) != 7) {
        wprom.alert("검증자코드를 입력하세요.");
        txt_sVerfyDler.focus();
        return false;
    }
    return true;
};

//---------------------------------------------------------------------------
// function 명 : btn_VerfyDler_OnClick
// 내용        : 사원조회버튼 클릭
//---------------------------------------------------------------------------
scwin.btn_VerfyDler_OnClick = function(obj) {
    var promise = Promise.resolve();
    //데이터셋 초기화
    wconv.clear(DsWs_List);
    wconv.clear(DsWs_ListCnt);
    wconv.clear(DsWs_ListExl);
    promise = promise.then(function(rtnVal) {
        	var popInfo = {
				"width"     : 674,
				"height"    : 451,  		
				"title"     : "사원찾기",
				"callback"  : ""
			};			
			return wprom.popupOpen("/ui/cbso/CBSO015P.xml", popInfo);
        //사용자팝업
     //   return wprom.promise.popupOpen("PROJECT_PATH_TEMP/popup/CBSO015P.xml", "", 640, 336);
    });
    promise = promise.then(function(rst) {
        /*wprom.popupOpen(가이드참조)*/
        if (wconv.length(rst) != 0) {
            var arrRst = wconv.split(rst, "^");
            txt_sVerfyDler.setValue(arrRst[0]);
            edt_VerfyDler.setValue(arrRst[1]);
        }
    });
    return promise;
};
