 var fv_args;

scwin.init = function() {

	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
    	return util.winTopShowModalPromise();
	}); //맨끝에 처리

	pro = pro.then(function(rv) {

	var indata = $w.getParameter("param");//
	try {
		fv_args = JSON.parse(indata);
	} catch(e) {}

    //sToday = wconv.substr(gfn_GetSysTime(), 0, 8);
    //sTime = wconv.substr(gfn_GetSysTime(), 8, 6);
	gfn_getServerTime();
    var sToday = wconv.substr(gDs_ServerTime.getCellData(0,"sToday"), 0, 8);
    var sTime = gDs_ServerTime.getCellData(0,"sHour") + gDs_ServerTime.getCellData(0,"sMinute") + gDs_ServerTime.getCellData(0,"sSecond");

    swDiv.show();
    //grd_b.show();
    //grd_c.show();
    wconv.clear(Ds_List);
    //wconv.clear(DsWs_List);

    //SOBJ_DataSearch.WSDL = gds.gWsdlBasis;
    if (fv_args.sFlag == "b") {
        //grd_c.hide();
        swDiv.setSelectedID( "case4" );
        /*
        var MethodObj = gfn_CreateMethodObj(SOBJ_DataSearch, "com.inswave.longterm.ssr.pln.task.DBB330Task", "getHappyCall", null);
        gfn_SetParam(MethodObj, "sPlanNo", sPlanNo); // 조회구분
        gfn_CallService(SOBJ_DataSearch, DOBJ_DataSearch, MethodObj);
        */
		var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB330Task", "getHappyCall");
		MethodObj.setId("getHappyCall");
		MethodObj.setParam("sPlanNo", fv_args.sPlanNo); // 조회구분
		MethodObj.setUrl("/contract/CBSC051PController.do");
		MethodObj.setXPath("//*/LTIDA00");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();
		//util.interfaceKey(_tranSync.result.serviceDs);
		//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
		util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_List");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
		util.setCompBindRefresh("DsWs_List");
		util.setRowPosition(DsWs_List, 0);

        if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
            util.insertColumn(DsWs_List, [["nTotAmt", "nTotAmt", "text"  ]]);
			DsWs_List.insertRow(0);
        } else {
            if (DsWs_List.getRowCount() == 0) {
                wprom.alert("조회건이 없습니다.");
            } else {
                wconv.copy(Ds_List, "DsWs_List");
                var sInsurStrtdate = DsWs_List.getCellData(0, "sInsurStrtdate");
                var YY = wconv.substr(sInsurStrtdate, 0, 4);
                var MM = wconv.substr(sInsurStrtdate, 4, 2);
                var DD = wconv.substr(sInsurStrtdate, 6, 2);
                edt_rato.setValue("(" + YY + "." + MM + "." + DD + " 현재 " + DsWs_List.getCellData(0, "nRato") + " %)");
                edt_date.setValue("약 " + DsWs_List.getCellData(0, "sCnclRtnrtPassPrd")); //nTotAmt+" 원"+"(기본보장 "+nBaseGrntPrem+" 원 + 적립 "+nBaseAccuPrem+" 원)"
                //grd_b.SetCellProp("Head", 3, "Text", "abs\nabc");
            }
        }       
    } else {
       //grd_b.hide();
        swDiv.setSelectedID( "case3" );    
	        /*
        var MethodObj = gfn_CreateMethodObj(SOBJ_DataSearch, "com.inswave.longterm.ssr.pln.task.DBB330Task", "getHappyCallC", null);
        gfn_SetParam(MethodObj, "sPlanNo", sPlanNo); // 조회구분
        gfn_CallService(SOBJ_DataSearch, DOBJ_DataSearch, MethodObj);
        */
		var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB330Task", "getHappyCallC");
		MethodObj.setId("getHappyCallC");
		MethodObj.setParam("sPlanNo", fv_args.sPlanNo); // 조회구분
		MethodObj.setUrl("/contract/CBSC051PController.do");
		MethodObj.setXPath("//*/LTIDA00");
		MethodObj.addXPath("//WARNING");
		MethodObj.sync();
		MethodObj.send();
		//util.interfaceKey(_tranSync.result.serviceDs);
		//DsWs_Team.setJSON (_tranSync.getDsJson("ADMAC03")); // dataset 데이터 
		util.jSonDsCreate(_tranSync.getDsJson("LTIDA00"), "DsWs_List");
		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
		util.setCompBindRefresh("DsWs_List");
		//util.setRowPosition(DsWs_List, 0);

        if (wconv.length(DsWs_Warning.getCellData(0, "msg")) > 0) {
            wprom.alert(DsWs_Warning.getCellData(0, "msg"));
        } else {
            if (DsWs_List.getRowCount() == 0) {
                wprom.alert("조회건이 없습니다.");
            } else {
                wconv.copy(Ds_List, "DsWs_List");
                var sInsurStrtdate = DsWs_List.getCellData(0, "sInsurStrtdate");
                var YY = wconv.substr(sInsurStrtdate, 0, 4);
                var MM = wconv.substr(sInsurStrtdate, 4, 2);
                var DD = wconv.substr(sInsurStrtdate, 6, 2);
                edt_rato.setValue("(" + YY + "." + MM + "." + DD + " 현재 " + DsWs_List.getCellData(0, "sApplRato") + " %)");
                edt_date.setValue("약 " + DsWs_List.getCellData(0, "sCnclRtnrtPassPrd"));
                var nSumAmt = parseFloat(DsWs_List.getCellData(0, "saleAmt").replace(",","")) 
                                    + parseFloat(DsWs_List.getCellData(0, "mgtAmt").replace(",",""))
                                    + parseFloat(DsWs_List.getCellData(0, "mgtCtAmt").replace(",",""))
                                    + parseFloat(DsWs_List.getCellData(0, "BETA_2").replace(",",""));
//console.log("DsWs_List.getCellData(0, 'saleAmt')      :    "+DsWs_List.getCellData(0, "saleAmt"));
                //util.insertColumn(DsWs_List, [   [        "nSumAmt", "nSumAmt",             "text"       ]   ]);
//alert("nSumAmt : "+nSumAmt);                
                DsWs_List.setCellData(0, "nSumAmt", nSumAmt);
            }
        }
    }
    return;
    //프로미스
    });
    pro = pro.then(function(rv) {
    	util.winTopHideModal();
	});
	return pro;
};

scwin.lfn_Close_OnClick = function(e) {
    wprom.close();
};
scwin.ExprDis = function(gridId, colId, rowIndex) {
	console.log("EXPR : " + gridId + "_" + colId + "_" + rowIndex);
	var rtnValue = "";
	try {
		//console.log("EXPR : " + gridId + "_" + colId + "_" + rowIndex);
		var grdOb = eval(gridId);
		var dataListOb = eval(eval(gridId).getDataList());

		switch (gridId) {
			case "grd_b" :

				if (colId == "nTotAmt") {//"보험료 "+nTotAmt+" 원"+
					rtnValue = "보험료 "+util.nvl(dataListOb.getCellData(rowIndex, colId),"") +" 원";
				}
				if (colId == "nBaseGrntPrem") {
					rtnValue = "(기본보장 "+util.nvl(dataListOb.getCellData(rowIndex, "nBaseGrntPrem"),"0") +" 원 + 적립 " 
					                                +util.nvl(dataListOb.getCellData(rowIndex, "nBaseAccuPrem"),"0") +" 원";
				}
				if (colId == "saleRt") {//saleRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "saleAmt") {//saleAmt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "mgtRt") {//mgtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "mgtAmt") {//mgtAmt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "mgtCtRt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "mgtCtAmt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "BETA_2") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				//nSumRt+" %"
				if (colId == "nSumRt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				//nSumAmt+" 원"+
				if (colId == "nSumAmt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				break;
			case "grd_c":
				if (colId == "nTotAmt") {//"보험료 "+nTotAmt+" 원"+
					rtnValue = "보험료 "+util.nvl(dataListOb.getCellData(rowIndex, colId),"0") +" 원";
				}
				if (colId == "nBaseGrntPrem") {
					rtnValue = "(기본보장 "+util.nvl(dataListOb.getCellData(rowIndex, "nBaseGrntPrem"),"0") +" 원 + 적립 " 
					                                +util.nvl(dataListOb.getCellData(rowIndex, "nBaseAccuPrem"),"0") +" 원";
				}
				if (colId == "saleRt") {//saleRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "saleAmt") {//saleAmt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "mgtRt") {//mgtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "mgtAmt") {//mgtAmt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "mgtCtRt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				if (colId == "mgtCtAmt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				if (colId == "BETA_2") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" 원";
				}
				//nSumRt+" %"
				if (colId == "nSumRt") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}
				//nSumAmt+" 원"+
				if (colId == "nSumAmt") {//mgtCtRt+" %"+
					rtnValue = util.appendComma(util.nvl(dataListOb.getCellData(rowIndex, colId),"0"))+" 원";
				}
				//ANN+" %"+
				if (colId == "ANN") {//mgtCtRt+" %"+
					rtnValue = util.nvl(dataListOb.getCellData(rowIndex, colId),"0")+" %";
				}

				break;
			default:
				break;		
		}
	} catch(e){
		rtnValue = "error";
		console.log("EXPR ERROR : lfn_exprDisplay " + gridId + "_" + colId + "_" + rowIndex);
	}
	return rtnValue;
};

scwin_lfn_Bgcolor_Chg = function(data, formattedData, rowIndex, colIndex) {
	grd_b.setCellBackgroundColor( rowIndex , colIndex , "#E3F0FF" );
	return formattedData;
};
