var sPlanNo = ""; 				// 설계번호 Pop
var sPsnGroupFlgcdPop = "";	//피보험자단체구분코드
var sPsnGroupFlgcd = "";	//피보험자단체구분코드 
var sCrNoPop = "";	//계약번호(변경계약처리시 사용)  
var sCrNo = "";	//계약번호(변경계약처리시 사용) 
var parent_Ds_LTIDA00 = "";;	// 부모창 보험 기본사항 정보 Ds Set
var parent_Ds_CntrDtl = "";;		// 부모창 보험 기본사항 정보 Ds Set
var parent_Ds_PiboList = "";		// 부모창 피보험자 정보 Ds Set 
var parent_Ds_Prprt = "";			// 부모창 목적물 Ds Set
var parent_Ds_BosangTrty = "";;		// 부모창 특약코드 정보 Ds Set
var parent_medt_PlanNo = "";	// 부모창 플랜코드(설계번호) Set
var parent_sPlanName = "";	// 부모창 플랜명 Set
var parent_sSsrtDate = "";	// 부모창 청약일 Set
var parent_cmb_Plan = "";	// 부모창 플랜코드 Set

//---------------------------------------------------------------------------
// function 명 : B200_OnLoadCompleted
// 내용        : 화면로딩시 메인으로부터 파라메터값 담음
//---------------------------------------------------------------------------
scwin.init = function() {
	console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_01]" );

	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		var pObj = wprom.getParamObject(); 
		parent_Ds_LTIDA00 = pObj.Ds_LTIDA00; // 부모창 보험 기본사항 정보 Ds Set
		parent_Ds_CntrDtl = pObj.Ds_CntrDtl; // 부모창 보험 기본사항 정보 Ds Set
		parent_Ds_PiboList = pObj.Ds_PiboList;	// 부모창 피보험자 정보 Ds Set   
		parent_Ds_Prprt = pObj.Ds_Prprt;	// 부모창 목적물 정보 Ds Set  
		parent_Ds_BosangTrty = pObj.Ds_BosangTrty;	// 부모창 특약 정보 Ds Set 
		parent_medt_PlanNo = pObj.sPlanCd;	// 부모창 플랜코드(설계번호) Ds Set    
		parent_sPlanName = pObj.sPlanName;	// 부모창 플랜명 Ds Set
		parent_sSsrtDate = pObj.sSsrtDate;	// 부모창 청약일 Ds Set
		parent_cmb_Plan = pObj.cmb_Plan;	// 부모창 플랜코드 Ds Set

		indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sMyPlan) 
		if (!util.isNull(inJSON.sCrNo)) sCrNoPop = inJSON.sCrNo;	 // 계약번호(변경계약처리시 사용)  
		if (!util.isNull(inJSON.sPsnGroupFlgcd)) sPsnGroupFlgcdPop = inJSON.sPsnGroupFlgcd;	 // 피보험자단체구분코드  

		switch_div.show();   // switch_div 보이기
		switch_div.setSelectedID("sw_div2");	 // switch_div sw_div2(개인 정보) 보이기 

		scwin.lfn_getCommonCodes();	  // 공통코드 조회 ;

	 	if (parent_Ds_Prprt.getRowCount() > 0) { 	// 부모창 목적물 정보가 존재하면
	        wconv.clear(Ds_Prprt);
	        wconv.copy(Ds_Prprt, parent_Ds_Prprt);		// 부모창 목적물 Ds 복사
	    }

	    if (sPsnGroupFlgcd.length > 0)	
	        sPsnGroupFlgcdPop = sPsnGroupFlgcd; 
	    if (sCrNoPop.length > 0)
	        sCrNo = sCrNoPop;

	    var idx = "";
	    var nGroupSeqno = "";
	    var nInrpsSeqno = "";
	    var nPsnPrprtGroupSeqno = "";
	    var sPsnPrprtGroupFlgcd = "";
	    var sSsrtDate = "";
	    var sGdCd = "";
	    var sApplPlanCd = "";
	    var sApplPlanName = "";

	    //설계화면에서 호출시
	    if (wconv.length(sCrNoPop) == 0) {
	        sSsrtDate = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// 청약일(global 현재일자)
	    } else { 
	   		//console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [parent_sSsrtDate_1]"+ parent_sSsrtDate );
	   		//console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [parent_sSsrtDate.getValue()_2]"+ parent_sSsrtDate.getValue() );
	        sSsrtDate = parent_sSsrtDate;		// 청약일  
	    }

		if (wconv.length(sCrNoPop) == 0) { 		//청약에서 호출 이면
			//console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_41] [01. 청약에서 호출 이면]" );

			if (parent_Ds_LTIDA00.getRowCount() > 0) { 
	           	wconv.clear(Ds_LTIDA00);
	     		wconv.copy(Ds_LTIDA00, parent_Ds_LTIDA00);
	        }

	        if (wconv.substr(Ds_LTIDA00.getCellData(0, "sPlanNo"), 0, 1) == "M") {
	            Div1_st_Tit.setValue("청약번호");
	            Div0_st_Tit1.setValue("설계번호");
	            Div1_medt_sPlanNo.setValue(Ds_LTIDA00.getCellData(0, "sPlanNo"));
	            Div0_medt_sPlanNo.setValue(Ds_LTIDA00.getCellData(0, "sPlanNo"));
	        } else if (wconv.subStr(Ds_LTIDA00.getCellData(0, "sPlanNo"), 0, 1) == "N") {
	            Div1_st_Tit.setValue("설계번호");
	            Div0_st_Tit1.setValue("설계번호");
	            Div1_medt_sPlanNo.setValue(Ds_LTIDA00.getCellData(0, "sPlanNo"));
	            Div0_medt_sPlanNo.setValue(Ds_LTIDA00.getCellData(0, "sPlanNo"));
	        } else {
	      		Div1_st_Tit.setValue("계약번호");
	        }

			if (parent_Ds_PiboList.getRowCount() > 0) {
	            var index = 0; 

	            for (var i = 0; i < parent_Ds_PiboList.getRowCount(); i++) {
	                if (parent_Ds_PiboList.getCellData(i, "sel") == "1") {
	                    index = i;
	                    break;
	                }
	            }
	            Ds_LTIDA00.setCellData(0, "sPlanCd", parent_cmb_Plan.getValue()); //플랜코드
	            Ds_LTIDA00.setCellData(0, "sPlanName", parent_sPlanName.getValue());  //플랜명
	            Ds_LTIDA00.setCellData(0, "sInrpsSex", ""); //피보험자성별
	            Ds_LTIDA00.setCellData(0, "sCrNo", sCrNoPop); //계약번호

	            //단체일경우 변수명이 달라서 개인쪽에 맞춤
	            if (sPsnGroupFlgcdPop == "21") {
	                Ds_LTIDA00.setCellData(0, "sInrpsName", parent_Ds_PiboList.getCellData(index, "sGroupInrpsName")); //피보험자명
	                Ds_LTIDA00.setCellData(0, "sInrpsCd", parent_Ds_PiboList.getCellData(index, "sGroupInrpsCd")); //피보험자코드
	                Ds_LTIDA00.setCellData(0, "nInrpsAge", parent_Ds_PiboList.getCellData(index, "nGroupInrpsAge")); //피보험자연령
	            } else {
	                Ds_LTIDA00.setCellData(0, "sInrpsName", parent_Ds_PiboList.getCellData(index, "sInrpsName")); //피보험자명
	                Ds_LTIDA00.setCellData(0, "sInrpsCd", parent_Ds_PiboList.getCellData(index, "sInrpsCd")); //피보험자코드
	                Ds_LTIDA00.setCellData(0, "nInrpsAge", parent_Ds_PiboList.getCellData(index, "nInrpsAge")); //피보험자연령		
	            }
	        }        

			if (Ds_Prprt.getRowCount() > 0) {
	            var sInsBntpName = "";
	            var sBuldgStrtGradname = "";
	            var index = 0;
	            var prprtCheckFlag = false;
	            for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
	                if (Ds_Prprt.getCellData(i, "sel1") == "1") {
	                    index = i;
	                    prprtCheckFlag = true;
	                    break;
	                }
	            }
	            if (prprtCheckFlag) { 
	                switch_div.setSelectedID("sw_div1");	 // switch_div sw_div1(건물 정보) 보이기 
	            } else {
	            	switch_div.setSelectedID("sw_div2");	 // switch_div sw_div2(개인 정보) 보이기 
	            }
	         	//console.log("[/CBSC055P.xml] [init()] ==> [1. 청약에서 호출 이면] [DsWs_BuldgStrtGradCd.getRowCount()]"+ DsWs_BuldgStrtGradCd.getRowCount() ); 

	            for (var i = 0; i < DsWs_BuldgStrtGradCd.getRowCount(); i++) { 	// 목적물특성 Ds
	            	//console.log("[/CBSC055P.xml] [init()] ==> [1. 청약에서 호출 이면] [Ds_Prprt.sInsBntpCd]"+ Ds_Prprt.getCellData(index, "sInsBntpCd") +" [DsWs_BntpCd.sCd]"+ DsWs_BntpCd.getCellData(index, "sCd") ); 

	                if (Ds_Prprt.getCellData(index, "sInsBntpCd") == DsWs_BntpCd.getCellData(i, "sCd")) {
	                    sInsBntpName = DsWs_BntpCd.getCellData(i, "sHnglCdName");
	                    //console.log("[/CBSC055P.xml] [init()] ==> [1. 청약에서 호출 이면] [sInsBntpName_71]"+ sInsBntpName );  
	                }
	            }
	            for (var i = 0; i < DsWs_BntpCd.getRowCount(); i++) {
	                if (Ds_Prprt.getCellData(index, "sBuldgStrtGradCd") == DsWs_BuldgStrtGradCd.getCellData(i, "sCd")) {
	                    sBuldgStrtGradname = DsWs_BuldgStrtGradCd.getCellData(i, "sHnglCdName");
	                }
	            }

	            Div0_edt_buildingName.setValue("건물" + wconv.toNumber(index + 1));		// 건물구분
	            Div0_edt_buildingCd.setValue(sInsBntpName + sBuldgStrtGradname);		// 목적물특성  ==> 부모창에서 목적물 정보가 안 보여서 Display 못함[재물이면 재물청약(/B030)으로 변경 되어야 됨]
	            //console.log("[/CBSC055P.xml] [init()] ==> [1. 청약에서 호출 이면] [건물구분]"+ Div0_edt_buildingName.getValue() +"[목적물특성]"+ Div0_edt_buildingCd.getValue() ); 
	        }

	    } else {	// 02. 변경에서 호출 이면
	    	//console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_41] [02. 변경에서 호출 이면]" );

	 		var prprtCheckFlag = false;
	        for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
	            if (Ds_Prprt.getCellData(i, "sel1") == "1") {
	                index = i;
	                prprtCheckFlag = true;
	                break;
	            }
	        }

	        if (prprtCheckFlag) {            
	           // idx = parent.lfn_GetChkPrprtIdx();
	            idx = wprom.getOpener().scwin.lfn_GetChkPrprtIdx();		// 물건 리스트의 체크된 인덱스 취득(부모창에서)
	            nGroupSeqno = "";
	            nInrpsSeqno = parent_Ds_Prprt.getCellData(idx, "nInsrdObjctSeqno"); 
	        } else { 
	           // idx = parent.lfn_GetChkIdx();
	            idx = wprom.getOpener().scwin.lfn_GetChkIdx();		// 피보험자 리스트의 체크된 인덱스 취득(부모창에서)
	            nGroupSeqno = parent_Ds_PiboList.getCellData(idx, "nGroupSeqno");
	            nInrpsSeqno = parent_Ds_PiboList.getCellData(idx, "nInrpsSeqno"); 
	        }
	        sCrNoPop = parent_Ds_CntrDtl.getCellData(0, "sCrNo");
	        nPsnPrprtGroupSeqno = "";
	        sPsnPrprtGroupFlgcd = "";
	        sSsrtDate = parent_Ds_CntrDtl.getCellData(0, "sSsrtDate");
	        sGdCd = parent_Ds_CntrDtl.getCellData(0, "sGdCd");

	        Div1_st_Tit.setValue("계약번호");
	        Div0_st_Tit1.setValue("계약번호");
	        Div1_medt_sPlanNo.setValue(sCrNoPop);
	        Div0_medt_sPlanNo.setValue(sCrNoPop);

	        if (nGroupSeqno.length > 0) {	 //단체인경우            
	            nPsnPrprtGroupSeqno = nInrpsSeqno;
	            sPsnPrprtGroupFlgcd = "3";
	        } else {
	            nPsnPrprtGroupSeqno = nInrpsSeqno;
	            sPsnPrprtGroupFlgcd = "1";

	            if (prprtCheckFlag) {	 //물건인경우                
	                sPsnPrprtGroupFlgcd = "2";
	            }
	        } 

	        if (sCrNo.length != 12) {
	            wprom.alert("계약번호를 확인해 주십시오.");
	            return;
	        }
	        if (sPsnPrprtGroupFlgcd.length > 0)
	            sPsnGroupFlgcdPop = sPsnPrprtGroupFlgcd;

	        if (parent_Ds_CntrDtl.getRowCount() > 0) { 
	            wconv.clear(Ds_LTIDA00);
	            wconv.copy(Ds_LTIDA00, parent_Ds_CntrDtl);
	        }
	 		//console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_43] [Ds_LTIDA00.getRowCount()]"+ Ds_LTIDA00.getRowCount() +"[Ds_LTIDA00]"+ WebSquare.xml.serialize(Ds_LTIDA00.getAllXML()) ); 

			Ds_LTIDA00.setRowPosition(0);	 // Row 위치 0번째로 지정 

	        if (parent_Ds_PiboList.getRowCount() > 0) {
	            var index = 0;
	            for (var i = 0; i < parent_Ds_PiboList.getRowCount(); i++) {
	                if (parent_Ds_PiboList.getCellData(i, "sel") == "1") {
	                    index = i;
	                    break;
	                }
	            }
	            Ds_LTIDA00.setCellData(0, "sInrpsSex", ""); //피보험자성별

	            //단체일경우 변수명이 달라서 개인쪽에 맞춤
	            Ds_LTIDA00.setCellData(0, "sInrpsName", parent_Ds_PiboList.getCellData(index, "sInrpsName")); //피보험자명
	            Ds_LTIDA00.setCellData(0, "sInrpsCd", parent_Ds_PiboList.getCellData(index, "sInrpsCd")); //피보험자코드
	            Ds_LTIDA00.setCellData(0, "nInrpsAge", parent_Ds_PiboList.getCellData(index, "nInrpsAge")); //피보험자연령		
	            sApplPlanCd = parent_Ds_PiboList.getCellData(index, "sApplPlanCd");
	            sApplPlanName = parent_Ds_PiboList.getCellData(index, "sApplPlanName");	 //플랜명
	            Ds_LTIDA00.setCellData(0, "sPlanName", sApplPlanName); //플랜명 셋팅 
	            //console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_45] [index_번째]"+ index +"[sApplPlanName]"+ sApplPlanName +"[Ds_LTIDA00.sPlanName]"+ Ds_LTIDA00.getCellData(0, "sPlanName") ); 		
	        }

	        if (Ds_Prprt.getRowCount() > 0) {
	            var sInsBntpName = "";
	            var sBuldgStrtGradname = "";
	            var index = 0;
	            var prprtCheckFlag = false;
	            for (var i = 0; i < Ds_Prprt.getRowCount(); i++) {
	                if (Ds_Prprt.getCellData(i, "sel1") == "1") {
	                    index = i;
	                    prprtCheckFlag = true;
	                    break;
	                }
	            }
	            if (prprtCheckFlag) { 	//물건인경우
					 switch_div.setSelectedID("sw_div1");	 // switch_div sw_div1(건물 정보) 보이기 
	            }
	            for (var i = 0; i < DsWs_BuldgStrtGradCd.getRowCount(); i++) {
	                if (Ds_Prprt.getCellData(index, "sInsBntpCd") == DsWs_BntpCd.getCellData(i, "sCd")) {
	                    sInsBntpName = DsWs_BntpCd.getCellData(i, "sHnglCdName");
	                }
	            }
	            for (var i = 0; i < DsWs_BntpCd.getRowCount(); i++) {
	                if (Ds_Prprt.getCellData(index, "sBuldgStrtGradCd") == DsWs_BuldgStrtGradCd.getCellData(i, "sCd")) {
	                    sBuldgStrtGradname = DsWs_BuldgStrtGradCd.getCellData(i, "sHnglCdName");
	                }
	            } 
	          	Div0_edt_buildingName.setValue("건물" + wconv.toNumber(index + 1));		// 건물구분
	            Div0_edt_buildingCd.setValue(sInsBntpName + sBuldgStrtGradname);		// 목적물특성
	            //console.log("[/CBSC055P.xml] [init()] ==> [2. 청약에서 호출아 아니면] [건물구분]"+ Div0_edt_buildingName.getValue() +"[목적물특성]"+ Div0_edt_buildingCd.getValue() ); 
	        }       
	    }

	 	wconv.clear(DsWs_LTIDA00);  // DsWs_LTIDA00 Clear

	  	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB070Task", "getBosangTrty");	// 보상한도 조회 Obj

		 //청약에서호출
	    if (wconv.length(sCrNoPop) == 0) { 
	        MethodObj.addVectorData("vBosangTrty", "vBosangTrty", "parent_Ds_BosangTrty:A");  		// Param 셋팅(특약코드 Ds)

	        util.jSonDsCreate([{"sGdCd":""}],  "Ds_sGdCd");
			$w.data["Ds_sGdCd"].setCellData(0, "sGdCd", parent_sGdCd );	// addDataTagByHashkey 처리(상품코드)		
			MethodObj.addSingleData("sGdCd", "sGdCd", "Ds_sGdCd", "type02");	// addSingleData 셋팅(상품코드)

			util.jSonDsCreate([{"sPlanCd":""}],  "Ds_sPlanCd");
			$w.data["Ds_sPlanCd"].setCellData(0, "sPlanCd", parent.cmb_Plan.getValue());	// addDataTagByHashkey 처리(플랜코드)		
			MethodObj.addSingleData("sPlanCd", "sPlanCd", "Ds_sPlanCd", "type02");	// addSingleData 셋팅(플랜코드)

			util.jSonDsCreate([{"sSsrtDate":""}],  "Ds_sSsrtDate");
			$w.data["Ds_sSsrtDate"].setCellData(0, "sSsrtDate", sSsrtDate);	// addDataTagByHashkey 처리(청약일)		
			MethodObj.addSingleData("sSsrtDate", "sSsrtDate", "Ds_sSsrtDate", "type02");	// addSingleData 셋팅(청약일)

	    } else { 
	 		util.jSonDsCreate([{"sGdCd":""}],  "Ds_sGdCd");  
			$w.data["Ds_sGdCd"].setCellData(0, "sGdCd", sGdCd );	// addDataTagByHashkey 처리(상품코드)		 
			MethodObj.addSingleData("sGdCd", "sGdCd", "Ds_sGdCd", "type02");	// addSingleData 셋팅(상품코드)

			util.jSonDsCreate([{"sPlanCd":""}],  "Ds_sPlanCd");
			$w.data["Ds_sPlanCd"].setCellData(0, "sPlanCd", sApplPlanCd);	// addDataTagByHashkey 처리(플랜코드)		
			MethodObj.addSingleData("sPlanCd", "sPlanCd", "Ds_sPlanCd", "type02");	// addSingleData 셋팅(플랜코드)

			util.jSonDsCreate([{"sSsrtDate":""}],  "Ds_sSsrtDate");
			$w.data["Ds_sSsrtDate"].setCellData(0, "sSsrtDate", sSsrtDate);	// addDataTagByHashkey 처리(청약일)		
			MethodObj.addSingleData("sSsrtDate", "sSsrtDate", "Ds_sSsrtDate", "type02");	// addSingleData 셋팅(청약일)

			util.jSonDsCreate([{"sPsnPrprtGroupFlgcd":""}],  "Ds_sPsnPrprtGroupFlgcd");
			$w.data["Ds_sPsnPrprtGroupFlgcd"].setCellData(0, "sPsnPrprtGroupFlgcd", sPsnPrprtGroupFlgcd);	// addDataTagByHashkey 처리(인물단체구분코드)		
			MethodObj.addSingleData("sPsnPrprtGroupFlgcd", "sPsnPrprtGroupFlgcd", "Ds_sPsnPrprtGroupFlgcd", "type02");	// addSingleData 셋팅(인물단체구분코드)

	 		util.jSonDsCreate([{"nPsnPrprtGroupSeqno":""}],  "Ds_nPsnPrprtGroupSeqno");
			$w.data["Ds_nPsnPrprtGroupSeqno"].setCellData(0, "nPsnPrprtGroupSeqno", nPsnPrprtGroupSeqno);	// addDataTagByHashkey 처리(인물단체일련번호)		
			MethodObj.addSingleData("nPsnPrprtGroupSeqno", "nPsnPrprtGroupSeqno", "Ds_nPsnPrprtGroupSeqno", "type02");	// addSingleData 셋팅(인물단체일련번호)
	  	}
	  	util.jSonDsCreate([{"sCrNo":""}],  "Ds_sCrNo");  
		$w.data["Ds_sCrNo"].setCellData(0, "sCrNo", sCrNoPop );	// addDataTagByHashkey 처리(계약번호)		 
		MethodObj.addSingleData("sCrNo", "sCrNo", "Ds_sCrNo", "type02");	// addSingleData 셋팅(계약번호) 

		MethodObj.setId("getBosangTrty");	// ID 셋팅    	 
		MethodObj.setUrl("/contract/CBSC055PController.do");  				// 서버단 호출 URL  
		MethodObj.addSingleData("LTIDA00", "LTIDA00", "Ds_LTIDA00:A");  		// Param 셋팅(계약기본사항정보Ds): A, I, U, R  
		MethodObj.setXPath("//hashtable/data[@hashkey='lTIDA00Doc']/LTIDA00");		// XPath(Ds_LTIDA00)
		MethodObj.addXPath("//hashtable/data[@hashkey='vBosangTrtyList']/vector/data/LTIDA43");		// XPath(Ds_InsurPerson)  
		MethodObj.addXPath("//*/WARNING");		// XPath(DsWs_Warning)
		MethodObj.setXPathDs(); 	// XPath 전체 경로 사용할 경우
		MethodObj.setHashTable();   	// 전문의 앞이 hashtable로 보낼 때
		MethodObj.sync();		// 동기 처리[기본(async:비동기)] 
		MethodObj.send();		// 최종 전송  
		//console.log("[/CBSC055P.xml] [init()()] ==> [01. 보상한도 조회, 전송_완료] [TEST_91]");

		//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

		DsWs_LTIDA00.setJSON(_tranSync.getDsJson("//hashtable/data[@hashkey='lTIDA00Doc']/LTIDA00"));			// 보험 기본사항 Ds 셋팅
		Ds_BosangTrty.setJSON(_tranSync.getDsJson("//hashtable/data[@hashkey='vBosangTrtyList']/vector/data/LTIDA43"));			// 보상 특약 정보 Ds 셋팅
		DsWs_Warning.setJSON(_tranSync.getDsJson("//*/WARNING"));  		// Warning DS 셋팅(조회 결과)    
		//console.log("[/CBSC055P.xml] [init()] ==> [I/F 조회 완료] DsWs_LTIDA00,_갯수]"+ DsWs_LTIDA00.getRowCount() +"[DsWs_LTIDA00]"+ WebSquare.xml.serialize(DsWs_LTIDA00.getAllXML()) );  

	  	if (DsWs_Warning.getRowCount() != 0) {
	        wprom.alert(DsWs_Warning.getCellData(0, "msg"));
	        return;
	    }
	    if (DsWs_LTIDA00.getRowCount() == 0) {
	  		wprom.alert("조회 건이 없습니다.");
	     	return;     

	    } else {	 	        
		 	//console.log("[/CBSC055P.xml] [init()] ==> [I/F 조회 성공] [보상한도 조회] [TEST_41]" );   

	        if (wconv.length(sCrNoPop) != 0) {	//변경일 경우
	            medt_sPlanNo = sCrNoPop;
	        }

			wconv.copy(Ds_LTIDA00, DsWs_LTIDA00);		// 조회결과 Ds를 기본사항 정보 Ds 복사

			Ds_LTIDA00.setRowPosition(0);	 // Row 위치 0번째로 지정 

	        // 01. 건물 정보     
	    	Div0_edt_sPaymCyclCd.setValue(Ds_LTIDA00.getCellData(0, "sPaymCyclName"));		// 납입주기 
	        Div0_edt_sInsurStrtEnddate.setValue(scwin.lfn_SetDateFormat(Ds_LTIDA00.getCellData(0, "sInsurStrtdate"), "-") + " ~ " + scwin.lfn_SetDateFormat(Ds_LTIDA00.getCellData(0, "sInsurEndDate"), "-")); 	// 보험기간(보험시작일자, 보험종료일자)
	        Div0_edt_sPaymTermCd.setValue(Ds_LTIDA00.getCellData(0, "sInsurTermName") + "/" + Ds_LTIDA00.getCellData(0, "sPaymTermName")); //보기/납기
	//        Ds_LTIDA00.setCellData(0, "sInrpsName", gfn_changeMask(Ds_LTIDA00.getCellData(0, "sInrpsName"), "4"));
	//        Ds_LTIDA00.setCellData(0, "sInrpsCd", gfn_changeMask(Ds_LTIDA00.getCellData(0, "sInrpsCd"), "1"));

	     	// 02. 개인 정보  
			Ds_LTIDA00.setCellData(0,"sInrpsName", gfn_changeMask(Ds_LTIDA00.getCellData(0,"sInrpsName"),"4"));	// 피보험자:  마스크 처리[4. 성명: 2째자리 부터 2자리 * 처리]
			Ds_LTIDA00.setCellData(0,"sInrpsCd", gfn_changeMask(Ds_LTIDA00.getCellData(0,"sInrpsCd"),"1"));		// 주민번호: 마스크 처리[1. 주민번호: 뒷번호 6자리] 
			Div1_edt_sPaymCyclCd.setValue(Ds_LTIDA00.getCellData(0, "sPaymCyclName"));		// 납입주기
	        Div1_edt_sInrpsCd.setValue("주민번호(" + Ds_LTIDA00.getCellData(0, "sInrpsCd") + ") 성별(" + Ds_LTIDA00.getCellData(0, "sInrpsSex") + ") 연령(" + Ds_LTIDA00.getCellData(0, "nInrpsAge") + "세)");  // 적용내용
		   	Div1_edt_sInsurStrtEnddate.setValue(scwin.lfn_SetDateFormat(Ds_LTIDA00.getCellData(0, "sInsurStrtdate"), "-") + " ~ " + scwin.lfn_SetDateFormat(Ds_LTIDA00.getCellData(0, "sInsurEndDate"), "-")); 	// 보험기간(보험시작일자, 보험종료일자) 
	        Div1_edt_sPaymTermCd.setValue(Ds_LTIDA00.getCellData(0, "sInsurTermName") + "/" + Ds_LTIDA00.getCellData(0, "sPaymTermName")); //보기/납기
	        //console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [sPlanName]"+ Ds_LTIDA00.getCellData(0, "sPlanName") +"[sInrpsName]"+ Ds_LTIDA00.getCellData(0, "sInrpsName") );   
	    } 

		return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); // =================================================>
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	//pro = pro.catch(function(rj) {
		//util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	//});

	return pro;

	console.log("[/CBSC055P.xml] [init()] ==> [폼 로딩] [TEST_end]" );
};


//---------------------------------------------------------------------------
// function 명 : lfn_SetDateFormat
// 내용        : 날짜 포맷
//---------------------------------------------------------------------------
scwin.lfn_SetDateFormat = function(str, strType) {
    var len = str.length;

    if (len == 0) {
        wprom.alert("치환할 8자리 날짜를 입력하세요!");
        return false;
    } else if (len > 8) {
        wprom.alert("날짜형식은 8자리를 초과할수 없습니다!");
        return false;
    } else if (len != 8) {
        wprom.alert("날짜형식은 최소 8자리입니다!");
        return false;
    }
    var strEx = str.substr(0, 4) + strType + str.substr(4, 2) + strType + str.substr(6, 2);
    return strEx;
};

