scwin.init = function() {
	console.log("[/CBSC056P.xml] [init()] ==> [사전조회 폼 로딩] [TEST_01]" );
//	 
 	var pro = Promise.resolve();
	window.top.util.winTopShowModalChk = "T";
	util.winTopShowModal();

	pro = pro.then(function(rv) {
		return util.winTopShowModalPromise();
	});

	pro = pro.then(function(rv) {
		// init 부분 함수 입력하는곳  // =================================================>

		var pObj = wprom.getParamObject(); 
		parent_Ds_Pibo = pObj.Ds_Pibo;	// 부모창 피보험자 정보 Ds Set 
		parent_Ds_LTIDA00 = pObj.Ds_LTIDA00;		//  부모창 보험 기본사항 정보 Ds Set
		parent_Ds_Pibo_BackUp = pObj.Ds_Pibo_BackUp;	// 부모창 보험 기본사항 정보 Ds Set
		//console.log("[CBSC056P.xml] [init()] ==> [폼 로딩] ==> [parent_Ds_Pibo_갯수_01]"+ parent_Ds_Pibo.getRowCount() +"[parent_Ds_Pibo]"+ WebSquare.xml.serialize(parent_Ds_Pibo.getAllXML()) );    

		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata); 	// 부모창에서 받아온 값( 예) inJSON.sPlanNo)

		if (!util.isNull(inJSON.sInrpsCd)) 	sInrpsCd = inJSON.sInrpsCd; 		//주민번호 
		if (!util.isNull(inJSON.sPlanNo)) 	sPlanNo = inJSON.sPlanNo; 	//설계번호 
		if (!util.isNull(inJSON.sInrpsName)) 	sInrpsName = inJSON.sInrpsName; 		//피보험자명 
		if (!util.isNull(inJSON.hasPronCmpsTrty)) 	hasPronCmpsTrty = inJSON.hasPronCmpsTrty; 		//실손가입여부(2011.05.13 김상운추가) 
		if (!util.isNull(inJSON.hasFxamtCmps)) 	hasFxamtCmps = inJSON.hasFxamtCmps; 					// 정액담보가입여부(2011.05.13 김상운추가)
		if (!util.isNull(inJSON.sSvtYn)) 	sSvtYn = inJSON.sSvtYn; 		//상품구분코드 여부 
		if (!util.isNull(inJSON.sAgreDocuRecpYn)) sAgreDocuRecpYn = inJSON.sAgreDocuRecpYn; 		// 가입설계 동의 여부 조회: 추가(2017.11.24)
		if (!util.isNull(inJSON.sHandlerID)) sHandlerID = inJSON.sHandlerID; 	// 우선순위(사용인ID, 취급자ID, 로그인사번): 추가(2017.11.24)
		console.log("[/CBSC056P.xml] [init()] ==> [사전조회 폼 로딩] [주민번호]"+ sInrpsCd +"[피보험자명]"+ sInrpsName +"[설계번호]"+ sPlanNo +"[01. 실손가입여부]"+ hasPronCmpsTrty +"[02. 정액담보가입여부]"+ hasFxamtCmps +"[상품구분코드 여부]"+ sSvtYn );

	    //청약번호 누락여부 체크
	    if (wconv.length(sPlanNo) < 1) {
	        wprom.alert("청약번호가 누락되었습니다. 확인하세요");
	        return;
	    } 

	    //대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
	    if (util.getGdsValue("gUserDuty") == "30" || util.getGdsValue("gUserDuty") == "33" || util.getGdsValue("gUserDuty") == "35") {}
	    /*
	    if(sSvtYn == "Y"){
	    	stc_basic1.Visible = false;
	    	stc_basic2.Visible = false;
	    	stc_basic3.Text = "";
	    	stc_basic4.Top = 218;
	    	stc_Svt1.Visible = true;
	    	stc_Svt2.Visible = true;
	    	stc_Svt1.Left = 42;
	    	stc_Svt1.Top = 79;
	    	stc_Svt2.Left = 42;
	    	stc_Svt2.Top = 155;
	    }
	    */
	    //대리점인지 여부(30:대리점,33:사용인,35:TM사용인)
	    if (sAgncYn == "Y") {
	    	gfn_obj_convertMask(msk_sInrpsCd, sInrpsCd, "######-#ZZZZZZ"); 
	    } else {
	        msk_sInrpsCd.setValue(sInrpsCd); 		//주피 피보험자 코드
	    }

	    edt_sInrpsName.setValue(sInrpsName); // 주피 피보험자명
	    edt_sRelnName.setValue("본인"); 	// 주피 계약자와의관계

	    // 의료비 관련 공통코드 불러오기 
		var dataSetStr = "Ds_Guart Ds_Covge Ds_Copay Ds_GuartDate";
	    var codTypeStr = "7076 7077 7078 7080";
	    var componentIds = ""; 		//"ipt_Hby sel_Imp Div0.wf_ipt_Hby Div0.wf_sel_Imp";
	    var selectAdds = "";
	    nfn_getCommonData(dataSetStr, codTypeStr, componentIds, selectAdds);

		var pi_nRow = wconv.findRow(parent_Ds_Pibo, "sel", "1");		// 선택한 칼럼값 Row 찾기

	 	Ds.insertRow();
	 	Ds.setCellData(0, "sOthMedicalYN", parent_Ds_Pibo.getCellData(pi_nRow, "sPsnInfoOferAgreYn")); //개인정보제공동의여부 
	 	Ds.setCellData(0, "sExplainProIndYN", parent_Ds_Pibo.getCellData(pi_nRow, "sMNumCrInsAgreYn")); //다수계약가입동의여부 
	 	Ds.setCellData(0, "sOthMedicalYN", "Y"); //강제 셋팅(ldh)
		Ds.setCellData(0, "sExplainProIndYN", "Y"); //강제 셋팅(ldh) 
		//console.log("[/CBSC056P.xml] [init()] ==> [Ds, 총갯수]"+ Ds.getTotalRow() +"[Ds]"+ WebSquare.xml.serialize(Ds.getAllXML()) ); 

		util.setCompBindRefresh(Ds.getID());  	// Ds Binding 처리

	    // 공통코드 내용 삭제
	    wconv.clear(DsWs_NlisrFxCrStatCd);
	    wconv.clear(DsWs_FinaOrgnCd);
	    gfn_getCommonCodes("DsWs_NlisrFxCrStatCd DsWs_FinaOrgnCd DsWs_AnswCd DsWs_NlisrAssoCvrgeCd", "NlisrFxCrStatCd FinaOrgnCd FixAnswCd NlisrAssoCvrgeCd", "Cmb_NlisrFxCrStatCd Cmb_FinaOrgnCd Cmb_AnswCd Cmb_NlisrAssoCvrgeCd", "B B B B");

		gfn_LogCreditInfo("", "", "S", sInrpsCd);		//개인정보 조회 이력 쌓기

		scwin.btn_search_OnClick(btn_search01); 	 //조회처리 

		return ;		// init 함수를 종료하고자 할때 사용	 // =================================================> 
	});

	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});

	return pro;
};

