// 조회 버튼 클릭시
/* ***************************************** */
scwin.btn_search_OnClick = function(e) { 
	//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [조회 버튼 클릭] [TEST_01] [e.id]"+ e.id );

    var sIsdCsmNum = "";
    var currDate = "";
    var currTime = "";

    currDate = WebSquare.date.getCurrentServerDate("yyyyMMdd"); 		// global 현재일자 
    currTime = wconv.substr(WebSquare.date.getCurrentServerDate("yyyyMMddHHmmss"), 8, 6); 		// global 현재시각

	 //청약번호 누락여부 체크
    if (wconv.length(sPlanNo) < 1) {
        wprom.alert("청약번호가 누락되었습니다. 확인하세요");
        return;
    } 

    //고객정보 실주민번호 셋팅(20140508) 
	if (sInrpsCd.indexOf("A") > 0) { 
		sInrpsCd = scwin.lfn_getRealNo(sInrpsCd);
    } 

     if (e.id == "btn_search01") {
    	//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [조회 버튼 클릭] [Radio0.getValue()]"+ Radio0.getValue() +"[Radio7.getValue()]"+ Radio7.getValue() );
 		//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [조회 버튼 클릭] [Ds, 총갯수]"+ Ds.getTotalRow() +"[Ds]"+ WebSquare.xml.serialize(Ds.getAllXML()) ); 

		if ((Radio0.getValue() == null || Radio0.getValue() == "") || ((Radio7.getValue() == null || Radio7.getValue() == "")) 
			|| ((Radio0.getValue() == "N" || Radio7.getValue() == "N"))) {
			wprom.alert("본인 비례보상조회 동의여부를 확인하세요.");
			return;
		}
        sIsdCsmNum = sInrpsCd;
        wconv.clear(Ds_List01);
        bOthIns1 = true;
    }
	//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [조회 버튼 클릭] [TEST_21]" );

    //손보협회 서비스 중지여부 체크
    if (gfn_hasStopSrvc("011^012")) {  
       // parent.MediFlag = true;
        for (var i = 0; i < parent_Ds_Pibo.getRowCount(); i++) {	 // 손보협회 서비스 중지 이면
            parent_Ds_Pibo.setCellData(i, "sPsnInfoOferAgreYn", Ds.getCellData(0, "sOthMedicalYN")); //개인정보제공동의여부
            parent_Ds_Pibo.setCellData(i, "sMNumCrInsAgreYn", Ds.getCellData(0, "sExplainProIndYN")); //다수계약가입동의여부
            parent_Ds_Pibo_BackUp.setCellData(i, "MediFlag", "Y");		// 본인 실손의료비 중복조회 여부 Flag(Y: 조회 했음)
            parent_Ds_Pibo_BackUp.setCellData(i, "fixFlag", "Y");			// 정액가입 중복조회 여부 Flag(Y: 조회 했음)
        }
		//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [gfn_hasStopSrvc(011^012)]"+ parent_Ds_Pibo_BackUp.getCellData(pi_nRow, "MediFlag") +"[parent_Ds_Pibo_BackUp.fixFlag]"+ parent_Ds_Pibo_BackUp.getCellData(pi_nRow, "fixFlag") ); 

        wprom.alert(gfn_getStopSrvcMsg());
        return;
    }
    var sSumMsg = "";

    //가입설계 동의 여부 조회 
    //N1608-00179:장기 단체보험 전산시스템 개발 요청(단체일 경우 단체 협약서를 받았으면 가입설계 동의 없이도 조회)
    if (sAgreDocuRecpYn != "1") {
        if (scwin.lfn_getCUSAA42Info(sInrpsCd) == "Y") {
        	//console.log("[/CBSC056P.xml] [btn_search_OnClick()] ==> [TEST_31] [sAgreDocuRecpYn]"+ sAgreDocuRecpYn +"[scwin.lfn_getCUSAA42Info(sInrpsCd)]"+ scwin.lfn_getCUSAA42Info(sInrpsCd) );
            wprom.alert(edt_sInrpsName.getValue() + "님의 가입설계 동의여부를 확인하세요");
            return;
        }
    }

    //N1608-00179:장기 단체보험 전산시스템 개발 요청(단체일 경우 개인실손 조회하지 않는다.) 
    //추후 단체 기타실손도 대상이 될 경우 단체실손 사전 조회 및 집중 추가
    if (sPsnGroupFlgcd != "21") {
        var midiRes = scwin.lfn_selectMediCover(btn_search01, sIsdCsmNum, currDate, currTime);	 // 실손 사전조회 
 		//console.log("[/CBSC056P.xml] [init()] ==> [01. 실손가입여부_확인]"+ midiRes +"[sPsnGroupFlgcd]"+ hasFxamtCmps );

        if (midiRes == false) { 
            wprom.alert("실손사전조회: " + sMediMsg);		// 실손사전 조회 에러 메시지 띄우기 --> 예) 정액담보조회: 조회되었으나 [조회 해당자료 없음401] 의 이유로 메세지가 표시되었습니다.
            return;
        }
    }
    //console.log("[/CBSC056P.xml] [init()] ==> [02. 정액담보 조회_hasFxamtCmps]"+ hasFxamtCmps );

    if (hasFxamtCmps == "Y") { 
		var fixRes = scwin.selectKCIS0200240();		// 정액담보 조회
		//console.log("[/CBSC056P.xml] [init()] ==> [02. 정액담보 조회_확인]"+ fixRes +"[hasFxamtCmps]"+ hasFxamtCmps );

        if (fixRes == false) {
            wprom.alert("정액담보조회: " + sMediMsg);	// 정액담보 조회 에러 메시지 띄우기
            return;
        }
    }

    //실손조회 + 정액담보조회 메세지 
    sSumMsg = sMediMsg + sFixMsg;

    //메세지 출력
    if (sSumMsg != "") {
        wprom.alert(sSumMsg);	// 2개의 에러 메시지 띄우기
    }

	scwin.Btn_NewReg_OnClick();   // 저장
};

//---------------------------------------------------------------------------
// function 명 : Btn_NewReg_OnClick
// 내용        : 저장
//---------------------------------------------------------------------------
scwin.Btn_NewReg_OnClick = function(e) { 
	//console.log("[/CBSC056P.xml] [Btn_NewReg_OnClick()] ==> [저장 처리] [TEST_01]" );

	gUserID = util.getGdsValue("gUserID");    // 사용자 ID
   	gUserIP = util.getGdsValue("gUserIP");    // 사용자 IP

    if (!bOthIns1) {
        // alert("실손의료비 중복조회를 확인하세요.");
        wprom.alert("비례보상 조회를 확인하세요.");
        return;
    }

    //주피보험자
    //if(sSaveYN == "N") //  save yn 여부에 따라 저장 
    Ds.setCellData(0, "sOthMedicalYN", Radio0.getValue());
    Ds.setCellData(0, "sExplainProIndYN", Radio7.getValue());

    if (wconv.length(parent_Ds_LTIDA00.getCellData(0, "sPlanNo")) < 1) {
        parent_Ds_LTIDA00.setCellData(0, "sPlanNo", sPlanNo);
    }

    var pi_nRow = wconv.findRow(parent_Ds_Pibo, "sel", "1"); 
    parent_Ds_LTIDA00.setCellData(0, "gUserID", gUserID);
    parent_Ds_LTIDA00.setCellData(0, "gUserIP", gUserIP); 
	parent_Ds_LTIDA00.setCellData(0, "sDoer", util.getGdsValue("gDoer")); 	//취급자 

	wconv.copy(Ds_LTIDA00, parent_Ds_LTIDA00);		// Ds_LTIDA00t Ds에 parent_Ds_LTIDA00를 복사 

	var MethodObj = new callService("com.inswave.longterm.ssr.pln.task.DBB250Task", "saveReward"); 	// 01. 저장 처리 Obj 
	MethodObj.setId("saveReward");		// ID 셋팅

	util.jSonDsCreate([{"sInrpsCd":""}], "Ds_sInrpsCd");
	$w.data["Ds_sInrpsCd"].setCellData(0, "sInrpsCd", sInrpsCd); 
    MethodObj.addSingleData("sInrpsCd", "sInrpsCd", "Ds_sInrpsCd", "type02");

	util.jSonDsCreate([{"nInrpsSeqno":""}], "nInrpsSeqno");
	$w.data["nInrpsSeqno"].setCellData(0, "nInrpsSeqno", parent_Ds_Pibo.getCellData(pi_nRow, "nInrpsSeqno"));	// Param 셋팅(피보험자 순번)    
    MethodObj.addSingleData("nInrpsSeqno", "nInrpsSeqno", "nInrpsSeqno", "type02"); 
	MethodObj.addSingleData("LTIDA00", "LTIDA00", "Ds_LTIDA00:A");  	// 계약 정보 Ds: A(전체), I(등록), U(수정), R
	MethodObj.addVectorData("vLTIDA44", "LTIDA44", "Ds_List01:A");   	// 실손사전 정보 Ds  
	MethodObj.setUrl("/contract/CBSC056PController.do");  				// 서버단 호출 URL
	MethodObj.addXPath("//WARNING");	// XPath(No) 
	MethodObj.setHashTable();   	// 전문의 앞이 hashtable로 보낼 때 
	MethodObj.sync();		// 동기 처리[기본(async:비동기)] 
	MethodObj.send();		// 최종 전송  		 
	//console.log("[/CBSC056P.xml] [Btn_NewReg_OnClick()] ==> [02. 저장 처리_완료] [TEST_71]");  	

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

 	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "Ds_Warning");  		// Warning DS 셋팅(조회 결과)  
	console.log("[/CBSC056P.xml] [Btn_NewReg_OnClick()] ==> [저장 처리] [Ds_Warning.getRowCount()]"+ Ds_Warning.getRowCount()  +"[Ds_Warning]"+ WebSquare.xml.serialize(Ds_Warning.getAllXML()) ); 

    if (Ds_Warning.getRowCount() != 0) {
        wprom.alert("warn" + Ds_Warning.getCellData(0, "msg"));
        return;

    } else { 
    	//console.log("[/CBSC056P.xml] [Btn_NewReg_OnClick()] ==> [저장 성공]");

        parent_Ds_Pibo.setCellData(pi_nRow, "sPsnInfoOferAgreYn", Ds.getCellData(0, "sOthMedicalYN")); 	// 개인정보제공동의여부
        parent_Ds_Pibo.setCellData(pi_nRow, "sMNumCrInsAgreYn", Ds.getCellData(0, "sExplainProIndYN")); // 다수계약가입동의여부

        if (hasPronCmpsTrty == "Y") {
            parent_Ds_Pibo_BackUp.setCellData(pi_nRow, "MediFlag", "Y"); 	// 본인 실손의료비 중복조회 여부 Flag(Y: 조회 했음)
        }
        if (hasFxamtCmps == "Y") {
            parent_Ds_Pibo_BackUp.setCellData(pi_nRow, "fixFlag", "Y");	// 정액가입 중복조회 여부 Flag(Y: 조회 했음)
        } 
