	//console.log("[/CBSC056P.xml] [lfn_selectMediCover()] ==> [실손담보 가입현황 조회] [TEST_01] [obj]"+ obj +"[sIsdCsmNum]"+ sIsdCsmNum +"[currDate]"+ currDate +"[currTime]"+ currTime );

