	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC056P.xml] [lfn_getCUSAA42Info()] ==> [01. 가입설계 동의 여부 조회, 전송_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_Search42.setJSON(_tranSync.getDsJson("CUSAA42"));		// DS 셋팅(조회 결과)  
	//console.log("[/CBSC056P.xml] [lfn_getCUSAA42Info()] ==> [DS_갯수]"+ Ds_Search42.getRowCount() +"[Ds_Search42]"+ WebSquare.xml.serialize(Ds_Search42.getAllXML()) ); 

    if (Ds_Search42.getCellData(0, "result") > 0) {
        if (Ds_Search42.getCellData(0, "sAgmReq") == "Y") {
            RejectYN = "Y"; //유효일자가 지난경우 다시 받기. 해당 칼럼이 다 체크해줌 
            //2016.09.12(N1609-00035) : 배서에서 들어오는 경우는 유효일자 체크안함
            if (!wconv.isExistVar(sPlanNo, true)) {		// 특정 Variable이 존재하는지 여부를 검색
                if (wconv.left(sPlanNo, 1) == "N") { 
              		RejectYN = "";
                }
            }
        }
    } else {
        RejectYN = "Y"; 	// 가입설계 동의 여부 체크 (Y: 동의)
    }
	//onsole.log("[/CBSC056P.xml] [lfn_getCUSAA42Info()] ==> [가입설계 동의여부 확인] [RejectYN]"+ RejectYN );

    return RejectYN;
};

//---------------------------------------------------------------------------
// function 명 : lfn_getRealNo
// 내용        : 실주민번호 조회
//---------------------------------------------------------------------------
scwin.lfn_getRealNo = function(sCustKeyNo) {
	//console.log("[/CBSC056P.xml] [lfn_getRealNo()] ==> [실주민번호 조회] [sCustKeyNo]"+ sCustKeyNo );

	wconv.clear(Ds_CustNo);

	var MethodObj = new callService("com.inswave.longterm.com.com.task.AXR300Task", "selectRealCustNo");  	// 01. 실주민번호 조회 Obj 
	MethodObj.setId("selectCollSettledListByDate");		// ID 셋팅
	MethodObj.setParam("sCustKeyNo", sCustKeyNo);			// Param 셋팅 
	MethodObj.setUrl("/contract/CBSC056PController.do");  		// 서버단 호출 URL
	MethodObj.setXPath("//*/LTI0000");		// XPath 
	MethodObj.sync();		// 동기 처리[기본(async:비동기)]
	MethodObj.send();		// 최종 전송 
	//console.log("[/CBSC056P.xml] [lfn_getRealNo()] ==> [01. 실주민번호 조회, 전송_완료] [TEST_91]");  

	//util.interfaceKey(_tranSync.result.serviceDs); 		// interface 조회 후 Data 리스트 Display

	Ds_CustNo.setJSON(_tranSync.getDsJson("CUSAA42"));		// DS 셋팅(조회 결과)  
	//console.log("[/CBSC056P.xml] [lfn_getRealNo()] ==> [DS_갯수]"+ Ds_CustNo.getRowCount() +"[Ds_CustNo]"+ WebSquare.xml.serialize(Ds_CustNo.getAllXML()) ); 

    if (Ds_CustNo.getRowCount() > 0 && wconv.length(Ds_CustNo.getCellData(0, "sRealCustNo")) > 0) {
        return Ds_CustNo.getCellData(0, "sRealCustNo");

    } else {
        return sCustKeyNo;
    }
};

//**************************************************************************************************
// Grid 칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getColumnValue = function(data, formattedData, rowIndex, colIndex) { 
	//console.log("[/CBSC056P.xml] [lfn_getColumnValue()] ==> [TEST_01] [grd_List.getColumnID(colIndex)]"+ grd_List.getColumnID(colIndex) +"[rowIndex]"+ rowIndex +"[colIndex]"+ colIndex  ); 

	var sRslt = "";

	if (grd_List.getColumnID(colIndex)  == "sInsCtrTpdPed") {  		// Grid 칼럼이 '보험기간' 이면  
		var sInsCtrTpdPed = Ds_List01.getCellData(rowIndex, "sInsCtrTpdPed");   // 보험기간
		sRslt = wconv.substr(sInsCtrTpdPed, 0, 4) +"-"+ wconv.substr(sInsCtrTpdPed, 4, 2) +"-"+  wconv.substr(sInsCtrTpdPed, 6, 2)
		 		 +" ~ "+ wconv.substr(sInsCtrTpdPed, 8, 4) +"-"+ wconv.substr(sInsCtrTpdPed, 12, 2) +"-"+  wconv.substr(sInsCtrTpdPed, 14, 2);	// 보험기간 

	 } else if (grd_List.getColumnID(colIndex)  == "sCovSpecificNm") {  		// Grid 칼럼이 '특약명' 이면    
		var sCovSpecificName2 = Ds_List01.getCellData(rowIndex, "sCovSpecificName");   // 특약명  
		var sCovName2 = Ds_List01.getCellData(rowIndex, "sCovName");    
		//console.log("[/CBSC056P.xml] [lfn_getColumnValue()] ==> [TEST_04] [sCovSpecificName2]"+ sCovSpecificName2 +"[sCovName2]"+ sCovName2  ); 

		if (wconv.length(sCovSpecificName2) > 0) {
	    	sRslt = sCovSpecificName2;   // 특약명  
		} else { 
	     	sRslt =  sCovName2;  
		} 

   	 } else if (grd_List.getColumnID(colIndex)  == "sCovDatePed") {  		// Grid 칼럼이 '특약기간' 이면    
		 if (util.isNull(Ds_List01.getCellData(rowIndex, "sCovDateFrom")) == false) {  		// Grid 칼럼이 '특약기간' 이면   
			sCovDateFrom = Ds_List01.getCellData(rowIndex, "sCovDateFrom");   // 특약기간From 
			sCovDateTo = Ds_List01.getCellData(rowIndex, "sCovDateTo");   		// 특약기간To 
			sRslt = wconv.substr(sCovDateFrom, 0, 4) +"-"+ wconv.substr(sCovDateFrom, 4, 2) +"-"+  wconv.substr(sCovDateFrom, 6, 2)
					+" ~ "+ wconv.substr(sCovDateTo, 0, 4) +"-"+ wconv.substr(sCovDateTo, 4, 2) +"-"+  wconv.substr(sCovDateTo, 6, 2);		// 특약기간  
		 	//console.log("[/CBSC056P.xml] [lfn_getColumnValue2()] ==> [특약기간] [sRslt]"+ sRslt +"[sCovDateFrom]"+ sCovDateFrom +"[sCovDateTo]"+ sCovDateTo  ); 
 		}  
 	}  else if (grd_List.getColumnID(colIndex)  == "sPolStat") {  		// Grid 칼럼이 '계약상태' 이면  
		var sPolStat = Ds_List01.getCellData(rowIndex, "sPolStat");   // 계약상태 
		sRslt = wconv.decode(sPolStat, "1", "정상", "2", "만기", "3", "해지", "4", "실효", "5", "기타");		// 계약상태   
	}

	return sRslt;
};

