// lfn_getCellDataValue:  칼럼 expression  처리 함수
//**************************************************************************************************
scwin.lfn_getCellDataValue = function(data, formattedData, rowIndex, colIndex) {
	//console.log("[/CBSC057P.xml] [lfn_getCellDataValue()] ==> [TEST_01] [rowIndex]"+ rowIndex +"[colIndex]"+ colIndex  ); 

	var sRslt = ""; 

	//if (Ds_InsurPerson.getCellDataID(colIndex) == "sInrpsCd") {  	// Grid에서 칼럼이 '주민번호' 이면
	if (colIndex == 2) {  	// Grid에서 칼럼이 '주민번호' 이면 
		var sInrpsCd = Ds_InsurPerson.getCellData(rowIndex, "sInrpsCd");   	// 주민번호 
		//console.log("[/CBSC057P.xml] [lfn_getCellDataValue()] ==> [Grid에서 칼럼이 '주민번호' 이면] [sInrpsCd]"+ sInrpsCd );  

		if(sAgncYn == "Y"){   
			if (wconv.length(sInrpsCd) > 10) {  
				sRslt = gfn_changeMask(sInrpsCd, 1);		// 마스크 처리 처리하는 함수
			} 
		} else {

			sRslt = gfn_changeMask(sInrpsCd, 1);		//마스크 처리 처리하는 함수
		}	 
	}

	return sRslt;
};

