//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 
//---------------------------------------------------------------------------
//비동기호출 
scwin.lfn_Search = function() {
	util.eventAsyncCall(this, scwin.lfn_Search_sync, arguments);
};

scwin.lfn_Search_sync = function(e) {
    wconv.clear(DsWs_LapsCnclCrCnt);
    wconv.clear(Ds_LapsCnclCrList);
    wconv.clear(DsWs_LapsCnclCrList);
    wconv.clear(DsWs_CmprCrNotc);
    //trace("--------------------->"+Ds_LTIDA00.SaveXML());
    //trace(DsWs_LTIDA03.SaveXML());
    /*
    SOBJ_Search.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.psv.ens.task.EAD071Task", "selLapsCnclCrList", "", "");
    gfn_SetParam(MethodObj, "sInrpsCd", sInrpsCd);
    gfn_SetParam(MethodObj, "sStandDt", sStandDt);
    gfn_SetParam(MethodObj, "sSsrtScrnYn", sSsrtScrnYn);
    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo);
    gfn_SetParam(MethodObj, "sCrNo", sCrNo);
    gfn_SetParam(MethodObj, "sGdTypeFlagCd", sGdTypeFlagCd);
    gfn_CallService(SOBJ_Search, DOBJ_DataSearch, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD071Task", "selLapsCnclCrList");
	MethodObj.setId("selLapsCnclCrList");
    MethodObj.setParam( "sInrpsCd", util.nvl(fv_args.sInrpsCd,""));
    MethodObj.setParam( "sStandDt", util.nvl(fv_args.sStandDt,""));
    MethodObj.setParam( "sSsrtScrnYn", util.nvl(fv_args.sSsrtScrnYn,""));
    MethodObj.setParam( "sPlanNo", util.nvl(fv_args.sPlanNo,""));
    MethodObj.setParam( "sCrNo", util.nvl(fv_args.sCrNo,""));
    MethodObj.setParam( "sGdTypeFlagCd", util.nvl(fv_args.sGdTypeFlagCd,""));	
	MethodObj.setUrl("/contract/CBSC062PController.do");
	MethodObj.setXPath("//hashtable/data[@hashkey='LapsCnclCrCnt']/LTIEA00Cnt");
	MethodObj.addXPath("//*/LTIEA00");
	MethodObj.addXPath("//hashtable/data[@hashkey='CmprCrNotc']/CmprCrNotc");
	MethodObj.addXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00Cnt"), "DsWs_LapsCnclCrCnt");
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "DsWs_LapsCnclCrList");
	util.jSonDsCreate(_tranSync.getDsJson("LTIEA00"), "Ds_LapsCnclCrList");
	util.jSonDsCreate(_tranSync.getDsJson("CmprCrNotc"), "DsWs_CmprCrNotc");
	util.jSonDsCreate(_tranSync.getDsJson("CmprCrNotc"), "Ds_CmprCrNotc");
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_DataWarning");
	//util.setCompBindRefresh("DsWs_Team");
	//util.setRowPosition(DsWs_Team, 0);

    if (DsWs_CmprCrNotc.getRowCount() > 0) {
        //wconv.copy(Ds_CmprCrNotc, "DsWs_CmprCrNotc");
        if (Ds_CmprCrNotc.getCellData(0, "sCmprCrNotc") == "Y") {
            chk_save.setDisabled(true);
        }
    } else {
        chk_save.setDisabled(false);
        util.insertColumn(Ds_CmprCrNotc, [[ "sCmprCrNotc",                "sCmprCrNotc",                "text"            ]]);
        util.appendRow(Ds_CmprCrNotc);
        Ds_CmprCrNotc.setCellData(0, "sCmprCrNotc", "N");
    }

    //일반보험은 chk_save 활성화처리.
    if (wconv.substr(fv_args.sPlanNo, 0, 1) == "B" || wconv.substr(fv_args.sCrNo, 0, 1) == "D") {
        chk_save.setDisabled(false);
        if (Ds_CmprCrNotc.getRowcount() < 1) {
            util.insertColumn(Ds_CmprCrNotc, [[ "sCmprCrNotc",                    "sCmprCrNotc",                    "text"                ]]);
            util.appendRow(Ds_CmprCrNotc);
            Ds_CmprCrNotc.setCellData(0, "sCmprCrNotc", "N");
        }
    }



    //2015.03.02 (N1502-00159)
    //대면& 간편설계 아닌경우 청약사항 입력 후 승환계약 안내 팝업 띄움
    //단, 저장 버튼은 비활성화
    if (fv_args.sScrnId == "Div2") {
        chk_save.setDisabled(true);
    }
    //END
    if (DsWs_DataWarning.getRowCount() != 0) {
        wprom.alert(DsWs_DataWarning.getCellData(0, "msg"));
        return;
    }
    //wconv.copy(Ds_LapsCnclCrList, "DsWs_LapsCnclCrList");
    Static12.setValue(sCrtorName);
    /*if(sSsrtScrnYn == "Y"){
		Static15.Value = sReqAgntName;
    }else{
        //취급자의 성명 가져오기
		gfn_getEmpName(sDoer);
		Static15.Value = gDsWs_SelectEmpName.GetColumn(0,"sEmpName");
    }*/
    gfn_getEmpName(fv_args.sDoer);
    Static15.setValue(gds.gDsWs_SelectEmpName.getCellData(0, "sEmpName"));
};

//---------------------------------------------------------------------------
// function 명 : lfn_close
// 내용        : 팝업클로즈
//---------------------------------------------------------------------------
scwin.lfn_close = function(e) {
    if (chk_save.getValue() == "Y") {
        scwin.lfn_save();
    }

    /*
    else{
    	alert("비교계약 안내를 하셔야만 다음으로 진행할수 있습니다.");
    	return;
    }
    */
    if (chk_save.getValue() != "Y") {
        sMoveYn = "1";
    } else {
        sMoveYn = "0";
    }
    wprom.close(sMoveYn);
};

