scwin.lfn_save = function(obj) {
    wconv.copy(DsWs_CmprCrNotc, "Ds_CmprCrNotc");
    wconv.copy(Ds_LapsCnclCrList, "DsWs_LapsCnclCrList");
    /*
    SOBJ_save.WSDL = gds.gWsdlBasis;
    var MethodObj = gfn_CreateMethodObj(SOBJ_save, "com.inswave.longterm.psv.ens.task.EAD071Task", "updateSearchYn", "", "");
    gfn_SetParam(MethodObj, "sInrpsCd", sInrpsCd);
    gfn_SetParam(MethodObj, "sStandDt", sStandDt);
    gfn_SetParam(MethodObj, "sSsrtScrnYn", sSsrtScrnYn);
    gfn_SetParam(MethodObj, "sPlanNo", sPlanNo);
    gfn_SetParam(MethodObj, "sCrNo", sCrNo);
    gfn_SetParam(MethodObj, "sCmprCrNotcCrNo", Ds_LapsCnclCrList.getCellData(0, "sCrNoPast"));
    gfn_CallService(SOBJ_save, DOBJ_save, MethodObj);
    */
	var MethodObj = new callService("com.inswave.longterm.psv.ens.task.EAD071Task", "updateSearchYn");
	MethodObj.setId("updateSearchYn");
	//MethodObj.setParam("sAgentCode", gUserID);
    MethodObj.setParam( "sInrpsCd", util.nvl(fv_args.sInrpsCd,""));
    MethodObj.setParam( "sStandDt", util.nvl(fv_args.sStandDt,""));
    MethodObj.setParam( "sSsrtScrnYn", util.nvl(fv_args.sSsrtScrnYn,""));
    MethodObj.setParam( "sPlanNo", util.nvl(fv_args.sPlanNo,""));
    MethodObj.setParam( "sCrNo", util.nvl(fv_args.sCrNo,""));
    MethodObj.setParam( "sCmprCrNotcCrNo", Ds_LapsCnclCrList.getCellData(0, "sCrNoPast"));
	MethodObj.setUrl("/contract/CBSC062PController.do");
	MethodObj.setXPath("//WARNING");
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_saveWarning");

    if (DsWs_saveWarning.getRowCount() != 0) {
        wprom.alert(DsWs_saveWarning.getCellData(0, "msg"));
        return;
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_GrdList_OnCellClick
// 내용        : Cell 클릭시
//---------------------------------------------------------------------------
scwin.lfn_GrdList_OnCellClick = function( nRow, nCell) {
	alert("추후개발예정");
	return;//goo 
    if (nCell == 9) {
        //신규계약
        sMoveYn = "1";
        var arg = "";
        if (sSsrtScrnYn == "Y") {
            arg = " sPlanNo=" + wconv.quote(Ds_LapsCnclCrList.getCellData(nRow, "sPlanNoFutr"));
        } else {
            arg = " sCrNo=" + wconv.quote(Ds_LapsCnclCrList.getCellData(nRow, "sCrNoFutr"));
        }
        arg += " sInrpsCd=" + wconv.quote(Ds_LapsCnclCrList.getCellData(nRow, "sInrpsCdFutr"));
        var result = Open("psv_ens::D072_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
    } else if (nCell == 10) {
        //기존계약
        sMoveYn = "1";
        var arg = " sCrNo=" + wconv.quote(Ds_LapsCnclCrList.getCellData(nRow, "sCrNoPast"));
        arg += " sInrpsCd=" + wconv.quote(Ds_LapsCnclCrList.getCellData(nRow, "sInrpsCdPast"));

        //alert(arg);
        var result = Open("psv_ens::D072_Pop.xml", arg, 800, 550, "TaskBar=true Resize=true");
    }
};

//---------------------------------------------------------------------------
// function 명 : lfn_CrNo_OnCellClick
// 내용        : 계약번호 클릭시 
//---------------------------------------------------------------------------
scwin.lfn_CrNo_OnCellClick = function(obj, nRow, nCell, nX, nY, nPivotIndex) {
//사용안됨.
    var sCrNo = obj.GetCellValue(nRow, nCell);
    gds.gDs_LinkArg.removeAll();
    var insRow = util.insertRow(gds.gDs_LinkArg, 0);
    /*수작업 변환 개발자 가이드(테스트 중)
    dataList에 row를 추가하고 setCellData를 하는 경우 첫 번째 인자를 insRow로 변경해주세요.*/
    util.insertColumn(gds.gDs_LinkArg, [        [            "sCrNo",            "sCrNo",            "text"        ]    ]);
    util.insertColumn(gds.gDs_LinkArg, [        [            "sScrnId",            "sScrnId",            "text"        ]    ]);
    gds.gDs_LinkArg.setCellData(0, "sCrNo", sCrNo);
    gds.gDs_LinkArg.setCellData(0, "sScrnId", "D010");
    gfn_OpenLinkPage("D020");
};

//---------------------------------------------------------------------------
// function 명 : lfn_nameJumin
// 내용        : 이름(주민번호) 
//---------------------------------------------------------------------------
scwin.lfn_nameJumin = function(sInrpsName, sInrpsCd) {
//사용안됨.
    var str = sInrpsName;
    if (wconv.length(sInrpsCd) == 13) {
        str += "(" + wconv.substr(sInrpsCd, 0, 6) + "-" + wconv.substr(sInrpsCd, 6, 7) + ")";
    } else if (wconv.length(sInrpsCd) == 10) {
        str += "(" + wconv.substr(sInrpsCd, 0, 3) + "-" + wconv.substr(sInrpsCd, 3, 2) + "-" + wconv.substr(sInrpsCd, 5, 5) + ")";
    } else {
        str += "(" + sInrpsCd + ")";
    }
    return str;
};
