head.script:
var custId = "";
var custNo = "";
var custName = "";
var handlerID = "";

var sStandDate;

	scwin.init = function() {
		var indata = $w.getParameter("param");
		var inJSON = JSON.parse(indata);

		//indata set
		custId = inJSON.custId;
		custNo = inJSON.custNo;
		custName = inJSON.custName;
		handlerID = inJSON.handlerID;

		id_custNo.setValue(custNo);
		id_custName.setValue(custName);

		//현재시간 가져오기
		gfn_getServerTime();
		sStandDate = wconv.substr(gds.gDs_ServerTime.getCellData(0, "sToday"), 0, 8);

		id_expireDate1.setValue(wconv.addMonth(sStandDate, 3));
		//id_expireDate2.setValue(wconv.addMonth(sStandDate, 3));
	};

	scwin.id_print_onclick = function() {
		var reportInfo  = {}
		reportInfo.rptName        = "longterm/B741_Rep";
		reportInfo.dataType       = "json";
		reportInfo.sInterfaceType = "N";
		reportInfo.gUserID        = util.getGdsValue("gUserID");
		reportInfo.title          = "가입설계동의서";
		wprom.reportOpen(reportInfo);
	};

	scwin.id_tab_ontabclick = function(tabID,idx) {
		if (idx == 1) {
			if (wconv.isNull(id_expireDate2.getValue())) {
				id_expireDate2.setValue(wconv.addMonth(sStandDate, 3));

				id_custNo.setValue(custNo);
				id_custName.setValue(custName);
			}
		}
	};

	scwin.id_phoneCertificationBirth_onclick = function() {
		if (scwin.lfn_validation()) {
			//모바일 LMS 가입설계동의서 문자전송
			if (scwin.lfn_sendSMS() == false) {
				return false;
			}

			//"http://10.1.1.131:9196/mobile/certification/phoneCertificationBirth.mo?sCellPhone=01087223939&sAgency=KTF&sGender=1&sInsrdName=%BC%BA%BA%B4%B9%AB&sBirthDay=19821021&sNationDiv=1&checkbox1=Y&checkbox2=Y&checkbox3=Y&checkbox4=Y";
			id_smsVldNo.setValue("");
//			id_smsVldNo.setDisabled(true);
//			id_phoneCertificationBirthCheck.setDisabled(true);

			HpSelfAuthVo.set("sCellPhone", id_sCellPhone1.getValue()+id_sCellPhone2.getValue()+id_sCellPhone3.getValue());
			HpSelfAuthVo.set("sAgency", id_sAgency.getValue());
			HpSelfAuthVo.set("sGender", scwin.lfn_getSex());
			HpSelfAuthVo.set("sInsrdName", scwin.lfn_enCoderCustName());
			HpSelfAuthVo.set("sBirthDay", scwin.lfn_getBirthDate());
			HpSelfAuthVo.set("sNationDiv", scwin.lfn_getNtl());
			HpSelfAuthVo.set("checkbox1", "Y");
			HpSelfAuthVo.set("checkbox2", "Y");
			HpSelfAuthVo.set("checkbox3", "Y");
			HpSelfAuthVo.set("checkbox4", "Y");

			HpSelfAuthVo.set("smsVldNo", "");
			HpSelfAuthVo.set("retcode", "");
			HpSelfAuthVo.set("retmsg", "");
			HpSelfAuthVo.set("rtnMsg", "");

			var trans = new transSub();						// transaction 보낼 객체 생성
			trans.setId("processHpSelfAuthRqst");			// transaction 보낼 SubmissionID
			trans.setUrl("/common/CBSO0002.do");			// Controller 쪽으로 보낼 주소
			trans.setDs("HpSelfAuthVo");
			trans.targetDs("HpSelfAuthVo");
			trans.callBack("scwin.subCallBack");			// 받을 Dataset을 처리시 콜백함수
			trans.send();
		};
	};

	scwin.subCallBack = function(e) {
		switch(e.subID){
			case "processHpSelfAuthRqst":
				if (e.flag == "s") {
					HpSelfAuthVo.setJSON(e.result);
					alert(HpSelfAuthVo.get("retmsg"));
					if (HpSelfAuthVo.get("retcode") == "0") {
//						id_smsVldNo.setDisabled(false);
//						id_phoneCertificationBirthCheck.setDisabled(false);
					}
				} else {
					wprom.alert(e.msg);
				}
				break;

			case "processHpSelfAuthNoCnfm":
				if (e.flag == "s") {
					HpSelfAuthVo.setJSON(e.result);
					alert(HpSelfAuthVo.get("retmsg"));
					if (HpSelfAuthVo.get("retcode") == "0") {
//						id_smsVldNo.setDisabled(true);
//						id_phoneCertificationBirthCheck.setDisabled(true);
						//서비스에서 처리
						//scwin.lfn_setCUSAA42Info();
						wprom.close();
					}
				} else {
					wprom.alert(e.msg);
				}
				break;
		}
	};

	scwin.id_phoneCertificationBirthCheck_onclick = function() {
		if (scwin.lfn_validation("check")) {
			HpSelfAuthVo.set("sCellPhone", id_sCellPhone1.getValue()+id_sCellPhone2.getValue()+id_sCellPhone3.getValue());
			HpSelfAuthVo.set("sAgency", id_sAgency.getValue());
			HpSelfAuthVo.set("sGender", scwin.lfn_getSex());
			HpSelfAuthVo.set("sInsrdName", scwin.lfn_enCoderCustName());
			HpSelfAuthVo.set("sBirthDay", scwin.lfn_getBirthDate());
			HpSelfAuthVo.set("sNationDiv", scwin.lfn_getNtl());
			HpSelfAuthVo.set("checkbox1", "Y");
			HpSelfAuthVo.set("checkbox2", "Y");
			HpSelfAuthVo.set("checkbox3", "Y");
			HpSelfAuthVo.set("checkbox4", "Y");
			HpSelfAuthVo.set("checkbox4", "Y");
			HpSelfAuthVo.set("smsVldNo", id_smsVldNo.getValue());

			HpSelfAuthVo.set("retcode", "");
			HpSelfAuthVo.set("retmsg", "");
			HpSelfAuthVo.set("rtnMsg", "");

			HpSelfAuthVo.set("sCustNo", custNo);
			HpSelfAuthVo.set("sHandlerID", handlerID);

			var trans = new transSub();						// transaction 보낼 객체 생성
			trans.setId("processHpSelfAuthNoCnfm");					// transaction 보낼 SubmissionID
			trans.setUrl("/common/CBSO0003.do");		// Controller 쪽으로 보낼 주소
			trans.setDs("HpSelfAuthVo");
			trans.targetDs("HpSelfAuthVo");
			trans.callBack("scwin.subCallBack");	// 받을 Dataset을 처리시 콜백함수
			trans.send();
		};
	};

	scwin.lfn_validation = function(gubun) {
		if (wconv.length(wconv.trim(id_sAgency.getValue())) == "") {
			wprom.alert("통신사를 선택하세요.");
			id_sAgency.focus();
			return false;
		}

		if (wconv.length(wconv.trim(id_sCellPhone1.getValue())) == "") {
			wprom.alert("휴대전화번호를 입력하세요.");
			id_sCellPhone1.focus();
			return false;
		}

		if (wconv.length(wconv.trim(id_sCellPhone2.getValue())) == "") {
			wprom.alert("휴대전화번호를 입력하세요.");
			id_sCellPhone2.focus();
			return false;
		}

		if (wconv.length(wconv.trim(id_sCellPhone3.getValue())) == "") {
			wprom.alert("휴대전화번호를 입력하세요.");
			id_sCellPhone3.focus();
			return false;
		}

		//인증번호 확인 인 경우
		if (gubun == "check") {
			if (wconv.length(wconv.trim(id_smsVldNo.getValue())) == "") {
				wprom.alert("인증번호를 입력하세요.");
				id_smsVldNo.focus();
				return false;
			}
		}

		return true;
	};

	scwin.lfn_enCoderCustName = function() {
		var cvCustName = custName;
		cvCustName = $w.URLEncoder(cvCustName);

		return cvCustName;
	}

	scwin.lfn_getSex = function() {
	    if ("1357".indexOf(wconv.substr(custNo, 6, 1)) >= 0) {
	        return "1";
	    } else if ("2468".indexOf(wconv.substr(custNo, 6, 1)) >= 0) {
	        return "2";
	    } else {
	        return "";
	    }
	};

	scwin.lfn_getBirthDate = function() {
		var birth_yy = wconv.substr(custNo,0,2);
		var birth_mm = wconv.substr(custNo,2,2);
    	var birth_dd = wconv.substr(custNo,4,2);

	    var birth_gbn =  wconv.substr(custNo,6,1);

    	if (birth_gbn == "3" || birth_gbn == "4" || birth_gbn == "7" || birth_gbn == "8"){
	         birth_yy = "20" + birth_yy;
	    } else if (birth_gbn == "9" || birth_gbn == "0") {
	    	birth_yy = "18" + birth_yy;
	    } else {
	    	 birth_yy = "19" + birth_yy;
    	}

    	return birth_yy+birth_mm+birth_dd;
	};

	scwin.lfn_getNtl = function() {
		var sNtlCheck = "5,6";
		var sNationDiv = "1";
		if (sNtlCheck.indexOf(wconv.substr(custNo, 6, 1)) > -1) {
			sNationDiv = "2";
		}

		return sNationDiv;
	};

	scwin.lfn_setCUSAA42Info = function() {
		gfn_getServerTime();
		var sDate = gds.gDs_ServerTime.getCellData(0, "sDate"); // 서버시간
		var sTime = gds.gDs_ServerTime.getCellData(0, "sToday").substr(8,6);

		var MethodObj = new callService("com.inswave.customer.task.CustTask", "setCUSAA42Info", 0, 0);
		MethodObj.setId("setCUSAA42Info");
		MethodObj.setUrl("/contract/CBSC015MController.do");

		MethodObj.setParam("sCustNo", custNo);
		MethodObj.setParam("sProductType", "3"); //장기
		//MethodObj.setParam("custKey_temp", custKey_temp);
		MethodObj.setParam("sAgmYn", "Y");
		MethodObj.setParam("sAgmYn1", "Y");
		MethodObj.setParam("sAgmYn2", "Y");
		MethodObj.setParam("sAgmYn3", "Y");
		MethodObj.setParam("sAgmYn4", "Y");
		MethodObj.setParam("sAgmYn5", "");
		MethodObj.setParam("sInputDate", sDate); //서버시간
		MethodObj.setParam("sInputTime", sTime); //서버시간
		MethodObj.setParam("sInputID", util.getGdsValue("gUserID"));
		MethodObj.setParam("sInputIP", util.getGdsValue("gUserIP"));
		MethodObj.setParam("sAgmMethod", "5"); //휴대폰 본인인증
		MethodObj.setParam("sAgmCheckInfo", HpSelfAuthVo.get("svcTxSeqno")); //sHsCertSvcTxSeqno
		MethodObj.setParam("sAgmRecvCode", handlerID);
		MethodObj.setParam("sAgmRecvName", "");
		MethodObj.setParam("sHandlerID", handlerID);

 		MethodObj.setXPath("//*/WARNING");
 		MethodObj.addXPath("//CUSAA42 ");
		MethodObj.sync();
		MethodObj.send(); 

		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
		if (DsWs_Warning.getRowCount() != 0) {
			wprom.alert(DsWs_Warning.getCellData(0, "msg"));
			return;
		} else {
			wprom.alert("가입설계동의가 완료되었습니다.");
			wprom.close();
    	}
	};

	scwin.lfn_sendSMS = function() {
		var MethodObj = new callService("com.inswave.ccc.task.SMSTask", "sendAgreeLMS", 0, 0);
		MethodObj.setId("sendAgreeLMS");
		MethodObj.setUrl("/contract/CBSC015MController.do");

		MethodObj.setParam("sCustNo", custNo);
		MethodObj.setParam("sName", custName);
		MethodObj.setParam("sCellPhone", id_sCellPhone1.getValue()+id_sCellPhone2.getValue()+id_sCellPhone3.getValue());
		MethodObj.setParam("sUserID", util.getGdsValue("gUserID"));

 		MethodObj.setXPath("//*/MMS_SEND");
 		MethodObj.addXPath("//*/WARNING");
		MethodObj.sync();
		MethodObj.send(); 

		util.jSonDsCreate(_tranSync.getDsJson("WARNING"), DsWs_Warning.getID());
		util.jSonDsCreate(_tranSync.getDsJson("MMS_SEND"), DsWs_MMS_SEND.getID());
		if (_tranSync.CallServiceResult && DsWs_Warning.getRowCount() == 0) {
		 	if (_tranSync.result.serviceDs.data[0].result == 1) {
				return true;
			} else {
				return false;
			}
		} else {
			wprom.alert("가입설계동의 LMS전송중 오류가 발생했습니다.");
			return false;
    	}

		return false;
	};
