scwin.init = function() {
var pro = Promise.resolve();

window.top.util.winTopShowModalChk = "T";
util.winTopShowModal();



pro = pro.then(function(rv) {
    return util.winTopShowModalPromise();
});



pro = pro.then(function(rv) {

     //init 부분 함수 입력하는곳

			wprom.windowOpenInit(F400);
	var indata = $w.getParameter("param");//
	try {
		arg = JSON.parse(indata);

		sApplyNo = arg.sApplyNo
		sGdCd = arg.sGdCd
		sCrYn =    arg.sCrYn;
		sCustNo =    arg.sCustNo;
		nSeqNo =    arg.nSeqNo;
		sCustName =    arg.sCustName;
		sScanObjtYn =    arg.sScanObjtYn;
		sPsnGroupFlgcd =    arg.sPsnGroupFlgcd;
		sCrtorCd	=    arg.sCrtorCd;
		sApplyStat = arg.sApplyStat;
	} catch(e) {
	};

    if (sApplyStat == 5 || sApplyStat == 7) // 확정
    {
        Div1_Checkbox2.setDisabled(true);
        Div1_Checkbox5.setDisabled(true);
        Div1_Checkbox6.setDisabled(true);
        Div2_Checkbox8.Enable = false;
        Btn_save.setDisabled(true);
    }
    if (sScanObjtYn == "Y") {
        Div1.hide();
        Div2.Top = 59;
        Shape2.Height = 192;
        window.Height = 260;
        window.Width = 800;
    }
    var sCrtorCd = wconv.trim(sCrtorCd);

    if (wconv.length(sCrtorCd) == 10) {
        var sCrtorCdFlg = wconv.subStr(sCrtorCd, 3, 2);
        wconv.trace("sCrtorCd : " + args.sCrtorCd + ", sCrtorCdFlg : " + args.sCrtorCdFlg);
        if (sCrtorCdFlg == "81" || sCrtorCdFlg == "86" || sCrtorCdFlg == "87" || sCrtorCdFlg == "88" || //영리법인 본점
            sCrtorCdFlg == "82" || // 비영리법인 본점 및 지점
            sCrtorCdFlg == "83" || // 국가, 지방지자체단체, 지방자치단체조합
            sCrtorCdFlg == "84" || //외국법인 본점 및 지점
            sCrtorCdFlg == "85") {
            Div1_Static18.show();
            Div1_Static0.hide();
        } else {
            Div1_Static18.hide();
            Div1_Static0.show();
        }
    } else {
        Div1_Static18.hide();
        Div1_Static0.show();
    }
    scwin.lfn_selectAgreYn();


    //init 함수를 종료하고자 할때 사용한다.
//    return Promise.reject("오류관련처리");
});


pro = pro.then(function(rv) {
    util.winTopHideModal();
});



pro = pro.catch(function(rj) {
    util.winTopHideModal();
    //alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
});



return pro;
};

