scwin.lfn_selectAgreYn = function(e) {
    wconv.clear(DsWs_LTIDA50); //리스트 내용삭제
    Ds_LTIDA50.removeAll();

//    SOBJ_select.WSDL = gds.gWsdlBasis; // 서비스 Obj WSDC 설정
//    var MethodObj = gfn_CreateMethodObj(SOBJ_select, "com.inswave.longterm.dpt.rec.task.FAF400Task", "selectAgreYn", "", "");
//    gfn_SetParam(MethodObj, "sPlanNo", sApplyNo);
//    var result = gfn_CallService(SOBJ_select, DOBJ_select, MethodObj);

	var MethodObj = new callService("com.inswave.longterm.dpt.rec.task.FAF400Task", "selectAgreYn",pageSize,pageNo);
	//AdminTask를 다 명시해줌	
	MethodObj.setId("selectAgreYn");
	MethodObj.setParam( "sPlanNo", sApplyNo);
	MethodObj.setUrl("/contract/CBSC066PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/LTIDA50");
	MethodObj.addXPath("//WARNING"); 
	MethodObj.sync();
	MethodObj.send();
	util.jSonDsCreate( _tranSync.getDsJson("WARNING"), "DsWs_selectWarning");

    if (DsWs_selectWarning.getRowCount() != 0) {
        wprom.alert(DsWs_selectWarning.getCellData(0, "msg"));
        return;
    }

    //util.jSonDsCreate(_tranSync.getDsJson("LTIDA50"), "Ds_LTIDA50");


    util.jSonDsCreate(_tranSync.getDsJson("LTIDA50"), "DsWs_LTIDA50");   
	util.setCompBindRefresh("DsWs_LTIDA50");
	util.setRowPosition(DsWs_LTIDA50, 0);	


    if (DsWs_LTIDA50.getRowPosition() > 0 && DsWs_LTIDA50.getCellData(0, "result") > 0) {
//        wconv.copy(Ds_LTIDA50, "DsWs_LTIDA50");
		util.copyM(Ds_LTIDA50, DsWs_LTIDA50.getAllJSON());
        util.setCompBindRefresh("Ds_LTIDA50");
		util.setRowPosition(Ds_LTIDA50, 0);	
        //trace(DsWs_LTIDA50.SaveXML());
        /*
        if (Ds_LTIDA50.GetColumn(0,"sCnclAgreYn") == "Y"){
        	Div2.chkCnclAgreYn.Enable = false;
        }

        if (Ds_LTIDA50.GetColumn(0,"sCrtorCnclAgreYn") == "Y"){
        	Div2.chkCrtorCnclAgreYn.Enable = false;
        }
        */
        if (sPsnGroupFlgcd != "21") {
            if (Ds_LTIDA50.getCellData(0, "s32AgreYn") == "Y") {}
            if (Ds_LTIDA50.getCellData(0, "sCrtor32AgreYn") == "Y") {}
        }
    } else {

        util.appendRow(Ds_LTIDA50);
        Ds_LTIDA50.setCellData(0, "sPlanNo", sApplyNo);
        Ds_LTIDA50.setCellData(0, "sItemCd", sGdCd);
        Ds_LTIDA50.setCellData(0, "sExplAgreYn1", "Y");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn3", "Y");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn4", "Y");        
        Ds_LTIDA50.setCellData(0, "sExplAgreYn7", "Y");
        Ds_LTIDA50.setCellData(0, "s33AgreYn", "Y");
        Ds_LTIDA50.setCellData(0, "sInptDler", util.getGdsValue("gUserID"));
        Ds_LTIDA50.setCellData(0, "sInptDlerIP", util.getGdsValue("gUserIP"));

    }
};

