scwin.Div1_Checkbox0_onviewchange = function(info) {
		    if (Div1_Checkbox0.getValue() == "Y") {
        Ds_LTIDA50.setCellData(0, "sExplAgreYn2", "Y");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn5", "Y");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn6", "Y");
        Ds_LTIDA50.setCellData(0, "sCnclAgreYn", "Y");
        Ds_LTIDA50.setCellData(0, "sCrtorCnclAgreYn", "Y");

    } else {
        Ds_LTIDA50.setCellData(0, "sExplAgreYn2", "");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn5", "");
        Ds_LTIDA50.setCellData(0, "sExplAgreYn6", "");
        Ds_LTIDA50.setCellData(0, "sCnclAgreYn", "");
        Ds_LTIDA50.setCellData(0, "sCrtorCnclAgreYn", "");

    }
};
