head.script:
// 금융기관 가입내역 조회시 비과세만 단독 조회시 사용(58) 
//---------------------------------------------------------------------------
// function 명 : G440_Pop_OnLoadCompleted
// 내용        : 폼로드할때 처리할 내용 
//---------------------------------------------------------------------------
var fv_args;
var pageSize = "";
var pageNo = "";
var nSvKind; // 금융기관 가입내역 조회시 비과세만 단독 조회시 사용(58)
var nCrtorCd;
scwin.init = function() {
	var pro = Promise.resolve();
		window.top.util.winTopShowModalChk = "T";
		util.winTopShowModal();
	pro = pro.then(function(rv) {
    	return util.winTopShowModalPromise();
	}); 
	pro = pro.then(function(rv) {
     //init 부분 함수 입력하는곳
	wprom.setPopupInit(1065,619, "세금우대 전금융기관가입 내역조회");
	var dataSets = "DsWs_InfoMainFlgcd DsWs_TaxPrfrDoubYn DsWs_SvGdCd DsWs_IhrtnYn";
 	var columns =  "InfoMainFlgcd TaxPrfrDoubYn SvGdCd IhrtnYn"; 
    var objects = "cmb_nInfoMainFlgcd cmb_nDoubInsFlag cmb_nSvKind cmb_nIhrtnYn";
    var titleTypes = "N N N N";   // A:전체, S:선택, N:안넣기    	

    if (gds.gDs_LinkArg.getRowCount() != 0) {
        msk_sCrtorCd1.setValue(gds.gDs_LinkArg.getCellData(0, "nCrtorCd")); //수정
        if (gds.gDs_LinkArg.getCellData(0, "nSvKind") != null) {
            nSvKind = gds.gDs_LinkArg.getCellData(0, "nSvKind");
        }
        gds.gDs_LinkArg.removeAll();
		gfn_getCommonCodes(dataSets, columns, objects, titleTypes); //공통조회
        scwin.lfn_Search_Async();
    } else {        
 		gfn_getCommonCodes(dataSets, columns, objects, titleTypes); //공통조회
        scwin.lfn_Search_Async();
    }   
      return ;		// init 함수를 종료하고자 할때 사용
		// return Promise.reject("오류관련처리"); 
	});
	pro = pro.then(function(rv) {
		util.winTopHideModal();
	});	
	pro = pro.catch(function(rj) {
		util.winTopHideModal();
		//alert(rj); //Promise reject 시 처리 "오류관련처리" 값출력
	});	 
	return pro; 
};
//---------------------------------------------------------------------------
// function 명 : lfn_Search
// 내용        : 조회버튼 클릭 Event처리
//---------------------------------------------------------------------------
scwin.lfn_Search_Async = function(e) {
	util.eventAsyncCall(this, scwin.lfn_Search, arguments);
};

scwin.lfn_Search = function() {  
    wconv.clear(DsWs_SendTax);
    wconv.clear(DsWs_SendTaxList);

//    SOBJ_Search.WSDL = gds.gWsdlBasis;
//    var MethodObj = gfn_CreateMethodObj(SOBJ_Search, "com.inswave.longterm.pay.ptm.task.GEG440Task", "sendSelectFinaOrgn", "", "");
//    gfn_SetParam(MethodObj, "sTermNo", gds.gUserCenterCode); //입력지점	
//    gfn_SetParam(MethodObj, "sTrmnUserID", gUserId); //단말기조작자ID
//    gfn_SetParam(MethodObj, "sTrmnUserNm", gds.gUserEmpName); //단말기조작자성명
//    gfn_SetParam(MethodObj, "nCrtorCd", msk_sCrtorCd1.getValue()); //계약자코드
//    gfn_SetParam(MethodObj, "nSvKind", nSvKind); //조회 저축 종류
	var MethodObj = new callService("com.inswave.longterm.pay.ptm.task.GEG440Task", "sendSelectFinaOrgn", pageSize, pageNo);	
	MethodObj.setId("sendSelectFinaOrgn");	
	MethodObj.setParam("sTermNo", util.getGdsValue("gUserCenterCode"));//gds.gUserCenterCode); //입력지점
	MethodObj.setParam("sTrmnUserID", util.getGdsValue("gUserID")); //단말기조작자ID
	MethodObj.setParam("sTrmnUserNm", util.getGdsValue("gUserEmpName"));//gds.gUserEmpName); //단말기조작자성명
	MethodObj.setParam("nCrtorCd", msk_sCrtorCd1.getValue()); //계약자코드
	MethodObj.setParam("nSvKind", nSvKind); //조회 저축 종류

	if (wconv.length(msk_sCrtorCd1.getValue()) == 10) {
        //gfn_SetParam(MethodObj, "nCrtorCdFlag", 2); //사업자번호 2
        MethodObj.setParam("nCrtorCdFlag", 2); //사업자번호 2    
    } else if (wconv.length(msk_sCrtorCd1.getValue()) == 13) {
        //gfn_SetParam(MethodObj, "nCrtorCdFlag", 1); //주빈번호 1
        MethodObj.setParam("nCrtorCdFlag", 1); //주빈번호 1
    }	
	MethodObj.setUrl("/contract/CBSC067PController.do");//배포후변경할것(controller위치와 이름)
	MethodObj.setXPath("//*/WARNING");

    if (nSvKind != "") {
//        DsWs_SendTaxList.XPath = "//*/vector/data/TAX0210";
//        DsWs_SendTax.XPath = "//*/TAX0021";
		MethodObj.addXPath("//*/vector/data/TAX0210");//(DsWs_SendTaxList의 XPath)				
		MethodObj.addXPath("//*/TAX0021");//(DsWs_SendTax의 XPath)	
    }else{    
    	MethodObj.addXPath("//*/vector/data/TAX0200");//(DsWs_SendTaxList의 XPath)				
		MethodObj.addXPath("//*/TAX0020");//(DsWs_SendTax의 XPath)	
	}
	MethodObj.sync();
	MethodObj.send();

	if (nSvKind != "") {
	util.jSonDsCreate(_tranSync.getDsJson("TAX0210"), "DsWs_SendTaxList");
	util.jSonDsCreate(_tranSync.getDsJson("TAX0021"), "DsWs_SendTax");
	util.jSonDsCreate(_tranSync.getDsJson("TAX0021"), "Ds_SendTax");
	}else{
	util.jSonDsCreate(_tranSync.getDsJson("TAX0200"), "DsWs_SendTaxList");
	util.jSonDsCreate(_tranSync.getDsJson("TAX0020"), "DsWs_SendTax");
	util.jSonDsCreate(_tranSync.getDsJson("TAX0020"), "Ds_SendTax");
	}	

	util.setCompBindRefresh("DsWs_SendTaxList");//바인딩을 해줌
	util.setCompBindRefresh("DsWs_SendTax");//바인딩을 해줌
	util.setCompBindRefresh("Ds_SendTax");//바인딩을 해줌
	util.setRowPosition(DsWs_SendTaxList, 0);	
	util.setRowPosition(DsWs_SendTax, 0);	

	util.jSonDsCreate(_tranSync.getDsJson("WARNING"), "DsWs_Warning");
 	//에러처리
    if (DsWs_Warning.getRowCount() != 0) {
       wprom.alert(DsWs_Warning.getCellData(0, "msg"));
    return;
    } else {
        if (DsWs_SendTax.getRowPosition() != 0) {
        } else {
       // debugger;
            DsWs_SendTax.setCellData(0, "sCrtorName", wconv.trim((DsWs_SendTax.getCellData(0, "sCrtorName")).replace("	", "")));

            wconv.copy(Ds_SendTax, DsWs_SendTax);
            // 계약자 명 sCrtorName
            Ds_SendTax.setCellData(0, "sCrtorName", gfn_changeMask(Ds_SendTax.getCellData(0, "sCrtorName"), "4"));
            // 계약자 주민번호 sCrtorCd
            Ds_SendTax.setCellData(0, "nCrtorCd", gfn_changeMask(Ds_SendTax.getCellData(0, "nCrtorCd"), "1"));
            //alert(Ds_SendTax.getCellData(0, "nCrtorCd"));
        }
    }
    if (nSvKind == "58") {
        Static5.setValue(DsWs_SvGdCd.getCellData(wconv.findRow(DsWs_SvGdCd, "sCd", nSvKind), "sHnglCdName") + " 가입내역 조회"); //Static5.Value
    }
};
//---------------------------------------------------------------------------
// function 명 : btn_Close_OnClick
// 내용        : 닫기 선택시 Event처리
//---------------------------------------------------------------------------
scwin.btn_Close_OnClick = function(e) {	
	wprom.close();
};
//---------------------------------------------------------------------------
// function 명 : getsToDate
// 내용        :  호출된 시점을 기준으로 일자와 시간 정보를 얻는다.
//---------------------------------------------------------------------------
scwin.getsToDate = function() {
    var sDate; //현재일
    global_http.sync = "true";
    getServerTime();
    sysDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간
    sDate = wconv.mid(sysDate, 0, 8); //YYYMMddH\
    global_http.sync = "false";
    return sDate;
};
//---------------------------------------------------------------------------
// function 명 : getsToDateTime()
// 내용        :  호출된 시점을 기준으로 일자와 시간 정보를 얻는다.
//---------------------------------------------------------------------------
scwin.getsToDateTime = function() {
    var sDate; //현재일
    global_http.sync = "true";
    getServerTime();
    sDate = gds.gDs_ServerTime.getCellData(0, "sToday"); // 서버시간(YYYYMMddHHmmss)
    global_http.sync = "false";
    return sDate;
};
