scwin.init = function() {
	console.log("[/CBSC068M.xml] [init()] ==> [폼 로딩] [TEST_01]" );

   	// 공통 버튼 권한 설정 - |1.입력|2.수정|3.삭제|4.결재|5.취소|6.인쇄|7.엑셀   총 7개
     gfn_btnEnable(false, false, false, false, false, false, false);

     if (sScanObjtYnGlb != "Y") {	  //대면일 경우  
        scwin.getComCodeS(); 	//공통코드조회

        scwin.bascMattSet();		 //기본사항입력	
    }

	msk_gsCrtorCd.focus();
	btn_FinaOrgnInsSpec.setDisabled(false); 

	// msk_gsCrtorCd(InputBox) MaskEdit 정규식 입력  
	var maskRegExp = [/^[0-9\s]{7}[A-Z0-9\s]{6}$/];		// 01. 피보험자코드 이면("######-#ZZZZZZ") : 13자리  
	var maxLength  = [13];
	var chkIndex   = 0;
	msk_gsCrtorCd_Event = new wMaskEditObj(msk_gsCrtorCd, maskRegExp, maxLength, chkIndex);

	msk_gsCrtorCd_Event.setIndex(0);  	// msk_gsCrtorCd(InputBox) MaskEdit 정규식 입력 0번째(01. 피보험자코드)
	msk_gsCrtorCd.setPlaceholder("______-_______");		// Placeholder 설정   
	msk_gsCrtorCd.setDisplayFormat("######-#######"); 

	// TEST @@@ ===>
	//msk_gsCrtorCd.setValue("8009021111111"); 					// 계약자코드	// TEST @@@ ===>  

	//console.log("[/CBSC068M.xml] [init()] ==> [폼 로딩] [TEST_end]"  );
};

