//---------------------------------------------------------------------------
// function 명 : chkEssnInpt
// 내용        : 필수입력 check
//---------------------------------------------------------------------------
scwin.chkEssnInpt = function() {
	//console.log("[/CBSC068M.xml] [chkEssnInpt()] ==> [필수입력 check] [TEST_01] " );

    if (msk_gsCrtorCd.getValue() == "") //계약자코드
    { 
        wprom.alert("계약자코드를 입력하세요.");  
        msk_gsCrtorCd.focus();
        return false;

    } else { 
        var sCrtorCden = wconv.length(msk_gsCrtorCd.getValue());

        if (!((sCrtorCden == 10 || sCrtorCden == 13))) {
            wprom.alert("계약자번호를 정확히 입력하세요.");
            msk_gsCrtorCd.focus();
            return false;
        }
    }

    return true;
};

